                    charCode = charCode === 97 ? 122 : charCode - 1;
                    input.value = String.fromCharCode(charCode);
                    input.dispatchEvent(new Event('input'));
                } else if (e.key === 'ArrowDown') {
                    e.preventDefault();
                    const input = document.getElementById('input-letter');
                    let charCode = input.value.charCodeAt(0) || 97;
                    charCode = charCode === 122 ? 97 : charCode + 1;
                    input.value = String.fromCharCode(charCode);
                    input.dispatchEvent(new Event('input'));
                }
            });
        }

        function updateVisualization() {
            // Get current control values
            const convolverValue = parseInt(document.getElementById('slider-convolver').value);
            const imageSize = parseInt(document.getElementById('slider-image-size').value);
            const letter = document.getElementById('input-letter').value || 'a';
            const filterType = document.querySelector('input[name="filter"]:checked').value;

            // Generate before image
            generateBeforeImage(beforeGraphics, letter, imageSize);

            // Generate convolution kernel
            const kernel = generateKernel(convolverValue, filterType);

            // Apply convolution
            const convolvedImage = applyConvolution(beforeGraphics, kernel, imageSize);

            // Generate after image
            generateAfterImage(afterGraphics, convolvedImage, imageSize);

            // Draw both images
            draw();
        }

        function generateBeforeImage(graphics, letter, size) {
            graphics.background(255);
            graphics.fill(0);
            graphics.noStroke();
            graphics.textSize(size * 0.8);
            graphics.textAlign(CENTER, CENTER);
            graphics.text(letter.charAt(0).toLowerCase(), graphics.width / 2, graphics.height / 2);
        }

        function generateKernel(k, filterType) {
            const D = 2 * k + 1;
            const kernel = Array(D).fill().map(() => Array(D).fill(0));

            if (filterType === 'blur') {
                const value = 1.0 / (D * D);
                for (let i = 0; i < D; i++) {
                    for (let j = 0; j < D; j++) {
                        kernel[i][j] = value;
                    }
                }
            } else { // shadow
                for (let i = 0; i < k; i++) {
                    for (let j = 0; j < k; j++) {
                        kernel[i + 1][j + 1] = 1.0;
                    }
                }

                // Normalize
                const sum = k * k;
                for (let i = 0; i < D; i++) {
                    for (let j = 0; j < D; j++) {
                        kernel[i][j] /= sum;
                    }
                }
            }

            return kernel;
        }

        function applyConvolution(graphics, kernel, size) {
            // Get pixel data from before image
            graphics.loadPixels();
            const beforePixels = [];

            // Sample the graphics at grid points
            const cellSize = canvasSize / size;
            const result = Array(size).fill().map(() => Array(size).fill(0));

            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    const x = Math.floor(j * cellSize + cellSize / 2);
                    const y = Math.floor(i * cellSize + cellSize / 2);

                    // Get pixel brightness (0-255)
                    const pixel = graphics.get(x, y);
                    const brightness = (pixel[0] + pixel[1] + pixel[2]) / 3;

                    // Convert to binary (0 or 1)
                    beforePixels[i] = beforePixels[i] || [];
                    beforePixels[i][j] = brightness < 128 ? 1 : 0;
                }
            }

            // Apply convolution
            const k = Math.floor(kernel.length / 2);
            const output = Array(size).fill().map(() => Array(size).fill(0));

            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    let sum = 0;

                    for (let ki = -k; ki <= k; ki++) {
                        for (let kj = -k; kj <= k; kj++) {
                            const ni = i + ki;
                            const nj = j + kj;

                            // Handle edges by clamping
                            if (ni >= 0 && ni < size && nj >= 0 && nj < size) {
                                sum += beforePixels[ni][nj] * kernel[ki + k][kj + k];
                            }
                        }
                    }

                    output[i][j] = sum;
                }
            }

            return output;
        }

        function generateAfterImage(graphics, convolvedData, size) {
            graphics.background(255);
            graphics.noStroke();

            const cellSize = canvasSize / size;

            for (let i = 0; i < size; i++) {
                for (let j = 0; j < size; j++) {
                    const value = convolvedData[i][j];
                    const grayValue = 255 * (1 - value);
                    graphics.fill(grayValue);
                    graphics.rect(j * cellSize, i * cellSize, cellSize, cellSize);
                }
            }
        }

        function draw() {
            background(255);
            image(beforeGraphics, 0, 0);
            image(afterGraphics, beforeGraphics.width + 20, 0);
        }
    </script>
</body>
</html>
