    <script>
        // Global variables
        let proteinData = [];
        let currentView = 'crystallography';
        let threeScene, threeCamera, threeRenderer, threeControls;
        let p5Instance;
        let distanceMatrix = [];

        // Amino acid codes
        const aminoAcids = ['A', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'Y'];

        // Initialize the application
        document.addEventListener('DOMContentLoaded', function() {
            // Generate protein data
            proteinData = generateProteinData(400);

            // Initialize UI controls
            initControls();

            // Initialize 3D view
            initThreeJS();

            // Initialize 2D view (will be created when needed)

            // Set up event listeners
            setupEventListeners();

            // Update info display
            updateInfoDisplay();
        });

        // Generate procedural protein data
        function generateProteinData(numResidues) {
            const data = [];
            let x = 0, y = 0, z = 0;

            for (let i = 0; i < numResidues; i++) {
                // Random walk for CA position
                x += (Math.random() - 0.5) * 2;
                y += (Math.random() - 0.5) * 2;
                z += (Math.random() - 0.5) * 2;

                // Random amino acid type
                const resName = aminoAcids[Math.floor(Math.random() * aminoAcids.length)];

                // Create residue object
                const residue = {
                    resName: resName,
                    resSeq: i + 1,
                    atoms: []
                };

                // Add atoms (CA, N, C, O, CB)
                residue.atoms.push({ atomName: 'CA', x: x, y: y, z: z });
                residue.atoms.push({ atomName: 'N', x: x - 1.5, y: y, z: z });
                residue.atoms.push({ atomName: 'C', x: x + 1.5, y: y, z: z });
                residue.atoms.push({ atomName: 'O', x: x + 1.5, y: y + 1.5, z: z });
                residue.atoms.push({ atomName: 'CB', x: x, y: y - 1.5, z: z });

                data.push(residue);
            }

            // Pre-calculate distance matrix for contact map
            calculateDistanceMatrix(data);

            return data;
        }

        // Calculate distance matrix for contact map
        function calculateDistanceMatrix(data) {
            distanceMatrix = [];
            for (let i = 0; i < data.length; i++) {
                distanceMatrix[i] = [];
                const ca1 = data[i].atoms.find(a => a.atomName === 'CA');

                for (let j = 0; j < data.length; j++) {
                    const ca2 = data[j].atoms.find(a => a.atomName === 'CA');
                    const dx = ca2.x - ca1.x;
                    const dy = ca2.y - ca1.y;
                    const dz = ca2.z - ca1.z;
                    distanceMatrix[i][j] = Math.sqrt(dx*dx + dy*dy + dz*dz);
                }
            }
        }

        // Initialize UI controls
        function initControls() {
            // Populate residue selector
            const selectResidue = document.getElementById('select-residue');
            aminoAcids.forEach(aa => {
                const option = document.createElement('option');
                option.value = aa;
                option.textContent = aa;
                selectResidue.appendChild(option);
            });

            // Update slider value displays
            updateSliderValue('slider-gray-level', 'gray-level-value');
            updateSliderValue('slider-browse-sequence', 'browse-sequence-value');
            updateSliderValue('slider-residue-x', 'residue-x-value');
            updateSliderValue('slider-residue-y', 'residue-y-value');
            updateSliderValue('slider-rmsd', 'rmsd-value');
            updateSliderValue('slider-plot-size', 'plot-size-value');
        }

        // Update slider value display
        function updateSliderValue(sliderId, displayId) {
            const slider = document.getElementById(sliderId);
            const display = document.getElementById(displayId);
            display.textContent = slider.value;
        }

        // Initialize Three.js scene
        function initThreeJS() {
            const container = document.getElementById('canvas-container');

            // Create scene
            threeScene = new THREE.Scene();
            threeScene.background = new THREE.Color(0xffffff);

            // Create camera
            threeCamera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
            threeCamera.position.z = 50;

            // Create renderer
            threeRenderer = new THREE.WebGLRenderer({ antialias: true });
            threeRenderer.setSize(container.clientWidth, container.clientHeight);
            container.innerHTML = '';
            container.appendChild(threeRenderer.domElement);

            // Add controls
            threeControls = new THREE.OrbitControls(threeCamera, threeRenderer.domElement);

            // Add lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1);
            threeScene.add(directionalLight);

            // Render protein
            renderProtein3D();

            // Start animation loop
            animate();
        }

        // Initialize p5.js for contact map
        function initP5JS() {
            const container = document.getElementById('canvas-container');
            container.innerHTML = '';

            p5Instance = new p5(function(p) {
                p.setup = function() {
                    const canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                    canvas.parent('canvas-container');
                    renderContactMap();
                };

                p.draw = function() {
                    // Nothing here - we'll render on demand
                };

                p.windowResized = function() {
                    p.resizeCanvas(container.clientWidth, container.clientHeight);
                    renderContactMap();
                };
            });
        }

        // Animation loop for Three.js
        function animate() {
            requestAnimationFrame(animate);
            threeControls.update();
            threeRenderer.render(threeScene, threeCamera);
        }

        // Render protein in 3D
        function renderProtein3D() {
            // Clear existing objects
            while (threeScene.children.length > 0) { 
                threeScene.remove(threeScene.children[0]); 
            }

            // Add lights back
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1);
            threeScene.add(directionalLight);

            // Get control values
            const showLateralChains = document.getElementById('checkbox-lateral-chains').checked;
            const grayLevel = parseFloat(document.getElementById('slider-gray-level').value);
            const browseSeq = parseInt(document.getElementById('slider-browse-sequence').value) - 1;
            const residueX = parseInt(document.getElementById('slider-residue-x').value) - 1;
            const residueY = parseInt(document.getElementById('slider-residue-y').value) - 1;

            // Main chain color (blue based on gray level)
            const mainColor = new THREE.Color(0, 0, grayLevel);

            // Create bounding box helper
            const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(1, 1, 1)), 0x888888);
            threeScene.add(boxHelper);

            // Track min/max for bounding box
            let minX = Infinity, minY = Infinity, minZ = Infinity;
            let maxX = -Infinity, maxY = -Infinity, maxZ = -Infinity;

            // Create spheres for atoms
            proteinData.forEach((residue, resIndex) => {
                residue.atoms.forEach(atom => {
                    // Skip lateral chains if checkbox is unchecked
                    if (!showLateralChains && atom.atomName !== 'CA') return;

                    // Update bounding box
                    minX = Math.min(minX, atom.x);
                    minY = Math.min(minY, atom.y);
                    minZ = Math.min(minZ, atom.z);
                    maxX = Math.max(maxX, atom.x);
                    maxY = Math.max(maxY, atom.y);
                    maxZ = Math.max(maxZ, atom.z);

                    // Create sphere
                    const geometry = new THREE.SphereGeometry(0.5, 16, 16);
                    let material;

                    // Determine color based on selection
                    if (resIndex === browseSeq) {
                        // Highlighted residue (yellow)
                        material = new THREE.MeshLambertMaterial({ color: 0xffff00 });
                    } else if (atom.atomName === 'CA' && (resIndex === residueX || resIndex === residueY)) {
                        // Selected residues (red)
                        material = new THREE.MeshLambertMaterial({ color: 0xff0000 });
                    } else {
                        // Regular atom (blue based on gray level)
                        material = new THREE.MeshLambertMaterial({ color: mainColor });
                    }

                    const sphere = new THREE.Mesh(geometry, material);
                    sphere.position.set(atom.x, atom.y, atom.z);
                    threeScene.add(sphere);
                });
            });

            // Update bounding box
            boxHelper.geometry = new THREE.BoxGeometry(maxX - minX, maxY - minY, maxZ - minZ);
            boxHelper.position.set((minX + maxX) / 2, (minY + maxY) / 2, (minZ + maxZ) / 2);
        }

        // Render contact map
        function renderContactMap() {
            if (!p5Instance) return;

            const p = p5Instance;
            const width = p.width;
            const height = p.height;
            const residueX = parseInt(document.getElementById('slider-residue-x').value) - 1;
            const residueY = parseInt(document.getElementById('slider-residue-y').value) - 1;
            const rmsd = parseFloat(document.getElementById('slider-rmsd').value);

            p.background(255);
            p.stroke(220);
            p.strokeWeight(1);

            // Draw grid lines
            const step = width / 10;
            for (let i = 0; i <= 10; i++) {
                const pos = i * step;
                // Vertical lines
                p.line(pos, 0, pos, height);
                // Horizontal lines
                p.line(0, pos, width, pos);

                // Labels
                const labelValue = Math.round(i * 40);
                p.fill(100);
                p.noStroke();
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);

                // Bottom labels
                p.text(labelValue, pos, height - 15);
                // Left labels
                p.text(labelValue, 15, pos);
                // Top labels
                p.text(labelValue, pos, 15);
                // Right labels
                p.text(labelValue, width - 15, pos);
            }

            // Draw contact points
            p.stroke(0);
            p.strokeWeight(2);

            for (let i = 0; i < proteinData.length; i++) {
                for (let j = 0; j < proteinData.length; j++) {
                    if (distanceMatrix[i][j] <= rmsd) {
                        const x = p.map(i, 0, proteinData.length - 1, 0, width);
                        const y = p.map(j, 0, proteinData.length - 1, 0, height);
                        p.point(x, y);
                    }
                }
            }

            // Draw selection lines
            p.stroke(255, 0, 0);
            p.strokeWeight(2);

            // Vertical line for residue X
            const xPos = p.map(residueX, 0, proteinData.length - 1, 0, width);
            p.line(xPos, 0, xPos, height);

            // Horizontal line for residue Y
            const yPos = p.map(residueY, 0, proteinData.length - 1, 0, height);
            p.line(0, yPos, width, yPos);
        }

        // Update info display
        function updateInfoDisplay() {
            const browseSeq = parseInt(document.getElementById('slider-browse-sequence').value) - 1;
            const residueX = parseInt(document.getElementById('slider-residue-x').value) - 1;
            const residueY = parseInt(document.getElementById('slider-residue-y').value) - 1;

            const infoA = proteinData[browseSeq] ? `${proteinData[browseSeq].resName}${proteinData[browseSeq].resSeq}` : 'N/A';
            const infoX = proteinData[residueX] ? `${proteinData[residueX].resName}${proteinData[residueX].resSeq}` : 'N/A';
            const infoY = proteinData[residueY] ? `${proteinData[residueY].resName}${proteinData[residueY].resSeq}` : 'N/A';

            document.getElementById('info-display').textContent = 
                `Res A: ${infoA} | Res X: ${infoX} | Res Y: ${infoY}`;
        }

        // Handle residue selection from dropdown
        function handleResidueSelection() {
            const select = document.getElementById('select-residue');
            const selectedAA = select.value;
            const currentSeq = parseInt(document.getElementById('slider-browse-sequence').value);

            // Find next residue of selected type
            let foundIndex = -1;
            for (let i = currentSeq; i < proteinData.length; i++) {
                if (proteinData[i].resName === selectedAA) {
                    foundIndex = i + 1;
                    break;
                }
            }

            // If not found after current, search from beginning
            if (foundIndex === -1) {
                for (let i = 0; i < currentSeq; i++) {
                    if (proteinData[i].resName === selectedAA) {
                        foundIndex = i + 1;
                        break;
                    }
                }
            }

            if (foundIndex !== -1) {
                document.getElementById('slider-browse-sequence').value = foundIndex;
                document.getElementById('browse-sequence-value').textContent = foundIndex;
                handleViewChange();
            }
        }

        // Handle view change
        function handleViewChange() {
            if (currentView === 'crystallography') {
                renderProtein3D();
            } else {
                renderContactMap();
            }
            updateInfoDisplay();
        }

        // Handle plot size change
        function handlePlotSizeChange() {
            const size = parseInt(document.getElementById('slider-plot-size').value);
            const container = document.getElementById('canvas-container');

            // Set container size
            container.style.width = `${size}px`;
            container.style.height = `${size}px`;

            // Update renderers
            if (currentView === 'crystallography' && threeRenderer) {
                threeRenderer.setSize(size, size);
                threeCamera.aspect = size / size;
                threeCamera.updateProjectionMatrix();
            } else if (p5Instance) {
                p5Instance.resizeCanvas(size, size);
                renderContactMap();
            }
        }

        // Setup event listeners
        function setupEventListeners() {
            // View switcher
            document.getElementById('btn-crystallography').addEventListener('click', function() {
                currentView = 'crystallography';
                this.classList.add('active');
                document.getElementById('btn-contact-map').classList.remove('active');

                // Switch to 3D view
                initThreeJS();
            });

            document.getElementById('btn-contact-map').addEventListener('click', function() {
                currentView = 'contact-map';
                this.classList.add('active');
                document.getElementById('btn-crystallography').classList.remove('active');

                // Switch to 2D view
                initP5JS();
            });

            // Control panel inputs
            document.getElementById('checkbox-lateral-chains').addEventListener('change', function() {
                if (currentView === 'crystallography') {
                    renderProtein3D();
                }
            });

            // Sliders with immediate update
            const sliders = [
                'slider-gray-level', 'slider-browse-sequence', 
                'slider-residue-x', 'slider-residue-y', 'slider-rmsd'
            ];

            sliders.forEach(sliderId => {
                document.getElementById(sliderId).addEventListener('input', function() {
                    document.getElementById(sliderId + '-value').textContent = this.value;
                    handleViewChange();
                });
            });

            // Plot size slider needs special handling
            document.getElementById('slider-plot-size').addEventListener('input', function() {
                document.getElementById('plot-size-value').textContent = this.value;
                handlePlotSizeChange();
            });

            // Residue selector
            document.getElementById('select-residue').addEventListener('change', handleResidueSelection);

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', function() {
                if (currentView === 'crystallography' && threeControls) {
                    threeControls.reset();
                }
            });
        }
    </script>
</body>
