                <label for="slider-c">c</label>
                <input type="range" id="slider-c" min="1" max="224" step="1" value="24">
                <span id="display-c">24</span>
            </div>
        </div>
    </div>

    <div id="visualization-area">
        <p id="equation-text">Current equation: 7x + 8y = 24</p>
        <div id="canvas-container"></div>
    </div>

    <script>
        let sketch = function(p) {
            let canvas;
            let a = 7, b = 8, c = 24;
            let showDots = true;
            let showFrobenius = false;
            let gcd = 1;
            let canvasSize = 550;
            let margin = 50;

            p.setup = function() {
                canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('canvas-container');
                p.noLoop();
                updateEquationText();
            };

            p.draw = function() {
                p.background(255);
                p.translate(margin, canvasSize - margin);
                p.scale(1, -1);

                gcd = calculateGCD(a, b);

                if (gcd !== 1) {
                    return;
                }

                drawGrid();
                drawAxes();

                if (showFrobenius) {
                    c = a * b - a - b;
                    p.select('#slider-c').value(c);
                    p.select('#display-c').html(c);
                }

                if (showDots) {
                    drawDotsMode();
                } else {
                    drawNumbersMode();
                }
            };

            function drawGrid() {
                p.stroke(200);
                p.strokeWeight(1);

                // Vertical lines
                for (let x = 0; x <= b; x++) {
                    p.line(x, 0, x, a);
                }

                // Horizontal lines
                for (let y = 0; y <= a; y++) {
                    p.line(0, y, b, y);
                }
            }

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(2);

                // X axis
                p.line(0, 0, b, 0);

                // Y axis
                p.line(0, 0, 0, a);

                // Labels
                p.push();
                p.scale(1, -1);
                p.textAlign(p.CENTER, p.CENTER);
                p.fill(0, 0, 255);
                p.noStroke();

                // X axis labels
                for (let x = 0; x <= b; x++) {
                    p.text(x, x, -10);
                }

                // Y axis labels
                for (let y = 0; y <= a; y++) {
                    p.text(y, -10, -y);
                }

                p.pop();
            }

            function drawDotsMode() {
                // Draw all dots
                p.fill(0);
                p.noStroke();
                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        p.circle(x, y, 5);
                    }
                }

                // Draw the line ax + by = c
                let x1 = 0;
                let y1 = c / b;
                let x2 = c / a;
                let y2 = 0;

                // Clip to visible area
                if (y1 > a) {
                    y1 = a;
                    x1 = (c - b * y1) / a;
                }

                if (x2 > b) {
                    x2 = b;
                    y2 = (c - a * x2) / b;
                }

                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.line(x1, y1, x2, y2);

                // Highlight solutions
                p.fill(0, 255, 0);
                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        if (a * x + b * y === c) {
                            p.circle(x, y, 10);
                        }
                    }
                }
            }

            function drawNumbersMode() {
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(14);

                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        const v = a * x + b * y;

                        if (showFrobenius) {
                            // Highlight numbers that are NOT representable
                            if (v > a * b - a - b || !isRepresentable(v, a, b)) {
                                p.fill(255, 0, 0);
                            } else {
                                p.fill(0);
                            }
                        } else {
                            // Highlight current c
                            if (v === c) {
                                p.fill(0, 255, 0);
                            } else {
                                p.fill(0);
                            }
                        }

                        p.noStroke();
                        p.text(v, x, y);
                    }
                }
            }

            function isRepresentable(n, a, b) {
                for (let x = 0; x <= n / a; x++) {
                    if ((n - a * x) % b === 0 && (n - a * x) / b >= 0) {
                        return true;
                    }
                }
                return false;
            }

            function calculateGCD(a, b) {
                a = Math.abs(a);
                b = Math.abs(b);
                while (b) {
                    const temp = b;
                    b = a % b;
                    a = temp;
                }
                return a;
            }

            function updateEquationText() {
                const equationText = p.select('#equation-text');

                if (gcd !== 1) {
                    equationText.html('Numbers a and b should be coprime!').addClass('error');
                    return;
                }

                equationText.removeClass('error');

                if (showFrobenius) {
                    const frobenius = a * b - a - b;
                    equationText.html(`Frobenius number: F = ${a}×${b} - ${a} - ${b} = ${frobenius}`);
                } else {
                    equationText.html(`Current equation: ${a}x + ${b}y = ${c}`);
                }
            }
        };

        new p5(sketch);

        // UI Event Handlers
        document.getElementById('info-toggle').addEventListener('click', function() {
            const infoContent = document.getElementById('info-content');
            if (infoContent.style.display === 'block') {
                infoContent.style.display = 'none';
                this.textContent = '+';
            } else {
                infoContent.style.display = 'block';
                this.textContent = '-';
            }
        });

        document.getElementById('input-a').addEventListener('input', function() {
            const sketchInstance = document.querySelector('canvas')._p5;
            sketchInstance.a = parseInt(this.value);
            updateSketch();
        });

        document.getElementById('input-b').addEventListener('input', function() {
            const sketchInstance = document.querySelector('canvas')._p5;
            sketchInstance.b = parseInt(this.value);
            updateSketch();
        });

        document.getElementById('slider-c').addEventListener('input', function() {
            const sketchInstance = document.querySelector('canvas')._p5;
            sketchInstance.c = parseInt(this.value);
            document.getElementById('display-c').textContent = this.value;
            updateSketch();
        });

        document.getElementById('checkbox-mode').addEventListener('change', function() {
            const sketchInstance = document.querySelector('canvas')._p5;
            sketchInstance.showDots = this.checked;
            updateSketch();
        });

        document.getElementById('checkbox-display').addEventListener('change', function() {
            const sketchInstance = document.querySelector('canvas')._p5;
            sketchInstance.showFrobenius = this.checked;

            const slider = document.getElementById('slider-c');
            const displayC = document.getElementById('display-c');

            if (this.checked) {
                slider.disabled = true;
                displayC.style.color = '#999';
            } else {
                slider.disabled = false;
                displayC.style.color = '';
            }

            updateSketch();
        });

        function updateSketch() {
            const sketchInstance = document.querySelector('canvas')._p5;
            sketchInstance.gcd = sketchInstance.calculateGCD(sketchInstance.a, sketchInstance.b);

            if (sketchInstance.gcd === 1) {
                const maxC = sketchInstance.a * sketchInstance.b * 2;
                document.getElementById('slider-c').max = maxC;
            }

            sketchInstance.updateEquationText();
            sketchInstance.redraw();
        }
    </script>
</body>
</html>
