                    document.getElementById('p-position').textContent = 
                        `Current position {${Math.round(geo.lon)}, ${Math.round(geo.lat)}}`;
                    p.redraw();
                }
            };

            p.mouseClicked = () => {
                if (p.mouseX > 0 && p.mouseX < p.width && p.mouseY > 0 && p.mouseY < p.height) {
                    let foundAny = false;

                    for (let i = 0; i < currentData.length; i++) {
                        if (!currentData[i].found) {
                            const pos = mapToPixels(currentData[i].lon, currentData[i].lat);
                            const dist = p.dist(p.mouseX, p.mouseY, pos.x, pos.y);

                            if (dist <= 15) {
                                currentData[i].found = true;
                                foundAny = true;

                                // Update the table
                                const cells = document.querySelectorAll('#table-quiz td');
                                if (cells[i]) {
                                    cells[i].textContent = 'True';
                                    cells[i].classList.add('found');
                                }

                                break;
                            }
                        }
                    }

                    if (foundAny) {
                        p.redraw();
                    }
                }
            };

            p.windowResized = () => {
                const container = document.getElementById('canvas-container');
                canvasWidth = container.offsetWidth;
                canvasHeight = container.offsetHeight;
                p.resizeCanvas(canvasWidth, canvasHeight);
                p.redraw();
            };

            function mapToPixels(lon, lat) {
                return {
                    x: p.map(lon, -180, 180, 0, p.width),
                    y: p.map(lat, 90, -90, 0, p.height)
                };
            }

            function mapToGeo(x, y) {
                return {
                    lon: p.map(x, 0, p.width, -180, 180),
                    lat: p.map(y, 0, p.height, 90, -90)
                };
            }

            function updateQuizTable() {
                const table = document.getElementById('table-quiz');
                table.innerHTML = '';

                // Create header row
                const headerRow = document.createElement('tr');
                currentData.forEach(item => {
                    const th = document.createElement('th');
                    th.textContent = item.name;
                    headerRow.appendChild(th);
                });
                table.appendChild(headerRow);

                // Create status row
                const statusRow = document.createElement('tr');
                currentData.forEach(item => {
                    const td = document.createElement('td');
                    td.textContent = item.found ? 'True' : 'False';
                    if (item.found) {
                        td.classList.add('found');
                    }
                    statusRow.appendChild(td);
                });
                table.appendChild(statusRow);
            }

            function resetQuiz() {
                currentData.forEach(item => {
                    item.found = false;
                });

                const cells = document.querySelectorAll('#table-quiz td');
                cells.forEach(cell => {
                    cell.textContent = 'False';
                    cell.classList.remove('found');
                });
            }
        };

        // Create the p5.js instance
        new p5(sketch, 'canvas-container');
    </script>
</body>
</html>
