            </div>
        </div>
    </div>

    <script>
        // Tree data structure
        const nodes = [
            { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        ];

        // State variables
        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;
        let canvas;

        // Initialize the application
        document.addEventListener('DOMContentLoaded', function() {
            // Set up event listeners
            document.querySelectorAll('#tiers-container button').forEach(button => {
                button.addEventListener('click', function() {
                    currentTier = parseInt(this.textContent);
                    updateTierButtons();
                    updateExplanationText();
                    updateEncodingTableVisibility();
                    updateEncodedTextVisibility();
                    if (canvas) canvas.redraw();
                });
            });

            document.getElementById('checkbox-binary').addEventListener('change', function() {
                showBinary = this.checked;
                if (canvas) canvas.redraw();
            });

            document.getElementById('radio-chars-no').addEventListener('change', function() {
                if (this.checked) {
                    showNodeChars = false;
                    if (canvas) canvas.redraw();
                }
            });

            document.getElementById('radio-chars-yes').addEventListener('change', function() {
                if (this.checked) {
                    showNodeChars = true;
                    if (canvas) canvas.redraw();
                }
            });

            // Initialize p5.js canvas
            const sketch = function(p) {
                p.setup = function() {
                    const canvasDiv = document.getElementById('canvas-area');
                    const width = canvasDiv.offsetWidth;
                    const height = 500;
                    p.createCanvas(width, height).parent('canvas-area');
                    p.noLoop();
                };

                p.draw = function() {
                    p.background(255);
                    p.textSize(12);

                    // Draw edges first
                    for (const node of nodes) {
                        if (node.tier > 1 && node.tier <= currentTier) {
                            const child1 = nodes.find(n => n.id === node.children[0]);
                            const child2 = nodes.find(n => n.id === node.children[1]);

                            if (child1 && child1.tier <= currentTier) {
                                drawEdge(node, child1, '0');
                            }
                            if (child2 && child2.tier <= currentTier) {
                                drawEdge(node, child2, '1');
                            }
                        }
                    }

                    // Then draw nodes
                    for (const node of nodes) {
                        if (node.tier <= currentTier) {
                            drawNode(node);
                        }
                    }
                };

                function drawEdge(parent, child, binaryLabel) {
                    p.stroke('#C70000');
                    p.strokeWeight(2);
                    p.line(parent.pos.x, parent.pos.y, child.pos.x, child.pos.y);

                    if (showBinary) {
                        const midX = (parent.pos.x + child.pos.x) / 2;
                        const midY = (parent.pos.y + child.pos.y) / 2;

                        // Adjust position based on direction to avoid overlap
                        const angle = p.atan2(child.pos.y - parent.pos.y, child.pos.x - parent.pos.x);
                        const offsetX = 10 * p.sin(angle);
                        const offsetY = -10 * p.cos(angle);

                        p.fill(0);
                        p.noStroke();
                        p.text(binaryLabel, midX + offsetX, midY + offsetY);
                    }
                }

                function drawNode(node) {
                    // Determine node label
                    let nodeLabel;
                    if (node.tier === 1) {
                        nodeLabel = `${node.label}(${node.freq})`;
                    } else {
                        nodeLabel = showNodeChars ? `${node.label}(${node.freq})` : `(${node.freq})`;
                    }

                    // Calculate text width to determine box size
                    p.textSize(12);
                    const textWidth = p.textWidth(nodeLabel);
                    const boxWidth = textWidth + 20;
                    const boxHeight = 30;

                    // Draw rounded rectangle
                    p.fill('#FEFDD5');
                    p.stroke('#C70000');
                    p.strokeWeight(1);
                    p.rectMode(p.CENTER);
                    p.rect(node.pos.x, node.pos.y, boxWidth, boxHeight, 5);

                    // Draw text
                    p.fill(0);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(nodeLabel, node.pos.x, node.pos.y);
                }

                p.windowResized = function() {
                    const canvasDiv = document.getElementById('canvas-area');
                    const width = canvasDiv.offsetWidth;
                    p.resizeCanvas(width, 500);
                    p.redraw();
                };
            };

            canvas = new p5(sketch);
        });

        // Helper functions
        function updateTierButtons() {
            document.querySelectorAll('#tiers-container button').forEach(button => {
                button.classList.toggle('active', parseInt(button.textContent) === currentTier);
            });
        }

        function updateExplanationText() {
            const explanationText = document.getElementById('explanation-text');
            switch(currentTier) {
                case 1:
                    explanationText.textContent = "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
                    break;
                case 2:
                case 3:
                case 4:
                    explanationText.textContent = "We continue grouping until we get to the highest tier.";
                    break;
                case 5:
                    explanationText.textContent = "Once at the top, the total frequency becomes 17, which is the total number of characters.";
                    break;
            }
        }

        function updateEncodingTableVisibility() {
            const encodingTable = document.getElementById('encoding-table');
            encodingTable.style.display = currentTier >= 2 ? 'block' : 'none';
        }

        function updateEncodedTextVisibility() {
            const encodedText = document.getElementById('encoded-text');
            encodedText.style.display = currentTier === 5 ? 'block' : 'none';
        }
    </script>
</body>
</html>
