    <div id="control-panel">
        <label for="slider-n">n:</label>
        <input type="range" id="slider-n" min="1" max="20" step="1" value="1">
        <input type="number" id="input-n" min="1" max="20" step="1" value="1">
    </div>

    <div id="visualization-area">
        <div id="function-plot-container">
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
            <canvas id="canvas-function"></canvas>
        </div>
        <div id="sum-plot-container">
            <canvas id="canvas-sum"></canvas>
        </div>
    </div>

    <script>
        // Color array for the rectangles
        const colors = [
            '#ffff80', '#98fb98', '#7fffd4', '#00ffff', 
            '#87cefa', '#6495ed', '#9370db', '#8a2be2',
            '#ff80ff', '#ff80c0', '#ff8080', '#ffc080',
            '#ffff80', '#c0ff80', '#80ff80', '#80ffc0',
            '#80ffff', '#80c0ff', '#8080ff', '#c080ff'
        ];

        // Get DOM elements
        const sliderN = document.getElementById('slider-n');
        const inputN = document.getElementById('input-n');

        // Synchronize slider and input
        sliderN.addEventListener('input', function() {
            inputN.value = this.value;
            redrawAll();
        });

        inputN.addEventListener('input', function() {
            let value = parseInt(this.value);
            if (isNaN(value) || value < 1) value = 1;
            if (value > 20) value = 20;
            this.value = value;
            sliderN.value = value;
            redrawAll();
        });

        // Function to trigger redraw of both canvases
        function redrawAll() {
            if (functionPlot && sumPlot) {
                functionPlot.redraw();
                sumPlot.redraw();
            }
        }

        // p5.js sketch for the function plot
        const functionPlot = new p5((p) => {
            let canvas;

            p.setup = function() {
                const container = document.getElementById('function-plot-container');
                canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                canvas.parent('function-plot-container');
                p.redraw();
            };

            p.windowResized = function() {
                const container = document.getElementById('function-plot-container');
                p.resizeCanvas(container.clientWidth, container.clientHeight);
                p.redraw();
            };

            p.redraw = function() {
                p.clear();
                drawAxes();
                drawFunction();
                drawRectangles();
            };

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(1);

                // X-axis
                p.line(0, p.height - 30, p.width, p.height - 30);
                // Y-axis
                p.line(50, 0, 50, p.height - 30);

                // X-axis ticks and labels
                for (let x = 0; x <= 10; x++) {
                    const xPos = mapX(x);
                    p.line(xPos, p.height - 30, xPos, p.height - 25);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(x, xPos, p.height - 20);
                }

                // Y-axis ticks and labels
                for (let y = 0; y <= 2; y++) {
                    const yPos = mapY(y);
                    p.line(50, yPos, 45, yPos);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(y, 40, yPos);
                }

                // Axis labels
                p.textAlign(p.CENTER, p.TOP);
                p.text('x', p.width - 10, p.height - 30);
                p.textAlign(p.LEFT, p.TOP);
                p.text('y', 55, 10);
            }

            function drawFunction() {
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                for (let x = 0.5; x <= 10; x += 0.01) {
                    const y = 2 / x;
                    p.vertex(mapX(x), mapY(y));
                }
                p.endShape();
            }

            function drawRectangles() {
                const n = parseInt(sliderN.value);

                for (let k = 1; k <= n; k++) {
                    const color = colors[(k - 1) % colors.length];
                    const y1 = 2 / k;
                    const y2 = 2 / (k + 1);

                    // Draw rectangle
                    p.fill(color);
                    p.noStroke();
                    p.rect(mapX(k), mapY(y1), mapX(k + 1) - mapX(k), mapY(y2) - mapY(y1));

                    // Draw dashed vertical line for first 3 terms
                    if (k <= 3) {
                        p.stroke(150);
                        p.strokeWeight(1);
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(mapX(k), mapY(0), mapX(k), mapY(y1));
                        p.drawingContext.setLineDash([]);
                    }
                }
            }

            function mapX(x) {
                return 50 + (x / 10.5) * (p.width - 50);
            }

            function mapY(y) {
                return (p.height - 30) - (y / 2.2) * (p.height - 30);
            }
        }, 'function-plot-container');

        // p5.js sketch for the summation plot
        const sumPlot = new p5((p) => {
            let canvas;

            p.setup = function() {
                const container = document.getElementById('sum-plot-container');
                canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                canvas.parent('sum-plot-container');
                p.redraw();
            };

            p.windowResized = function() {
                const container = document.getElementById('sum-plot-container');
                p.resizeCanvas(container.clientWidth, container.clientHeight);
                p.redraw();
            };

            p.redraw = function() {
                p.clear();
                drawAxes();
                drawStackedRectangles();
            };

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(1);

                // X-axis
                p.line(0, p.height - 30, p.width, p.height - 30);
                // Y-axis
                p.line(50, 0, 50, p.height - 30);

                // X-axis ticks and labels
                p.line(mapX(0), p.height - 30, mapX(0), p.height - 25);
                p.line(mapX(1), p.height - 30, mapX(1), p.height - 25);
                p.textAlign(p.CENTER, p.TOP);
                p.text('0', mapX(0), p.height - 20);
                p.text('1', mapX(1), p.height - 20);

                // Y-axis ticks and labels
                for (let y = 0; y <= 2; y++) {
                    const yPos = mapY(y);
                    p.line(50, yPos, 45, yPos);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(y, 40, yPos);
                }

                // Axis labels
                p.textAlign(p.CENTER, p.TOP);
                p.text('x', p.width - 10, p.height - 30);
                p.textAlign(p.LEFT, p.TOP);
                p.text('y', 55, 10);
            }

            function drawStackedRectangles() {
                const n = parseInt(sliderN.value);
                let yStack = 0;

                // Draw from n down to 1 to match the left plot's color order
                for (let k = n; k >= 1; k--) {
                    const height = 2 / k - 2 / (k + 1);
                    const color = colors[(k - 1) % colors.length];

                    p.fill(color);
                    p.noStroke();
                    p.rect(mapX(0), mapY(yStack + height), mapX(1) - mapX(0), mapY(yStack) - mapY(yStack + height));

                    yStack += height;
                }
            }

            function mapX(x) {
                return 50 + (x / 1.5) * (p.width - 50);
            }

            function mapY(y) {
                return (p.height - 30) - (y / 2.2) * (p.height - 30);
            }
        }, 'sum-plot-container');
    </script>
</body>
