            </div>

            <h2>Parameters</h2>
            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4.0">
                <span id="value-x2f">4.0</span>
            </div>
            <div class="slider-container">
                <label for="slider-umax">μ<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax">0.6</span>
            </div>
            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km">0.12</span>
            </div>
            <div class="slider-container" id="k1-container">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>
            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds">0.6</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Global state
        let currentModel = 'monod';
        let currentView = 'bifurcation';
        let params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        // Initialize the page
        document.addEventListener('DOMContentLoaded', function() {
            // Set up event listeners for buttons
            document.getElementById('btn-monod').addEventListener('click', function() {
                currentModel = 'monod';
                document.getElementById('btn-monod').classList.add('active');
                document.getElementById('btn-si').classList.remove('active');
                document.getElementById('k1-container').classList.add('hidden');
                updatePlot();
            });

            document.getElementById('btn-si').addEventListener('click', function() {
                currentModel = 'si';
                document.getElementById('btn-si').classList.add('active');
                document.getElementById('btn-monod').classList.remove('active');
                document.getElementById('k1-container').classList.remove('hidden');
                updatePlot();
            });

            document.getElementById('btn-bifurcation').addEventListener('click', function() {
                currentView = 'bifurcation';
                document.getElementById('btn-bifurcation').classList.add('active');
                document.getElementById('btn-nontrivial').classList.remove('active');
                updatePlot();
            });

            document.getElementById('btn-nontrivial').addEventListener('click', function() {
                currentView = 'nontrivial';
                document.getElementById('btn-nontrivial').classList.add('active');
                document.getElementById('btn-bifurcation').classList.remove('active');
                updatePlot();
            });

            // Set up event listeners for sliders
            const sliders = ['x2f', 'umax', 'km', 'k1', 'ds'];
            sliders.forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const display = document.getElementById(`value-${param}`);

                slider.addEventListener('input', function() {
                    params[param] = parseFloat(this.value);
                    display.textContent = this.value;
                    updatePlot();
                });
            });

            // Hide k1 slider initially
            document.getElementById('k1-container').classList.add('hidden');

            // Initial plot
            updatePlot();
        });

        // Main plotting function
        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        // Bifurcation diagram
        function drawBifurcationDiagram() {
            const x2f = params.x2f;
            const umax = params.umax;
            const km = params.km;
            const k1 = params.k1;
            const ds = params.ds;

            // Generate x2 values
            const x2Values = Array.from({length: 100}, (_, i) => i * x2f / 99);

            // Calculate D values based on model
            let DValues, criticalPoint;
            if (currentModel === 'monod') {
                DValues = x2Values.map(x2 => umax * x2 / (km + x2));
                criticalPoint = null;
            } else {
                DValues = x2Values.map(x2 => umax * x2 / (km + x2 + k1 * x2 * x2));
                criticalPoint = Math.sqrt(km / k1);
            }

            // Prepare traces
            const traces = [];

            // Nontrivial steady state curve
            if (currentModel === 'monod') {
                traces.push({
                    x: DValues,
                    y: x2Values,
                    mode: 'lines',
                    line: {color: 'blue', width: 2},
                    name: 'Nontrivial steady state'
                });
            } else {
                // Split into stable and unstable parts
                const stableIdx = x2Values.findIndex(x2 => x2 > criticalPoint);

                traces.push({
                    x: DValues.slice(0, stableIdx),
                    y: x2Values.slice(0, stableIdx),
                    mode: 'lines',
                    line: {color: 'blue', width: 2},
                    name: 'Stable steady state'
                });

                traces.push({
                    x: DValues.slice(stableIdx),
                    y: x2Values.slice(stableIdx),
                    mode: 'lines',
                    line: {color: 'red', width: 2, dash: 'dash'},
                    name: 'Unstable steady state'
                });
            }

            // Trivial steady state line (x2 = x2f)
            const D_bif = currentModel === 'monod' 
                ? umax * x2f / (km + x2f)
                : umax * x2f / (km + x2f + k1 * x2f * x2f);

            traces.push({
                x: [0, D_bif],
                y: [x2f, x2f],
                mode: 'lines',
                line: {color: 'red', width: 2, dash: 'dash'},
                name: 'Unstable trivial state'
            });

            traces.push({
                x: [D_bif, 1.0],
                y: [x2f, x2f],
                mode: 'lines',
                line: {color: 'blue', width: 2},
                name: 'Stable trivial state'
            });

            // Find intersection points
            const intersections = findIntersections(DValues, x2Values, ds);

            // Add intersection points
            intersections.forEach((x2, i) => {
                const isStable = currentModel === 'monod' 
                    ? true 
                    : (x2 < criticalPoint);

                traces.push({
                    x: [ds],
                    y: [x2],
                    mode: 'markers',
                    marker: {
                        color: isStable ? 'magenta' : 'cyan',
                        size: 10
                    },
                    name: isStable ? 'Stable solution' : 'Unstable solution'
                });
            });

            // Add trivial solution point
            traces.push({
                x: [ds],
                y: [x2f],
                mode: 'markers',
                marker: {
                    color: 'green',
                    size: 10
                },
                name: 'Trivial solution'
            });

            // Layout
            const layout = {
                title: 'Bifurcation Diagram',
                xaxis: {
                    title: 'Dilution rate (D)',
                    range: [0, 1.0]
                },
                yaxis: {
                    title: 'X<sub>2</sub>',
                    range: [0, 6.0]
                },
                shapes: [{
                    type: 'line',
                    x0: ds,
                    x1: ds,
                    y0: 0,
                    y1: x2f,
                    line: {
                        color: 'gray',
                        width: 1,
                        dash: 'dot'
                    }
                }],
                showlegend: true
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        // Nontrivial state plot
        function drawNontrivialStatePlot() {
            const x2f = params.x2f;
            const umax = params.umax;
            const km = params.km;
            const k1 = params.k1;
            const ds = params.ds;

            // Generate x2 values
            const x2Values = Array.from({length: 100}, (_, i) => i * 7 / 99);

            // Calculate μ values
            const muValues = x2Values.map(x2 => umax * x2 / (km + x2 + k1 * x2 * x2));

            // Calculate bistability region bounds
            const D_upper = umax / (1 + 2 * Math.sqrt(k1 * km));
            const D_lower = umax * x2f / (km + x2f + k1 * x2f * x2f);

            // Prepare traces
            const traces = [];

            // μ curve
            traces.push({
                x: x2Values,
                y: muValues,
                mode: 'lines',
                line: {color: 'blue', width: 2},
                name: 'μ(x<sub>2</sub>)'
            });

            // D_s line
            traces.push({
                x: [0, 7],
                y: [ds, ds],
                mode: 'lines',
                line: {color: 'gray', width: 1},
                name: 'D<sub>s</sub>'
            });

            // Bistability region bounds
            traces.push({
                x: [0, 7],
                y: [D_upper, D_upper],
                mode: 'lines',
                line: {color: 'red', width: 1, dash: 'dash'},
                name: 'D<sub>upper</sub>'
            });

            traces.push({
                x: [0, 7],
                y: [D_lower, D_lower],
                mode: 'lines',
                line: {color: 'red', width: 1, dash: 'dash'},
                name: 'D<sub>lower</sub>'
            });

            // Find intersection points
            const intersections = [];
            for (let i = 0; i < x2Values.length - 1; i++) {
                const y1 = muValues[i] - ds;
                const y2 = muValues[i+1] - ds;

                if (y1 * y2 < 0) {
                    // Linear approximation for intersection
                    const x1 = x2Values[i];
                    const x2 = x2Values[i+1];
                    const x = x1 - y1 * (x2 - x1) / (y2 - y1);
                    intersections.push(x);
                }
            }

            // Add intersection points
            intersections.forEach(x2 => {
                const isStable = (muValues[x2Values.findIndex(val => val >= x2)] - 
                                  muValues[x2Values.findIndex(val => val >= x2) - 1]) > 0;

                traces.push({
                    x: [x2],
                    y: [ds],
                    mode: 'markers',
                    marker: {
                        color: isStable ? 'magenta' : 'cyan',
                        size: 10
                    },
                    name: isStable ? 'Stable solution' : 'Unstable solution'
                });
            });

            // Layout with shaded region
            const layout = {
                title: 'Nontrivial Steady State',
                xaxis: {
                    title: 'Substrate concentration (x<sub>2</sub>)',
                    range: [0, 7]
                },
                yaxis: {
                    title: 'Specific growth rate coefficient (μ)',
                    range: [0, 1]
                },
                shapes: [{
                    type: 'rect',
                    x0: 0,
                    x1: 7,
                    y0: D_lower,
                    y1: D_upper,
                    fillcolor: 'rgba(255, 165, 0, 0.2)',
                    line: {width: 0}
                }],
                showlegend: true
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        // Helper function to find intersections
        function findIntersections(DValues, x2Values, targetD) {
            const intersections = [];

            for (let i = 0; i < DValues.length - 1; i++) {
                if ((DValues[i] - targetD) * (DValues[i+1] - targetD) <= 0) {
                    // Linear approximation for intersection
                    const x1 = DValues[i];
                    const x2 = DValues[i+1];
                    const y1 = x2Values[i];
                    const y2 = x2Values[i+1];

                    const x = x1 === x2 
                        ? y1 
                        : y1 + (targetD - x1) * (y2 - y1) / (x2 - x1);

                    intersections.push(x);
                }
            }

            return intersections;
        }
    </script>
