            const solarDeclination = 23.5 * Math.cos(2 * Math.PI * season); // in degrees
            const solarDeclinationRad = solarDeclination * Math.PI / 180;
            const latitudeRad = latitude * Math.PI / 180;

            // Update 3D globe
            earth.rotation.y = (time / 24) * 2 * Math.PI;

            // Update sun position
            sunLight.position.set(0, Math.sin(solarDeclinationRad), -Math.cos(solarDeclinationRad));

            // Update latitude line
            scene.remove(latitudeLine);
            const newLatitudeGeometry = new THREE.CircleGeometry(Math.cos(latitudeRad), 64);
            newLatitudeGeometry.rotateX(Math.PI / 2);
