            );

            scene.remove(surfaceNormalArrow);
            surfaceNormalArrow = new THREE.ArrowHelper(
                pointOnSurface.clone().normalize(),
                pointOnSurface,
                0.5,
                0x000000
            );
            scene.add(surfaceNormalArrow);

            scene.remove(sunDirectionArrow);
            sunDirectionArrow = new THREE.ArrowHelper(
                sunLight.position.clone().normalize(),
                pointOnSurface,
                0.5,
                0xff0000
            );
            scene.add(sunDirectionArrow);

            // Calculate and update plots
            updatePlots(solarDeclination, latitude, time);

            // Render scene
            renderer.render(scene, camera);
        }

        function updatePlots(solarDeclination, latitude, currentTime) {
            const solarDeclinationRad = solarDeclination * Math.PI / 180;
            const latitudeRad = latitude * Math.PI / 180;

            // Generate time points for the plots
            const hours = Array.from({ length: 241 }, (_, i) => i * 0.1); // 0 to 24 in 0.1 steps

            // Calculate instantaneous energy for three latitudes
            const equatorEnergy = hours.map(hour => {
                const hra = 15 * (hour - 12) * Math.PI / 180;
                return 1361 * Math.max(0, 
                    Math.sin(0) * Math.sin(solarDeclinationRad) + 
                    Math.cos(0) * Math.cos(solarDeclinationRad) * Math.cos(hra)
                );
            });

            const selectedLatEnergy = hours.map(hour => {
                const hra = 15 * (hour - 12) * Math.PI / 180;
                return 1361 * Math.max(0, 
                    Math.sin(latitudeRad) * Math.sin(solarDeclinationRad) + 
                    Math.cos(latitudeRad) * Math.cos(solarDeclinationRad) * Math.cos(hra)
                );
            });

            const poleEnergy = hours.map(hour => {
                const hra = 15 * (hour - 12) * Math.PI / 180;
                return 1361 * Math.max(0, 
                    Math.sin(Math.PI/2) * Math.sin(solarDeclinationRad) + 
                    Math.cos(Math.PI/2) * Math.cos(solarDeclinationRad) * Math.cos(hra)
                );
            });

            // Calculate current time energy for marker
            const currentHRA = 15 * (currentTime - 12) * Math.PI / 180;
            const currentEnergy = 1361 * Math.max(0, 
                Math.sin(latitudeRad) * Math.sin(solarDeclinationRad) + 
                Math.cos(latitudeRad) * Math.cos(solarDeclinationRad) * Math.cos(currentHRA)
            );

            // Calculate cumulative energy
            const equatorCumulative = [];
            const selectedLatCumulative = [];
            const poleCumulative = [];

            let eqSum = 0, latSum = 0, poleSum = 0;
            for (let i = 0; i < hours.length; i++) {
                eqSum += equatorEnergy[i] * 0.1 / 3600; // convert to W·h/m²
                latSum += selectedLatEnergy[i] * 0.1 / 3600;
                poleSum += poleEnergy[i] * 0.1 / 3600;

                equatorCumulative.push(eqSum);
                selectedLatCumulative.push(latSum);
                poleCumulative.push(poleSum);
            }

            // Update instantaneous plot
            Plotly.react('plot-instant', [{
                x: hours,
                y: equatorEnergy,
                mode: 'lines',
                line: { color: 'red' },
                name: 'Equator (0°)'
            }, {
                x: hours,
                y: selectedLatEnergy,
                mode: 'lines',
                line: { color: 'black' },
                name: 'Selected Latitude'
            }, {
                x: hours,
                y: poleEnergy,
                mode: 'lines',
                line: { color: 'blue' },
                name: 'North Pole (90°)'
            }, {
                x: [currentTime],
                y: [currentEnergy],
                mode: 'markers',
                marker: { color: 'black', size: 8 },
                showlegend: false
            }], instantPlot._fullLayout);

            // Update cumulative plot
            Plotly.react('plot-cumulative', [{
                x: hours,
                y: equatorCumulative,
                mode: 'lines',
                line: { color: 'red' },
                name: 'Equator (0°)'
            }, {
                x: hours,
                y: selectedLatCumulative,
                mode: 'lines',
                line: { color: 'black' },
                name: 'Selected Latitude'
            }, {
                x: hours,
                y: poleCumulative,
                mode: 'lines',
                line: { color: 'blue' },
                name: 'North Pole (90°)'
            }], cumulativePlot._fullLayout);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();
    </script>
</body>
</html>
```

This HTML document includes:

1. A complete 3D Earth visualization using Three.js with:
   - Procedurally generated texture (no external assets)
   - Day/night shading based on sun position
   - Interactive orbit controls
   - Latitude lines and vector indicators

2. Two Plotly.js charts showing:
   - Instantaneous solar energy over 24 hours
   - Cumulative solar energy over 24 hours
   - With curves for equator, selected latitude, and North Pole

3. Interactive sliders for:
   - Season (affects solar declination)
   - Latitude (selects location on Earth)
   - Time of day (rotates Earth and updates plots)

4. A legend showing the color coding for the three latitude lines

5. Responsive design that adapts to window resizing

The visualization updates in real-time as sliders are adjusted, with all the physics calculations performed as specified in the implementation plan.

