                // Calculate scaling factor to fit the triangle in the canvas
                const scale = min(
                    (canvasWidth - 2*padding) / aValue,
                    (canvasHeight - 2*padding) / bValue
                );

                const scaledA = aValue * scale;
                const scaledB = bValue * scale;

                // Calculate center position
                const centerX = canvasWidth / 2;
                const centerY = canvasHeight / 2;

                // Calculate triangle vertices (right angle at bottom-left)
                const x1 = centerX - scaledA/2;
                const y1 = centerY + scaledB/2;
                const x2 = centerX + scaledA/2;
                const y2 = centerY + scaledB/2;
                const x3 = centerX - scaledA/2;
                const y3 = centerY - scaledB/2;

