        // Initialize Three.js scenes
        function initThreeJS() {
            // Primal problem scene
            primalScene = new THREE.Scene();
            primalCamera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
            primalRenderer = new THREE.WebGLRenderer({ antialias: true });
