            dualCamera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
            dualRenderer = new THREE.WebGLRenderer({ antialias: true });
            dualRenderer.setSize(document.getElementById('plot-dual').clientWidth, document.getElementById('plot-dual').clientHeight);
            document.getElementById('plot-dual').appendChild(dualRenderer.domElement);
            dualControls = new THREE.OrbitControls(dualCamera, dualRenderer.domElement);

            // Common setup for both scenes
            [primalScene, dualScene].forEach(scene => {
                // Add lights
                const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
                scene.add(ambientLight);

                const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
                directionalLight.position.set(1, 1, 1);
                scene.add(directionalLight);

                // Add axes helper
                const axesHelper = new THREE.AxesHelper(10);
                scene.add(axesHelper);

                // Add bounding box
                const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10)));
                scene.add(boxHelper);
