            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            const primalContainer = document.getElementById('plot-primal');
            const dualContainer = document.getElementById('plot-dual');

            primalCamera.aspect = primalContainer.clientWidth / primalContainer.clientHeight;
            primalCamera.updateProjectionMatrix();
            primalRenderer.setSize(primalContainer.clientWidth, primalContainer.clientHeight);

            dualCamera.aspect = dualContainer.clientWidth / dualContainer.clientHeight;
            dualCamera.updateProjectionMatrix();
            dualRenderer.setSize(dualContainer.clientWidth, dualContainer.clientHeight);
        }

        // Solve primal problem
        function solvePrimal() {
            const { alpha, beta, w, r, Q } = params;

            // Calculate optimal factors
            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;

            // Calculate minimum cost
            const C_out = w * x_p + r * y_p;

            solutions.primal = { x: x_p, y: y_p, C: C_out };

            // Update output display
            document.getElementById('output-primal').textContent = 
                `x = ${x_p.toFixed(2)}, y = ${y_p.toFixed(2)}, C = ${C_out.toFixed(2)}`;

            return { x: x_p, y: y_p, C: C_out };
        }

        // Solve dual problem
        function solveDual() {
            const { alpha, beta, w, r, C } = params;

            // Calculate optimal factors
            const x_d = (C * alpha) / (w * (alpha + beta));
            const y_d = (C * beta) / (r * (alpha + beta));

            // Calculate maximum quantity
            const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);

            solutions.dual = { x: x_d, y: y_d, Q: Q_out };

            // Update output display
            document.getElementById('output-dual').textContent = 
                `x = ${x_d.toFixed(2)}, y = ${y_d.toFixed(2)}, Q = ${Q_out.toFixed(2)}`;

            return { x: x_d, y: y_d, Q: Q_out };
        }

        // Update visualization
        function updateVisualization() {
            // Clear previous meshes (except lights, helpers, etc.)
            [primalScene, dualScene].forEach(scene => {
                const toRemove = [];
                scene.children.forEach(child => {
                    if (!(child instanceof THREE.Light || 
                          child instanceof THREE.AxesHelper || 
                          child instanceof THREE.BoxHelper)) {
                        toRemove.push(child);
                    }
                });
                toRemove.forEach(child => scene.remove(child));
            });

            const { alpha, beta, w, r, Q, C } = params;
            const primalSol = solutions.primal;
            const dualSol = solutions.dual;

            // Primal problem visualization
            // Isoquant curtain
            const isoquantPoints = [];
            const steps = 50;
            for (let i = 0; i <= steps; i++) {
                const x = 0.1 + (i / steps) * 9.9;
                const y = Math.pow(Q / Math.pow(x, alpha), 1/beta);

                // Base point
                isoquantPoints.push(new THREE.Vector3(x, y, 0));

                // Top point (arbitrary height for visualization)
                isoquantPoints.push(new THREE.Vector3(x, y, 10));
            }

            const isoquantGeometry = new THREE.BufferGeometry().setFromPoints(isoquantPoints);
            const isoquantMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xffa500, 
                side: THREE.DoubleSide,
                transparent: true,
                opacity: 0.7
            });
            const isoquantMesh = new THREE.Mesh(isoquantGeometry, isoquantMaterial);
            primalScene.add(isoquantMesh);

            // Isocost plane
            const isocostGeometry = new THREE.PlaneGeometry(10, 10);
            const isocostMaterial = new THREE.MeshBasicMaterial({
                color: 0x888888,
                side: THREE.DoubleSide,
                transparent: true,
                opacity: 0.5
            });
            const isocostMesh = new THREE.Mesh(isocostGeometry, isocostMaterial);

            // Position and rotate the plane to match w*x + r*y = C
            const normal = new THREE.Vector3(w, r, 0).normalize();
            const quaternion = new THREE.Quaternion().setFromUnitVectors(
                new THREE.Vector3(0, 0, 1), normal
            );
            isocostMesh.applyQuaternion(quaternion);
            isocostMesh.position.set(0, 0, primalSol.C / 2);
            primalScene.add(isocostMesh);

            // Solution vector
            const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(primalSol.x, primalSol.y, primalSol.C)
            ]);
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 3 });
            const line = new THREE.Line(lineGeometry, lineMaterial);
            primalScene.add(line);

            // Dual problem visualization
            // Production surface
            const surfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
                const x = 0.1 + u * 9.9;
                const y = 0.1 + v * 9.9;
                const q = Math.pow(x, alpha) * Math.pow(y, beta);
                target.set(x, y, q);
            }, 50, 50);

            const surfaceMaterial = new THREE.MeshBasicMaterial({
                color: 0xffa500,
                wireframe: true,
                transparent: true,
                opacity: 0.7
            });
            const surfaceMesh = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            dualScene.add(surfaceMesh);

            // Budget curtain
            const budgetPoints = [];
            for (let i = 0; i <= steps; i++) {
                const x = 0.1 + (i / steps) * 9.9;
                const y = (C - w * x) / r;

                // Base point
                budgetPoints.push(new THREE.Vector3(x, y, 0));

                // Top point
                const q = Math.pow(x, alpha) * Math.pow(y, beta);
                budgetPoints.push(new THREE.Vector3(x, y, q));
            }

            const budgetGeometry = new THREE.BufferGeometry().setFromPoints(budgetPoints);
            const budgetMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x888888, 
                side: THREE.DoubleSide,
                transparent: true,
                opacity: 0.5
            });
            const budgetMesh = new THREE.Mesh(budgetGeometry, budgetMaterial);
            dualScene.add(budgetMesh);

            // Intersection curve (red)
            const curvePoints = [];
            for (let i = 0; i <= steps; i++) {
                const x = 0.1 + (i / steps) * 9.9;
                const y = (C - w * x) / r;
                const q = Math.pow(x, alpha) * Math.pow(y, beta);
                curvePoints.push(new THREE.Vector3(x, y, q));
            }

            const curveGeometry = new THREE.BufferGeometry().setFromPoints(curvePoints);
            const curveMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
            const curve = new THREE.Line(curveGeometry, curveMaterial);
            dualScene.add(curve);

            // Optimal point marker
            const sphereGeometry = new THREE.SphereGeometry(0.1, 16, 16);
            const sphereMaterial = new THREE.MeshBasicMaterial({ color: 0x888888 });
            const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            sphere.position.set(dualSol.x, dualSol.y, dualSol.Q);
            dualScene.add(sphere);

            // Render scenes
            animate();
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            primalRenderer.render(primalScene, primalCamera);
            dualRenderer.render(dualScene, dualCamera);
        }

        // Main update function
        function update() {
            // Update parameters from sliders
            params.alpha = parseFloat(document.getElementById('slider-alpha').value);
            params.beta = parseFloat(document.getElementById('slider-beta').value);
            params.w = parseFloat(document.getElementById('slider-w').value);
            params.r = parseFloat(document.getElementById('slider-r').value);
            params.Q = parseFloat(document.getElementById('slider-Q').value);
            params.C = parseFloat(documentFloat.getElementById('slider-C').value);

            // Update labels
            document.getElementById('label-alpha').textContent = params.alpha.toFixed(2);
            document.getElementById('label-beta').textContent = params.beta.toFixed(2);
            document.getElementById('label-w').textContent = params.w.toFixed(2);
            document.getElementById('label-r').textContent = params.r.toFixed(2);
            document.getElementById('label-Q').textContent = params.Q.toFixed(1);
            document.getElementById('label-C').textContent = params.C.toFixed(1);

            // Solve problems
            solvePrimal();
            solveDual();

            // Update visualization
            updateVisualization();
        }

        // Initialize
        document.addEventListener('DOMContentLoaded', () => {
            initThreeJS();

            // Set up event listeners
            document.getElementById('slider-alpha').addEventListener('input', update);
            document.getElementById('slider-beta').addEventListener('input', update);
            document.getElementById('slider-w').addEventListener('input', update);
            document.getElementById('slider-r').addEventListener('input', update);
            document.getElementById('slider-Q').addEventListener('input', update);
            document.getElementById('slider-C').addEventListener('input', update);

            document.getElementById('btn-reset').addEventListener('click', () => {
                document.getElementById('slider-alpha').value = 0.5;
                document.getElementById('slider-beta').value = 0.5;
                document.getElementById('slider-w').value = 1.0;
                document.getElementById('slider-r').value = 1.0;
                document.getElementById('slider-Q').value = 3.0;
                document.getElementById('slider-C').value = 4.0;
                update();
            });

            document.getElementById('btn-Q-from-dual').addEventListener('click', () => {
                document.getElementById('slider-Q').value = solutions.dual.Q.toFixed(1);
                update();
            });

            document.getElementById('btn-C-from-primal').addEventListener('click', () => {
                document.getElementById('slider-C').value = solutions.primal.C.toFixed(1);
                update();
            });

            // Initial update
            update();
        });
    </script>
</body>
</html>
