</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <!-- Flash Pressure Slider -->
            <div class="control-row">
                <label for="slider-pressure">flash pressure</label>
                <input type="range" id="slider-pressure" min="100" max="1000" step="1" value="180">
                <span id="pressure-value">180 kPa</span>
            </div>
