    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // Global variables to store calculation results
        let flashResults = {
            T_flash: 0,
            V_frac: 0,
            x: [0, 0, 0, 0],
            y: [0, 0, 0, 0]
        };

        // Antoine equation constants
        const antoineConstants = [
            {A: 6.80165, B: 1005.157, C: -27.159},   // n-butane
            {A: 6.86177, B: 1184.249, C: -35.223},   // n-pentane
            {A: 6.90237, B: 1358.59, C: -51.303},    // n-heptane
            {A: 6.92383, B: 1431.149, C: -59.501}    // n-octane
        ];

        // Physical properties
        const Cp_L = [132, 161, 225, 255];           // kJ/kmol·K
        const deltaH_vap = [22400, 25800, 31800, 34400]; // kJ/kmol
        const z = [0.25, 0.25, 0.25, 0.25];          // Feed mole fractions
        const F = 10;                                 // kmol/hr
        const T_F = 323.2;                            // K
        const P_F = 1000;                             // kPa

        // Component names for display
        const componentNames = ["n-butane", "n-pentane", "n-heptane", "n-octane"];

        // p5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(800, 500);
                canvas.parent('visualization-area');
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);

                // Initial calculation with default values
                updateCalculation();
            };

            p.draw = function() {
                // Clear canvas
                p.background(240);

                // Draw flash vessel
                p.fill(230, 230, 250);
                p.stroke(0);
                p.strokeWeight(1);
                p.rect(350, 200, 100, 100);
                p.fill(0);
                p.text("Flash", 400, 250);

                // Draw feed stream (light green)
                p.stroke(154, 205, 50);
                p.strokeWeight(2);
                p.line(100, 250, 350, 250);
                drawArrow(p, 350, 250, 100, 250);

                // Draw vapor stream (purple)
                p.stroke(147, 112, 219);
                p.strokeWeight(2);
                p.line(400, 200, 400, 100);
                p.line(400, 100, 550, 100);
                drawArrow(p, 550, 100, 400, 100);

                // Draw liquid stream (orange)
                p.stroke(255, 165, 0);
                p.strokeWeight(2);
                p.line(400, 300, 400, 400);
                p.line(400, 400, 550, 400);
                drawArrow(p, 550, 400, 400, 400);

                // Draw heat input (dashed red)
                p.stroke(255, 0, 0);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([5, 5]);
                p.line(300, 150, 400, 200);
                drawArrow(p, 400, 200, 300, 150);
                p.drawingContext.setLineDash([]);

                // Draw text labels
                p.fill(0);
                p.textAlign(p.LEFT, p.CENTER);

                // Feed stream info
                p.text("Feed:", 50, 220);
                p.text("T = 323.2 K", 50, 240);
                p.text("P = 1000 kPa", 50, 260);
                p.text("z = [0.25, 0.25, 0.25, 0.25]", 50, 280);

                // Vapor stream info
                p.text("Vapor mole fractions:", 570, 80);
                for (let i = 0; i < 4; i++) {
                    p.text(`${componentNames[i]}: ${flashResults.y[i].toFixed(4)}`, 570, 100 + i * 20);
                }

                // Liquid stream info
                p.text("Liquid mole fractions:", 570, 380);
                for (let i = 0; i < 4; i++) {
                    p.text(`${componentNames[i]}: ${flashResults.x[i].toFixed(4)}`, 570, 400 + i * 20);
                }

                // Process info
                p.textAlign(p.CENTER, p.CENTER);
                p.text(`Vapor fraction: ${flashResults.V_frac.toFixed(4)}`, 600, 250);
                p.text(`Flash temperature: ${flashResults.T_flash.toFixed(2)} K`, 600, 280);
                p.text(`Flash pressure: ${document.getElementById('slider-pressure').value} kPa`, 600, 310);

                // Heat info
                p.text(`Q = ${document.getElementById('slider-heat').value} kJ/hr`, 300, 130);
            };

            function drawArrow(p, x1, y1, x2, y2) {
                // Draw an arrowhead at (x1,y1) pointing to (x2,y2)
                let angle = p.atan2(y1 - y2, x1 - x2);
                p.push();
                p.translate(x1, y1);
                p.rotate(angle);
                p.line(0, 0, -10, -5);
                p.line(0, 0, -10, 5);
                p.pop();
            }
        };

        // Create a new p5 instance
        new p5(sketch);

        // Helper function to calculate saturation pressure using Antoine equation
        function calculatePsat(A, B, C, T) {
            return Math.pow(10, A - B / (T + C));
        }

        // Rachford-Rice equation
        function rachfordRice(V, K, z) {
            let sum = 0;
            for (let i = 0; i < z.length; i++) {
                sum += z[i] * (K[i] - 1) / (1 + V * (K[i] - 1));
            }
            return sum;
        }

        // Solve Rachford-Rice equation for V using bisection method
        function solveRachfordRice(K, z) {
            let V_min = 0;
            let V_max = 1;
            let V_mid;
            let tolerance = 1e-6;
            let maxIterations = 100;

            for (let i = 0; i < maxIterations; i++) {
                V_mid = (V_min + V_max) / 2;
                let rr = rachfordRice(V_mid, K, z);

                if (Math.abs(rr) < tolerance) {
                    return V_mid;
                }

                if (rr * rachfordRice(V_min, K, z) < 0) {
                    V_max = V_mid;
                } else {
                    V_min = V_mid;
                }
            }

            return V_mid;
        }

        // Energy balance equation
        function energyBalance(T, P_flash, Q_hr, z) {
            // Calculate K-values at this temperature
            let K = [];
            for (let i = 0; i < z.length; i++) {
                const A = antoineConstants[i].A;
                const B = antoineConstants[i].B;
                const C = antoineConstants[i].C;
                const Psat = calculatePsat(A, B, C, T);
                K.push(Psat / P_flash);
            }

            // Solve Rachford-Rice for V
            const V = solveRachfordRice(K, z);

            // Calculate mole fractions
            let x = [];
            let y = [];
            for (let i = 0; i < z.length; i++) {
                x.push(z[i] / (1 + V * (K[i] - 1)));
                y.push(K[i] * x[i]);
            }

            // Calculate left side of energy balance (feed enthalpy - liquid/vapor enthalpy)
            let leftSide = Q_hr / F;
            for (let i = 0; i < z.length; i++) {
                leftSide += z[i] * Cp_L[i] * (T_F - T) / 1000; // Convert to kJ/kmol
            }

            // Calculate right side of energy balance (vaporization enthalpy)
            let rightSide = 0;
            for (let i = 0; i < z.length; i++) {
                rightSide += y[i] * deltaH_vap[i] * V;
            }
            rightSide /= 1000; // Convert to kJ/kmol

            return leftSide - rightSide;
        }

        // Solve energy balance for T using bisection method
        function solveEnergyBalance(P_flash, Q_hr, z) {
            let T_min = 273;  // 0°C
            let T_max = 500;  // Upper bound
            let T_mid;
            let tolerance = 0.1;
            let maxIterations = 100;

            for (let i = 0; i < maxIterations; i++) {
                T_mid = (T_min + T_max) / 2;
                let eb = energyBalance(T_mid, P_flash, Q_hr, z);

                if (Math.abs(eb) < tolerance) {
                    return T_mid;
                }

                if (eb * energyBalance(T_min, P_flash, Q_hr, z) < 0) {
                    T_max = T_mid;
                } else {
                    T_min = T_mid;
                }
            }

            return T_mid;
        }

        // Main calculation function
        function calculateFlash(P_flash, Q_hr) {
            // Solve for temperature that satisfies energy balance
            const T = solveEnergyBalance(P_flash, Q_hr, z);

            // Calculate K-values at this temperature
            let K = [];
            for (let i = 0; i < z.length; i++) {
                const A = antoineConstants[i].A;
                const B = antoineConstants[i].B;
                const C = antoineConstants[i].C;
                const Psat = calculatePsat(A, B, C, T);
                K.push(Psat / P_flash);
            }

            // Solve Rachford-Rice for V
            const V = solveRachfordRice(K, z);

            // Calculate mole fractions
            let x = [];
            let y = [];
            for (let i = 0; i < z.length; i++) {
                x.push(z[i] / (1 + V * (K[i] - 1)));
                y.push(K[i] * x[i]);
            }

            // Store results globally
            flashResults = {
                T_flash: T,
                V_frac: V,
                x: x,
                y: y
            };
        }

        // Update calculation when sliders change
        function updateCalculation() {
            const P_flash = parseFloat(document.getElementById('slider-pressure').value);
            const Q_hr = parseFloat(document.getElementById('slider-heat').value);

            // Update displayed values
            document.getElementById('pressure-value').textContent = P_flash + ' kPa';
            document.getElementById('heat-value').textContent = Q_hr + ' kJ/hr';

            // Perform calculation
            calculateFlash(P_flash, Q_hr);
        }

        // Event listeners for sliders
        document.getElementById('slider-pressure').addEventListener('input', updateCalculation);
        document.getElementById('slider-heat').addEventListener('input', updateCalculation);
    </script>
</body>
</html>
