        });
        const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
        earth.add(atmosphere);

        // Create Moon surface (for land view)
        const moonSurfaceGeometry = new THREE.PlaneGeometry(100, 100);
        const moonSurfaceMaterial = new THREE.MeshStandardMaterial({ color: 0x888888, roughness: 0.8 });
        const moonSurface = new THREE.Mesh(moonSurfaceGeometry, moonSurfaceMaterial);
        moonSurface.rotation.x = -Math.PI / 2;
        moonSurface.position.y = -1;
        scene.add(moonSurface);

        // Create Moon sphere (for space view)
        const moonGeometry = new THREE.SphereGeometry(1, 32, 32);
        const moonMaterial = new THREE.MeshStandardMaterial({ color: 0x888888, roughness: 0.8 });
        const moon = new THREE.Mesh(moonGeometry, moonMaterial);
        scene.add(moon);
        moon.visible = false;

        // Create constellations group
