        scene.add(constellations);

        // Sample constellation data (simplified)
        const constellationData = [
            { name: "Orion", stars: [
                { x: 0, y: 0, z: -10 }, { x: 2, y: 0, z: -10 }, { x: 2, y: 3, z: -10 },
                { x: 0, y: 3, z: -10 }, { x: 0, y: 1.5, z: -10 }, { x: -2, y: 1.5, z: -10 }
            ]}
        ];

        // Create constellation stars and lines
        constellationData.forEach(constellation => {
            const starPositions = [];
            const linePositions = [];

            constellation.stars.forEach(star => {
                starPositions.push(star.x, star.y, star.z);
            });

            // Simple line connections (just for demo)
            for (let i = 0; i < constellation.stars.length - 1; i++) {
                linePositions.push(
                    constellation.stars[i].x, constellation.stars[i].y, constellation.stars[i].z,
                    constellation.stars[i+1].x, constellation.stars[i+1].y, constellation.stars[i+1].z
                );
            }

            // Create stars
            const starsGeometry = new THREE.BufferGeometry();
            starsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starPositions, 3));
            const starsMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 0.2 });
            const stars = new THREE.Points(starsGeometry, starsMaterial);
            constellations.add(stars);

            // Create lines
            const linesGeometry = new THREE.BufferGeometry();
            linesGeometry.setAttribute('position', new THREE.Float32BufferAttribute(linePositions, 3));
            const linesMaterial = new THREE.LineBasicMaterial({ color: 0xffffaa });
            const lines = new THREE.LineSegments(linesGeometry, linesMaterial);
            constellations.add(lines);
        });

        // Set initial camera position (land view)
        camera.position.set(0, 1.5, 5);
        camera.lookAt(10, 0, 0);

        // State management
        const state = {
            viewMode: 'land',
            time: 0,
            nodalAngle: 0,
            sunEarthAngle: Math.PI/2,
            zodiacAngle: 0,
            isAnimating: {
                time: false,
                nodalAngle: false,
                sunEarthAngle: false,
                zodiacAngle: false
            },
            showConstellations: true,
            realisticSize: false,
            lastFrameTime: performance.now()
        };

        // UI event handlers
        document.getElementById('btn-land').addEventListener('click', () => {
            state.viewMode = 'land';
            document.getElementById('btn-land').classList.add('active');
            document.getElementById('btn-space').classList.remove('active');

            // Update scene for land view
            moonSurface.visible = true;
            moon.visible = false;
            camera.position.set(0, 1.5, 5);
            camera.lookAt(10, 0, 0);
        });

        document.getElementById('btn-space').addEventListener('click', () => {
            state.viewMode = 'space';
            document.getElementById('btn-space').classList.add('active');
            document.getElementById('btn-land').classList.remove('active');

            // Update scene for space view
            moonSurface.visible = false;
            moon.visible = true;
            camera.position.set(0, 0, 20);
            camera.lookAt(0, 0, 0);
        });

        // Slider handlers
        document.getElementById('slider-time').addEventListener('input', (e) => {
            state.time = parseFloat(e.target.value);
            document.getElementById('slider-time-value').textContent = state.time.toFixed(2);
        });

        document.getElementById('slider-nodal-angle').addEventListener('input', (e) => {
            state.nodalAngle = parseFloat(e.target.value);
            document.getElementById('slider-nodal-angle-value').textContent = state.nodalAngle.toFixed(2);
        });

        document.getElementById('slider-sun-earth-angle').addEventListener('input', (e) => {
            state.sunEarthAngle = parseFloat(e.target.value);
            document.getElementById('slider-sun-earth-angle-value').textContent = state.sunEarthAngle.toFixed(2);
        });

        document.getElementById('slider-zodiac').addEventListener('input', (e) => {
            state.zodiacAngle = parseFloat(e.target.value);
            document.getElementById('slider-zodiac-value').textContent = state.zodiacAngle.toFixed(2);
        });

        // Play button handlers
        document.getElementById('btn-time-play').addEventListener('click', (e) => {
            state.isAnimating.time = !state.isAnimating.time;
            e.target.classList.toggle('active', state.isAnimating.time);
        });

        document.getElementById('btn-nodal-angle-play').addEventListener('click', (e) => {
            state.isAnimating.nodalAngle = !state.isAnimating.nodalAngle;
            e.target.classList.toggle('active', state.isAnimating.nodalAngle);
        });

        document.getElementById('btn-sun-earth-angle-play').addEventListener('click', (e) => {
            state.isAnimating.sunEarthAngle = !state.isAnimating.sunEarthAngle;
            e.target.classList.toggle('active', state.isAnimating.sunEarthAngle);
        });

        document.getElementById('btn-zodiac-play').addEventListener('click', (e) => {
            state.isAnimating.zodiacAngle = !state.isAnimating.zodiacAngle;
            e.target.classList.toggle('active', state.isAnimating.zodiacAngle);
        });

        // Checkbox handlers
        document.getElementById('checkbox-constellations').addEventListener('change', (e) => {
            state.showConstellations = e.target.checked;
            constellations.visible = state.showConstellations;
        });

        document.getElementById('checkbox-realistic-size').addEventListener('change', (e) => {
            state.realisticSize = e.target.checked;
            const scale = state.realisticSize ? 0.5 : 1;
            earth.scale.set(scale, scale, scale);
            atmosphere.scale.set(scale, scale, scale);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            const currentTime = performance.now();
            const deltaTime = (currentTime - state.lastFrameTime) / 1000; // Convert to seconds
            state.lastFrameTime = currentTime;

            // Update animated values
            if (state.isAnimating.time) {
                state.time = (state.time + deltaTime * 0.5) % 100;
                document.getElementById('slider-time').value = state.time;
                document.getElementById('slider-time-value').textContent = state.time.toFixed(2);
            }

            if (state.isAnimating.nodalAngle) {
                state.nodalAngle = (state.nodalAngle + deltaTime * 0.5) % 6.283;
                document.getElementById('slider-nodal-angle').value = state.nodalAngle;
                document.getElementById('slider-nodal-angle-value').textContent = state.nodalAngle.toFixed(2);
            }

            if (state.isAnimating.sunEarthAngle) {
                state.sunEarthAngle = (state.sunEarthAngle + deltaTime * 0.5) % 6.283;
                document.getElementById('slider-sun-earth-angle').value = state.sunEarthAngle;
                document.getElementById('slider-sun-earth-angle-value').textContent = state.sunEarthAngle.toFixed(2);
            }

            if (state.isAnimating.zodiacAngle) {
                state.zodiacAngle = (state.zodiacAngle + deltaTime * 0.5) % 6.283;
                document.getElementById('slider-zodiac').value = state.zodiacAngle;
                document.getElementById('slider-zodiac-value').textContent = state.zodiacAngle.toFixed(2);
            }

            // Update scene based on state
            // Update Earth position based on time and angles
            const earthDistance = 10;
            const earthX = earthDistance * Math.cos(state.time * 0.1);
            const earthZ = earthDistance * Math.sin(state.time * 0.1);

            // Apply nodal angle (tilt)
            const tiltX = earthX * Math.cos(state.nodalAngle) - earthZ * Math.sin(state.nodalAngle);
            const tiltZ = earthX * Math.sin(state.nodalAngle) + earthZ * Math.cos(state.nodalAngle);

            earth.position.set(tiltX, 0, tiltZ);

            // Update Sun position based on sunEarthAngle
            const sunDistance = 50;
            const sunX = sunDistance * Math.cos(state.sunEarthAngle + state.time * 0.1);
            const sunZ = sunDistance * Math.sin(state.sunEarthAngle + state.time * 0.1);
            directionalLight.position.set(sunX, 0, sunZ);

            // Update constellations rotation
            constellations.rotation.y = state.zodiacAngle;

            // Check for eclipse (Earth between Sun and Moon)
            const earthToSun = new THREE.Vector3().subVectors(
                directionalLight.position,
                earth.position
            ).normalize();

            const earthToMoon = new THREE.Vector3().subVectors(
                new THREE.Vector3(0, 0, 0),
                earth.position
            ).normalize();

            const dot = earthToSun.dot(earthToMoon);
            if (dot > 0.99) { // Approximate eclipse condition
                earthMaterial.color.setHex(0xff3333);
                atmosphereMaterial.color.setHex(0xff3333);
            } else {
                earthMaterial.color.setHex(0x1a66ff);
                atmosphereMaterial.color.setHex(0x1a66ff);
            }

            renderer.render(scene, camera);
        }

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            renderer.setSize(width, height, false);
        });

        // Start animation
        animate();
    </script>
</body>
</html>
