        // Constants
        const C0 = 850;        // Initial atmospheric carbon (GtC)
        const E0 = 8.5;        // Initial annual emission rate (GtC/yr)
        const C_eq = 586;      // Pre-industrial equilibrium (GtC)
        const E_floor = 1.5;   // Floor for emission rate (GtC/yr)
        const rise_rate = 0.02; // Rate of emission increase (GtC/yr^2)
        const time_step = 0.5; // Time step for numerical integration (years)
        const max_time = 300;  // Maximum time for simulation (years)
        const target_ceiling = 1200; // Target ceiling value (GtC)

        // Initialize plot with default values
        document.addEventListener('DOMContentLoaded', function() {
            updatePlot();

            // Set up event listeners for sliders
            document.getElementById('slider-residence-time').addEventListener('input', function() {
                document.getElementById('value-residence-time').textContent = this.value;
                updatePlot();
            });

            document.getElementById('slider-transition-pace').addEventListener('input', function() {
                document.getElementById('value-transition-pace').textContent = this.value;
                updatePlot();
            });

            document.getElementById('slider-wedges').addEventListener('input', function() {
                document.getElementById('value-wedges').textContent = parseFloat(this.value).toFixed(2);
                updatePlot();
            });
        });

        function updatePlot() {
            // Get current slider values
            const t_res = parseFloat(document.getElementById('slider-residence-time').value);
            const t_trans = parseFloat(document.getElementById('slider-transition-pace').value);
            const N_wedges = parseFloat(document.getElementById('slider-wedges').value);

            // Calculate model data
            const { timeArray, emissionsArray, carbonArray } = calculateModel(t_res, t_trans, N_wedges);

            // Prepare data for Plotly
            const traceCarbon = {
                x: timeArray,
                y: carbonArray,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                name: 'Atmospheric Carbon'
            };

            const traceEmissions = {
                x: timeArray,
                y: emissionsArray.map(e => 100 * e),
                type: 'scatter',
                mode: 'lines',
                line: { color: 'purple', width: 2 },
                name: 'Annual Emissions × 100'
            };

            const traceTarget = {
                x: [0, max_time],
                y: [target_ceiling, target_ceiling],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'black', width: 2, dash: 'dash' },
                name: 'Target Ceiling'
            };

            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: { title: 'years', range: [0, max_time], zeroline: true },
                yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
                showlegend: false,
                margin: { l: 60, r: 20, b: 40, t: 80 },
                paper_bgcolor: '#f9f9f9',
                plot_bgcolor: 'white'
            };

            // Update or create plot
            Plotly.react('plot-div', [traceCarbon, traceEmissions, traceTarget], layout);
        }

        function calculateModel(t_res, t_trans, N_wedges) {
            const timeArray = [];
            const emissionsArray = [];
            const carbonArray = [];

            // Calculate peak emission rate
            const E_peak = E0 + rise_rate * t_trans;
            const reduction_rate = N_wedges / 50;

            // Initialize variables
            let t = 0;
            let C_prev = C0;

            // Main simulation loop
            while (t <= max_time) {
                // Calculate emission rate E(t)
                let E_t;
                if (t < t_trans) {
                    E_t = E0 + rise_rate * t;
                } else {
                    const E_linear = E_peak - reduction_rate * (t - t_trans);
                    E_t = Math.max(E_floor, E_linear);
                }

                // Calculate atmospheric carbon C(t) using Euler's method
                const dC = (E_t - (C_prev - C_eq) / t_res) * time_step;
                const C_t = C_prev + dC;

                // Store values
                timeArray.push(t);
                emissionsArray.push(E_t);
                carbonArray.push(C_t);

                // Update for next iteration
                t += time_step;
                C_prev = C_t;
            }

            return { timeArray, emissionsArray, carbonArray };
        }
    </script>
