                p.background(240);

                // Calculate positions
                const px = p.width / 2 + x * scale;
                const py = p.height / 2;
                const bx = px + (l * scale) * p.sin(theta);
                const by = py + (l * scale) * p.cos(theta);

                // Draw connector
                p.stroke(0);
                p.strokeWeight(2);
                p.line(px, py, bx, by);

                // Draw pivot
                p.noStroke();
                p.fill('red');
                p.rectMode(p.CENTER);
                p.rect(px, py, 20, 20);

                // Draw bob
                p.fill('orange');
                p.stroke('black');
                p.strokeWeight(2);
                p.ellipse(bx, by, 30, 30);

                // Update physics if playing
                if (isPlaying) {
                    updatePhysics();
                }
            };

            function updatePhysics() {
                // Calculate angular acceleration
                const sinTheta = p.sin(theta);
                const cosTheta = p.cos(theta);
                const numerator = -m * l * sinTheta * cosTheta * theta_dot * theta_dot - g * (M + m) * sinTheta;
                const denominator = l * (M + m * sinTheta * sinTheta);
                const theta_ddot = numerator / denominator;

                // Update angular velocity and angle
                theta_dot += theta_ddot * dt;
                theta += theta_dot * dt;

                // Update pivot velocity and position
                x_dot = parseFloat(document.getElementById('slider-v0').value) - (m * l * cosTheta * theta_dot) / (M + m);
                x += x_dot * dt;

                // Update time
                t += dt;
            }
        };

        // Create p5 instance
        new p5(sketch);

        // Helper function to format theta value
        function formatThetaValue(val) {
            const pi = Math.PI;
            const commonFractions = [
                {value: -pi, text: "-π"},
                {value: -pi/2, text: "-π/2"},
                {value: -pi/3, text: "-π/3"},
                {value: -pi/4, text: "-π/4"},
                {value: -pi/6, text: "-π/6"},
                {value: 0, text: "0"},
                {value: pi/6, text: "π/6"},
                {value: pi/4, text: "π/4"},
                {value: pi/3, text: "π/3"},
                {value: pi/2, text: "π/2"},
                {value: pi, text: "π"}
            ];

            for (let fraction of commonFractions) {
                if (Math.abs(val - fraction.value) < 0.01) {
                    return fraction.text;
                }
            }

            return val.toFixed(2);
        }

        // Reset simulation to initial state
        function resetSimulation() {
            const theta0 = parseFloat(document.getElementById('slider-theta0').value);
            const v0 = parseFloat(document.getElementById('slider-v0').value);
            l = parseFloat(document.getElementById('slider-length').value);

            theta = theta0;
            theta_dot = 0;
            x = 0;
            x_dot = v0;
            t = 0;
        }

        // Event listeners for sliders
        document.getElementById('slider-theta0').addEventListener('input', function() {
            const value = parseFloat(this.value);
            document.getElementById('value-theta0').textContent = formatThetaValue(value);
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            resetSimulation();
        });

        document.getElementById('slider-v0').addEventListener('input', function() {
            const value = parseFloat(this.value);
            document.getElementById('value-v0').textContent = value.toFixed(1);
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            resetSimulation();
        });

        document.getElementById('slider-length').addEventListener('input', function() {
            const value = parseFloat(this.value);
            document.getElementById('value-length').textContent = value.toFixed(1);
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            resetSimulation();
        });

        // Event listeners for buttons
        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '||' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            resetSimulation();
        });
    </script>
</body>
</html>
