        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Create sphere geometry
        const sphereGeometry = new THREE.SphereGeometry(1, 64, 32);
        const originalVertices = sphereGeometry.attributes.position.clone();

        // Create sphere material with vertex colors
        const sphereMaterial = new THREE.MeshStandardMaterial({
            vertexColors: true,
            flatShading: true,
            side: THREE.DoubleSide
        });

        // Create sphere mesh
        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        // Create bounding box helper
        const boxHelper = new THREE.BoxHelper(sphere, 0x888888);
        scene.add(boxHelper);
        boxHelper.visible = false;

        // Create compass rose
        const compassRadius = 1.2;
        const compassCurve = new THREE.EllipseCurve(
            0, 0, compassRadius, compassRadius, 0, 2 * Math.PI, false, 0
        );
        const compassPoints = compassCurve.getPoints(100);
        const compassGeometry = new THREE.BufferGeometry().setFromPoints(compassPoints);
        const compassMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
        const compass = new THREE.Line(compassGeometry, compassMaterial);
        compass.rotation.x = Math.PI / 2; // Rotate to XZ plane
        scene.add(compass);

        // Create compass labels
        function createCompassLabel(text, position, rotation) {
            const canvas = document.createElement('canvas');
            canvas.width = 64;
            canvas.height = 32;
            const context = canvas.getContext('2d');
            context.font = 'Bold 20px Arial';
            context.fillStyle = 'black';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText(text, 32, 16);

            const texture = new THREE.CanvasTexture(canvas);
            const material = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(material);
            sprite.position.copy(position);
            if (rotation) sprite.rotation.copy(rotation);
            sprite.scale.set(0.1, 0.05, 1);
            scene.add(sprite);
        }

        createCompassLabel('N', new THREE.Vector3(0, 0, -compassRadius));
        createCompassLabel('E', new THREE.Vector3(compassRadius, 0, 0));
        createCompassLabel('S', new THREE.Vector3(0, 0, compassRadius));
        createCompassLabel('W', new THREE.Vector3(-compassRadius, 0, 0));

        // Create axis labels (P, T, U)
        const axisLabels = {
            p: { text: 'P', color: 0xff0000 },
            t: { text: 'T', color: 0x0000ff },
            u: { text: 'U', color: 0x00ff00 }
        };

        const labelSprites = {};
        for (const [key, value] of Object.entries(axisLabels)) {
            const canvas = document.createElement('canvas');
            canvas.width = 64;
            canvas.height = 32;
            const context = canvas.getContext('2d');
            context.font = 'Bold 20px Arial';
            context.fillStyle = 'white';
            context.strokeStyle = 'black';
            context.lineWidth = 2;
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.strokeText(value.text, 32, 16);
            context.fillText(value.text, 32, 16);

            const texture = new THREE.CanvasTexture(canvas);
