            });
            const sprite = new THREE.Sprite(material);
            sprite.scale.set(0.15, 0.075, 1);
            scene.add(sprite);
            labelSprites[key] = sprite;
        }

        // Calculate fault parameters
        function calculateFaultParameters(strikeDeg, dipDeg, slipAngleDeg) {
            const strike = THREE.MathUtils.degToRad(strikeDeg);
            const dip = THREE.MathUtils.degToRad(dipDeg);
            const slipAngle = THREE.MathUtils.degToRad(slipAngleDeg);

            // Calculate fault normal vector (n) and slip vector (u)
            const n = new THREE.Vector3(
                Math.sin(dip) * Math.sin(strike),
                -Math.sin(dip) * Math.cos(strike),
                -Math.cos(dip)
            );

            const u = new THREE.Vector3(
                Math.cos(slipAngle) * Math.cos(strike) + Math.sin(slipAngle) * Math.cos(dip) * Math.sin(strike),
                Math.cos(slipAngle) * Math.sin(strike) - Math.sin(slipAngle) * Math.cos(dip) * Math.cos(strike),
                Math.sin(slipAngle) * Math.sin(dip)
            );

            // Calculate P and T axes
            const p = new THREE.Vector3().addVectors(n, u).normalize();
            const t = new THREE.Vector3().subVectors(u, n).normalize();

            return { n, u, p, t };
        }

        // Update sphere colors based on P and T axes
        function updateSphereColors(p, t) {
            const colors = [];
            const positions = sphereGeometry.attributes.position;
            const count = positions.count;

            for (let i = 0; i < count; i++) {
                const x = positions.getX(i);
                const y = positions.getY(i);
                const z = positions.getZ(i);
                const vertex = new THREE.Vector3(x, y, z).normalize();

                // Calculate dot products with P and T axes
                const dotP = vertex.dot(p);
                const dotT = vertex.dot(t);

                // Determine if vertex is in compressive or dilatational quadrant
                const isCompressive = (dotP * dotP) > (dotT * dotT);

                // Assign color (white for compressive, orange for dilatational)
                const color = isCompressive ? new THREE.Color(0xffffff) : new THREE.Color(0xffa500);
                colors.push(color.r, color.g, color.b);
            }

            sphereGeometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            sphereGeometry.attributes.color.needsUpdate = true;
        }

        // Deform sphere based on slip amount
        function deformSphere(p, t, slipAmount) {
            const positions = sphereGeometry.attributes.position;
            const count = positions.count;

            for (let i = 0; i < count; i++) {
                const x = originalVertices.getX(i);
                const y = originalVertices.getY(i);
                const z = originalVertices.getZ(i);
                const vertex = new THREE.Vector3(x, y, z);
                const normalized = vertex.clone().normalize();

                // Calculate dot products with P and T axes
                const dotP = normalized.dot(p);
                const dotT = normalized.dot(t);

                // Calculate deformation factor
                const deformation = (dotP * dotP) - (dotT * dotT);
                const deformationScale = 0.5 * slipAmount;

                // Apply deformation
                vertex.add(normalized.multiplyScalar(deformation * deformationScale));

                // Update position
                positions.setXYZ(i, vertex.x, vertex.y, vertex.z);
            }

            positions.needsUpdate = true;
            sphereGeometry.computeVertexNormals();
        }

        // Update axis labels positions
        function updateAxisLabels(p, t, u) {
            // Position labels at the surface of the sphere
            labelSprites.p.position.copy(p.clone().multiplyScalar(1.1));
            labelSprites.t.position.copy(t.clone().multiplyScalar(1.1));
            labelSprites.u.position.copy(u.clone().multiplyScalar(1.1));

            // Make labels face the camera
            labelSprites.p.lookAt(camera.position);
            labelSprites.t.lookAt(camera.position);
            labelSprites.u.lookAt(camera.position);
        }

        // Update visualization based on current parameters
        function updateVisualization() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const slipAngle = parseFloat(document.getElementById('slider-slip-angle').value);
            const slipAmount = parseFloat(document.getElementById('slider-slip-amount').value);

            const { n, u, p, t } = calculateFaultParameters(strike, dip, slipAngle);

            updateSphereColors(p, t);
            deformSphere(p, t, slipAmount);
            updateAxisLabels(p, t, u);

            // Update bounding box
            boxHelper.update();
        }

        // Initialize UI controls
        function initializeControls() {
            // Set initial values in text displays
            document.getElementById('value-strike').value = document.getElementById('slider-strike').value;
            document.getElementById('value-dip').value = document.getElementById('slider-dip').value;
            document.getElementById('value-slip-angle').value = document.getElementById('slider-slip-angle').value;
            document.getElementById('value-slip-amount').value = document.getElementById('slider-slip-amount').value;

            // Add event listeners for sliders
            document.getElementById('slider-strike').addEventListener('input', function() {
                document.getElementById('value-strike').value = this.value;
                updateVisualization();
            });

            document.getElementById('slider-dip').addEventListener('input', function() {
                document.getElementById('value-dip').value = this.value;
                updateVisualization();
            });

            document.getElementById('slider-slip-angle').addEventListener('input', function() {
                document.getElementById('value-slip-angle').value = this.value;
                updateVisualization();
            });

            document.getElementById('slider-slip-amount').addEventListener('input', function() {
                document.getElementById('value-slip-amount').value = this.value;
                updateVisualization();
            });

            // Add event listener for checkbox
            document.getElementById('checkbox-box').addEventListener('change', function() {
                boxHelper.visible = this.checked;
            });

            // Add event listener for reset button
            document.getElementById('btn-reset').addEventListener('click', function() {
                document.getElementById('slider-strike').value = 180;
                document.getElementById('slider-dip').value = 60;
                document.getElementById('slider-slip-angle').value = 90;
                document.getElementById('slider-slip-amount').value = 0.5;
                document.getElementById('checkbox-box').checked = false;

                document.getElementById('value-strike').value = 180;
                document.getElementById('value-dip').value = 60;
                document.getElementById('value-slip-angle').value = 90;
                document.getElementById('value-slip-amount').value = 0.5;

                boxHelper.visible = false;
                updateVisualization();
            });
        }

        // Handle window resize
        function onWindowResize() {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        window.addEventListener('resize', onWindowResize);

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize and start
        initializeControls();
        updateVisualization();
        onWindowResize();
        animate();
    </script>
