            border-radius: 4px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
                <div class="value-display">+<span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
                <div class="value-display">+<span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probability</div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let sketch = function(p) {
            let N = 10;
            let k = 2;
            const targetIndex = 6;
            let canvasWidth, canvasHeight;
            let sectionHeight;

            p.setup = function() {
                canvasWidth = document.getElementById('canvas-container').offsetWidth;
                canvasHeight = document.getElementById('canvas-container').offsetHeight;
                sectionHeight = canvasHeight / 4;

                let canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');

                // Initialize sliders
                document.getElementById('slider-db-size').addEventListener('input', function() {
                    N = parseInt(this.value);
                    document.getElementById('display-db-size').textContent = N;
                    updateProbability();
                });

                document.getElementById('slider-iterations').addEventListener('input', function() {
                    k = parseInt(this.value);
                    document.getElementById('display-iterations').textContent = k;
                    updateProbability();
                });
            };

            p.draw = function() {
                p.background(255);

                // Draw the four sections
                drawSection(0, "Original Amplitudes", getInitialAmplitudes(N));
                drawSection(1, "After First Inversion", applyInversion(getInitialAmplitudes(N), targetIndex));
                drawSection(2, "After First Diffusion", applyDiffusion(applyInversion(getInitialAmplitudes(N), targetIndex)));
                drawSection(3, `After ${k} Iterations`, applyGroverIterations(N, k, targetIndex));
            };

            function drawSection(sectionIndex, title, amplitudes) {
                const yOffset = sectionIndex * sectionHeight;
                const margin = 30;
                const plotWidth = canvasWidth - 2 * margin;

                // Draw section title
                p.fill(0);
                p.noStroke();
                p.textSize(14);
                p.textAlign(p.CENTER);
                p.text(title, canvasWidth / 2, yOffset + 20);

                // Draw baseline
                p.stroke(200);
                p.line(margin, yOffset + sectionHeight / 2, canvasWidth - margin, yOffset + sectionHeight / 2);

                // Draw parentheses around states (only for first section)
                if (sectionIndex === 0) {
                    const parenWidth = 20;
                    const parenHeight = sectionHeight * 0.6;

                    p.noFill();
                    p.stroke(150);
                    // Left parenthesis
                    p.arc(margin - parenWidth/2, yOffset + sectionHeight/2, parenWidth, parenHeight, -p.HALF_PI, p.HALF_PI);
                    // Right parenthesis
                    p.arc(canvasWidth - margin + parenWidth/2, yOffset + sectionHeight/2, parenWidth, parenHeight, p.HALF_PI, p.HALF_PI*3);
                }

                // Draw amplitude bars
                const barWidth = plotWidth / N * 0.8;
                const barSpacing = plotWidth / N;
                const scaleFactor = sectionHeight * 0.4;

                for (let i = 0; i < amplitudes.length; i++) {
                    const x = margin + i * barSpacing + barSpacing/2;
                    const y = yOffset + sectionHeight / 2;
                    const barHeight = amplitudes[i] * scaleFactor;

                    // Set color: orange for target state, gray for others
                    if (i === targetIndex && targetIndex < N) {
                        p.fill(255, 165, 0); // orange
                    } else {
                        p.fill(150); // gray
                    }

                    p.noStroke();
                    p.rectMode(p.CENTER);
                    p.rect(x, y - barHeight/2, barWidth, barHeight);
                }
            }

            function getInitialAmplitudes(N) {
                let amplitudes = new Array(N);
                const initialValue = 1 / Math.sqrt(N);
                for (let i = 0; i < N; i++) {
                    amplitudes[i] = initialValue;
                }
                return amplitudes;
            }

            function applyInversion(amplitudes, targetIndex) {
                if (targetIndex < amplitudes.length) {
                    amplitudes[targetIndex] *= -1;
                }
                return amplitudes;
            }

            function applyDiffusion(amplitudes) {
                // Calculate mean amplitude
                let mean = 0;
                for (let i = 0; i < amplitudes.length; i++) {
                    mean += amplitudes[i];
                }
                mean /= amplitudes.length;

                // Invert about the mean
                let newAmplitudes = new Array(amplitudes.length);
                for (let i = 0; i < amplitudes.length; i++) {
                    newAmplitudes[i] = 2 * mean - amplitudes[i];
                }

                return newAmplitudes;
            }

            function applyGroverIterations(N, k, targetIndex) {
                let amplitudes = getInitialAmplitudes(N);

                for (let i = 0; i < k; i++) {
                    amplitudes = applyInversion(amplitudes, targetIndex);
                    amplitudes = applyDiffusion(amplitudes);
                }

                return amplitudes;
            }

            function updateProbability() {
                if (N <= 0) return;

                const angle = Math.asin(1 / Math.sqrt(N));
                const successProbability = Math.pow(Math.sin((2 * k + 1) * angle), 2);
                const percentage = (successProbability * 100).toFixed(1);

                document.getElementById('probability-display').textContent = 
                    `${percentage}% success probability`;
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
