    <script>
        // Parameters with default values
        let params = {
            volatility: 2.5,
            distillate: 0.95,
            bottom: 0.05,
            feedComp: 0.50,
            feedQuality: 1.0
        };

        // p5.js sketch
        let sketch = function(p) {
            let canvas;
            let margin = 50;
            let plotWidth, plotHeight;
            let stageCount = 0;

            p.setup = function() {
                let container = document.getElementById('canvas-container');
                canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
