            };

            p.windowResized = function() {
                let container = document.getElementById('canvas-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                plotWidth = p.width - 2 * margin;
                plotHeight = p.height - 2 * margin;
                drawDiagram();
            };

            function drawDiagram() {
                p.background(255);
                p.strokeWeight(1);

                // Draw axes
                p.stroke(128);
                p.line(margin, margin, margin, p.height - margin); // Y-axis
                p.line(margin, p.height - margin, p.width - margin, p.height - margin); // X-axis

                // Draw axis labels
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);
                p.text('x (liquid composition)', p.width/2, p.height - margin/2);
                p.textAlign(p.CENTER, p.CENTER);
                p.push();
                p.translate(margin/2, p.height/2);
                p.rotate(-p.HALF_PI);
                p.text('y (vapor composition)', 0, 0);
                p.pop();

                // Draw tick marks
                for (let i = 0; i <= 1; i += 0.1) {
                    let x = margin + i * plotWidth;
                    let y = p.height - margin - i * plotHeight;

                    // X-axis ticks
                    p.line(x, p.height - margin, x, p.height - margin + 5);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(i.toFixed(1), x, p.height - margin + 10);

                    // Y-axis ticks
                    p.line(margin - 5, y, margin, y);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(i.toFixed(1), margin - 10, y);
                }

                // Draw equilibrium curve (red)
                p.stroke(255, 0, 0);
                p.noFill();
                p.beginShape();
                for (let x = 0; x <= 1; x += 0.01) {
                    let y = (params.volatility * x) / (1 + (params.volatility - 1) * x);
                    let plotX = margin + x * plotWidth;
                    let plotY = p.height - margin - y * plotHeight;
                    p.vertex(plotX, plotY);
                }
                p.endShape();

                // Draw diagonal line (green)
                p.stroke(34, 139, 34);
                p.line(margin, p.height - margin, p.width - margin, margin);

                // Calculate intersection point
                let xF = params.feedComp;
                let q = params.feedQuality;
                let m = q / (q - 1);

                // Special cases for feed line
                let xInt, yInt;

                if (q === 1) {
                    // Vertical line
                    xInt = xF;
                    yInt = (params.volatility * xInt) / (1 + (params.volatility - 1) * xInt);
                } else if (q === 0) {
                    // Horizontal line
                    yInt = xF;
                    // Solve y = xF = (α * x) / (1 + (α - 1) * x) for x
                    xInt = yInt / (params.volatility - (params.volatility - 1) * yInt);
                } else {
                    // General case: solve intersection of q-line and equilibrium curve
                    // q-line equation: y = m*(x - xF) + xF
                    // Equilibrium curve: y = (α * x) / (1 + (α - 1) * x)
                    // Solve m*(x - xF) + xF = (α * x) / (1 + (α - 1) * x)
                    // This is a quadratic equation: a*x^2 + b*x + c = 0
                    let a = (params.volatility - 1) * m;
                    let b = m + (params.volatility - 1) * (xF - m * xF) - params.volatility;
                    let c = m * xF - xF;

                    let discriminant = b * b - 4 * a * c;
                    xInt = (-b - Math.sqrt(discriminant)) / (2 * a); // We take the solution between 0 and 1
                    yInt = m * (xInt - xF) + xF;
                }

                // Draw operating lines
                let xD = params.distillate;
                let xB = params.bottom;

                // Rectifying line (cyan) from (xD, xD) to (xInt, yInt)
                p.stroke(0, 255, 255);
                p.line(
                    margin + xD * plotWidth, p.height - margin - xD * plotHeight,
                    margin + xInt * plotWidth, p.height - margin - yInt * plotHeight
                );

                // Stripping line (magenta) from (xB, xB) to (xInt, yInt)
                p.stroke(255, 0, 255);
                p.line(
                    margin + xB * plotWidth, p.height - margin - xB * plotHeight,
                    margin + xInt * plotWidth, p.height - margin - yInt * plotHeight
                );

                // Feed line (dark blue) from (xF, xF) to (xInt, yInt)
                p.stroke(0, 0, 205);
                p.line(
                    margin + xF * plotWidth, p.height - margin - xF * plotHeight,
                    margin + xInt * plotWidth, p.height - margin - yInt * plotHeight
                );

                // Calculate and draw stages
                p.stroke(0);
                p.strokeWeight(1);
                stageCount = 0;

                let currentX = xD;
                let currentY = xD;

                while (true) {
                    // Horizontal step to equilibrium curve
                    let newX = currentX;
                    let newY = (params.volatility * newX) / (1 + (params.volatility - 1) * newX);

                    p.line(
                        margin + currentX * plotWidth, p.height - margin - currentY * plotHeight,
                        margin + newX * plotWidth, p.height - margin - newY * plotHeight
                    );

                    currentX = newX;
                    currentY = newY;

                    // Check if we've reached the bottom
                    if (currentX <= xB) {
                        stageCount++;
                        break;
                    }

                    stageCount++;

                    // Vertical step to operating line
                    let operatingLineY;
                    if (currentX > xInt) {
                        // Rectifying line
                        operatingLineY = xD + (currentX - xD) * (yInt - xD) / (xInt - xD);
                    } else {
                        // Stripping line
                        operatingLineY = xB + (currentX - xB) * (yInt - xB) / (xInt - xB);
                    }

                    p.line(
                        margin + currentX * plotWidth, p.height - margin - currentY * plotHeight,
                        margin + currentX * plotWidth, p.height - margin - operatingLineY * plotHeight
                    );

                    currentY = operatingLineY;
                }

                // Update stage count display
                document.getElementById('stages-count-display').textContent = 
                    `The number of stages is ${stageCount}.`;
            }
        };

        // Create p5 instance
        new p5(sketch);

        // Initialize sliders
        document.addEventListener('DOMContentLoaded', function() {
            // Set up sliders with their properties
            const sliders = {
                'slider-volatility': { min: 1.1, max: 10, step: 0.1, value: 2.5, display: 'volatility-value' },
                'slider-distillate': { min: 0.51, max: 0.99, step: 0.01, value: 0.95, display: 'distillate-value' },
                'slider-bottom': { min: 0.01, max: 0.49, step: 0.01, value: 0.05, display: 'bottom-value' },
                'slider-feed-comp': { min: 0.02, max: 0.98, step: 0.01, value: 0.50, display: 'feed-comp-value' },
                'slider-feed-quality': { min: 0.0, max: 2.0, step: 0.05, value: 1.0, display: 'feed-quality-value' }
            };

            // Initialize slider values and event listeners
            for (const [id, props] of Object.entries(sliders)) {
                const slider = document.getElementById(id);
                slider.min = props.min;
                slider.max = props.max;
                slider.step = props.step;
                slider.value = props.value;

                // Update display value
                document.getElementById(props.display).textContent = props.value;

                // Add input event listener
                slider.addEventListener('input', function() {
                    params[id.split('-')[1]] = parseFloat(this.value);
                    document.getElementById(props.display).textContent = this.value;

                    // Enforce parameter constraints
                    enforceConstraints();
                    drawDiagram();
                });
            }

            // Set up increment buttons
            const buttons = {
                'btn-volatility-plus': 'slider-volatility',
                'btn-distillate-plus': 'slider-distillate',
                'btn-bottom-plus': 'slider-bottom',
                'btn-feed-comp-plus': 'slider-feed-comp',
                'btn-feed-quality-plus': 'slider-feed-quality'
            };

            for (const [btnId, sliderId] of Object.entries(buttons)) {
                document.getElementById(btnId).addEventListener('click', function() {
                    const slider = document.getElementById(sliderId);
                    const step = parseFloat(slider.step);
                    const max = parseFloat(slider.max);

                    let newValue = parseFloat(slider.value) + step;
                    if (newValue > max) newValue = max;

                    slider.value = newValue;
                    params[sliderId.split('-')[1]] = newValue;
                    document.getElementById(sliders[sliderId].display).textContent = newValue.toFixed(2);

                    // Enforce parameter constraints
                    enforceConstraints();
                    drawDiagram();
                });
            }

            // Function to enforce parameter constraints
            function enforceConstraints() {
                const bottomSlider = document.getElementById('slider-bottom');
                const feedCompSlider = document.getElementById('slider-feed-comp');
                const distillateSlider = document.getElementById('slider-distillate');

                const bottom = parseFloat(bottomSlider.value);
                const feedComp = parseFloat(feedCompSlider.value);
                const distillate = parseFloat(distillateSlider.value);
                const step = parseFloat(feedCompSlider.step);

                // Enforce bottom < feedComp < distillate
                if (bottom >= feedComp) {
                    const newFeedComp = bottom + step;
                    if (newFeedComp <= distillate) {
                        feedCompSlider.value = newFeedComp;
                        params['feedComp'] = newFeedComp;
                        document.getElementById('feed-comp-value').textContent = newFeedComp.toFixed(2);
                    }
                }

                if (distillate <= feedComp) {
                    const newFeedComp = distillate - step;
                    if (newFeedComp >= bottom) {
                        feedCompSlider.value = newFeedComp;
                        params['feedComp'] = newFeedComp;
                        document.getElementById('feed-comp-value').textContent = newFeedComp.toFixed(2);
                    }
                }

                // Update feedComp min and max based on bottom and distillate
                feedCompSlider.min = parseFloat(bottomSlider.value) + step;
                feedCompSlider.max = parseFloat(distillateSlider.value) - step;
            }

            // Wrapper function to redraw the diagram
            function drawDiagram() {
                if (sketch && sketch.drawDiagram) {
                    sketch.drawDiagram();
                }
            }
        });
    </script>
</body>
