        }

        // Three.js initialization for 3D view
        function initThreeJS() {
            const canvas = document.getElementById('main-canvas');

            // Scene
            threeScene = new THREE.Scene();
            threeScene.background = new THREE.Color(0x000000);

            // Camera
            threeCamera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            threeCamera.position.set(0, 0, 5);

            // Renderer
            threeRenderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            threeRenderer.setSize(window.innerWidth, window.innerHeight);

            // Controls
            threeControls = new THREE.OrbitControls(threeCamera, canvas);
            threeControls.enableDamping = true;
            threeControls.dampingFactor = 0.05;

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);

            const sunLight = new THREE.PointLight(0xffffff, 1, 100);
            sunLight.position.set(0, 0, 0);
            threeScene.add(sunLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sunMesh = new THREE.Mesh(sunGeometry, sunMaterial);
            threeScene.add(sunMesh);

            // Earth's orbit path
            const orbitCurve = new THREE.EllipseCurve(
                0, 0,                         // center
                3, 3,                          // xRadius, yRadius
                0, 2 * Math.PI,                // startAngle, endAngle
                false,                         // clockwise
                0                              // rotation
            );

            const orbitPoints = orbitCurve.getPoints(100);
            const orbitGeometry = new THREE.BufferGeometry().setFromPoints(orbitPoints);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
            const orbitLine = new THREE.Line(orbitGeometry, orbitMaterial);
            threeScene.add(orbitLine);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(0.1, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ 
                color: 0x4169E1,
                roughness: 0.7,
                metalness: 0.3
            });
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            threeScene.add(earthMesh);

            // Earth's axis
            const axisDirection = new THREE.Vector3(0, 1, 0);
            axisDirection.applyAxisAngle(new THREE.Vector3(1, 0, 0), THREE.MathUtils.degToRad(23.5));
            earthAxis = new THREE.ArrowHelper(
                axisDirection.clone().normalize(),
                new THREE.Vector3(0, 0, 0),
                0.15,
                0x00ff00
            );
            earthMesh.add(earthAxis);

            // Create magnetosphere shape
            const magnetoShape = new THREE.Shape();
            magnetoShape.moveTo(0, 0.1);
            magnetoShape.quadraticCurveTo(0.3, 0.1, 0.4, 0);
            magnetoShape.quadraticCurveTo(0.3, -0.1, 0, -0.1);
            magnetoShape.lineTo(0, -0.3);
            magnetoShape.quadraticCurveTo(-0.8, -0.3, -1.5, 0);
            magnetoShape.quadraticCurveTo(-0.8, 0.3, 0, 0.3);
            magnetoShape.lineTo(0, 0.1);

            const magnetoGeometry = new THREE.LatheGeometry(magnetoShape.getPoints(50), 32);
            const magnetoMaterial = new THREE.MeshStandardMaterial({
                color: 0x0000ff,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });
            magnetosphereMesh = new THREE.Mesh(magnetoGeometry, magnetoMaterial);
            magnetosphereMesh.scale.set(0.5, 0.5, 0.5);
            earthMesh.add(magnetosphereMesh);

            // Solar wind arrows
            for (let i = 0; i < 50; i++) {
                const angle = Math.random() * Math.PI * 2;
                const radius = 0.5 + Math.random() * 2.5;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);

                const direction = new THREE.Vector3(-x, -y, 0).normalize();
                const arrow = new THREE.ArrowHelper(
                    direction,
                    new THREE.Vector3(x, y, 0),
                    0.2,
                    0xff0000
                );
                threeScene.add(arrow);
            }

            // Animation loop
            function animate() {
                requestAnimationFrame(animate);
                threeControls.update();
                threeRenderer.render(threeScene, threeCamera);
            }

            animate();

            // Handle window resize
            window.addEventListener('resize', function() {
                threeCamera.aspect = window.innerWidth / window.innerHeight;
                threeCamera.updateProjectionMatrix();
                threeRenderer.setSize(window.innerWidth, window.innerHeight);
            });
        }

        // Update 3D scene based on current month
        function update3DScene() {
            if (!threeScene) return;

            // Calculate Earth's position in orbit (simplified circular orbit)
            const angle = (currentMonth / 12) * Math.PI * 2;
            const radius = 3;
            earthMesh.position.x = radius * Math.cos(angle);
            earthMesh.position.y = radius * Math.sin(angle);

            // Update Earth's axis tilt relative to Sun
            const tiltAxis = new THREE.Vector3(1, 0, 0);
            earthMesh.rotation.set(0, 0, THREE.MathUtils.degToRad(23.5));
            earthMesh.rotateOnAxis(tiltAxis, angle);

            // Ensure magnetosphere tail points away from Sun
            const sunDirection = new THREE.Vector3().subVectors(
                earthMesh.position,
                new THREE.Vector3(0, 0, 0)
            ).normalize();

            magnetosphereMesh.lookAt(sunDirection);
        }

        // Initialize with 2D view
        switchTo2D();
    </script>
</body>
</html>
