    <script>
        // Gamma function approximation (Lanczos)
        function gamma(z) {
            const g = 7;
            const p = [
                0.99999999999980993, 676.5203681218851, -1259.1392167224028,
                771.32342877765313, -176.61502916214059, 12.507343278686905,
                -0.13857109526572012, 9.9843695780195716e-6, 1.5056327351493116e-7
            ];

            if (z < 0.5) {
                return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
            } else {
                z -= 1;
                let x = p[0];
                for (let i = 1; i < g + 2; i++) {
                    x += p[i] / (z + i);
                }
                const t = z + g + 0.5;
                return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
            }
        }

        // Gamma PDF function
        function gammaPDF(x, alpha, beta) {
            if (x <= 0) return 0;
            const numerator = Math.pow(x, alpha - 1) * Math.exp(-x / beta);
            const denominator = Math.pow(beta, alpha) * gamma(alpha);
            return numerator / denominator;
        }

        // Numerically integrate PDF to get CDF using trapezoidal rule
        function calculateCDF(xValues, pdfValues) {
            const cdf = [];
            let sum = 0;

            for (let i = 1; i < xValues.length; i++) {
                const dx = xValues[i] - xValues[i - 1];
                const avgPdf = (pdfValues[i] + pdfValues[i - 1]) / 2;
                sum += avgPdf * dx;
                cdf.push(sum);
            }

            // Add initial 0 for first point
            cdf.unshift(0);
            return cdf;
        }

        // Generate x values from 0 to 25 with step 0.1
        function generateXValues() {
            const xValues = [];
            for (let x = 0; x <= 25; x += 0.1) {
                xValues.push(x);
            }
            return xValues;
        }

        // Calculate PDF and CDF values for given alpha and beta
        function calculateDistributions(alpha, beta) {
            const xValues = generateXValues();
            const pdfValues = xValues.map(x => gammaPDF(x, alpha, beta));
            const cdfValues = calculateCDF(xValues, pdfValues);

            return { xValues, pdfValues, cdfValues };
        }

        // Update the plot with new data
        function updatePlot(alpha, beta) {
            const { xValues, pdfValues, cdfValues } = calculateDistributions(alpha, beta);

            const mean = alpha * beta;
            const variance = alpha * beta * beta;
            const pdfAtMean = gammaPDF(mean, alpha, beta);

            const data = [
                {
                    x: xValues,
                    y: pdfValues,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2.5 },
                    name: 'PDF'
                },
                {
                    x: xValues,
                    y: cdfValues,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#FF4500', width: 2.5 },
                    name: 'CDF'
                }
            ];

            const layout = {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { t: 0, b: 40, l: 50, r: 20 },
                shapes: [
                    {
                        type: 'line',
                        x0: mean,
                        x1: mean,
                        y0: 0,
                        y1: pdfAtMean,
                        line: { color: 'red', width: 3 }
                    }
                ]
            };

            Plotly.react('plot-container', data, layout);

            return { mean, variance };
        }

        // Update statistics display
        function updateStatistics(alpha, beta, mean, variance) {
            document.getElementById('display-alpha').textContent = alpha.toFixed(3);
            document.getElementById('display-beta').textContent = beta.toFixed(3);
            document.getElementById('mean-value').textContent = mean.toFixed(3);
            document.getElementById('variance-value').textContent = variance.toFixed(3);

            const mgfFormula = document.getElementById('mgf-formula');
            mgfFormula.innerHTML = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${beta.toFixed(3)} t)^{${alpha.toFixed(3)}}} $$`;

            if (window.MathJax) {
                MathJax.typesetPromise([mgfFormula]).catch(err => console.log('MathJax typeset error:', err));
            }
        }

        // Handle slider input
        function handleSliderChange() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            const { mean, variance } = updatePlot(alpha, beta);
            updateStatistics(alpha, beta, mean, variance);
        }

        // Initialize the plot and statistics
        function initialize() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            const { mean, variance } = updatePlot(alpha, beta);
            updateStatistics(alpha, beta, mean, variance);

            // Add event listeners
            document.getElementById('slider-alpha').addEventListener('input', handleSliderChange);
            document.getElementById('slider-beta').addEventListener('input', handleSliderChange);
        }

        // Initialize when DOM is loaded
        document.addEventListener('DOMContentLoaded', initialize);
    </script>
