Here's the complete HTML document implementing the interactive ternary plot visualization as per your requirements:

```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Ternary Plot Visualization</title>
    <style>
        body {
            margin: 0;
            padding: 20px;
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #f5f5f5;
            font-family: Arial, sans-serif;
        }

        #canvas-container {
            border: 1px solid #ccc;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            background-color: white;
        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let sketch = function(p) {
            // Canvas dimensions
            const canvasWidth = 650;
            const canvasHeight = 650;
            const margin = 60;
            const plotWidth = canvasWidth - 2 * margin;
            const plotHeight = canvasHeight - 2 * margin;

            // Azeotropes data
            const azeotropes = {
                A1: { x: 0.655, y: 0, label: "A₁" },
                A2: { x: 0, y: 0.795, label: "A₂" },
                A3: { x: 0.605, y: 0.345, label: "A₃" },
                A4: { x: 0.26, y: 0.335, label: "A₄" }
            };

            // Distillation boundary (polyline connecting A2-A4-A1)
            const boundaryPoints = [
                azeotropes.A2,
                azeotropes.A4,
                azeotropes.A1
            ];

            // Locator state
            let locator = {
                position: { x: 0.2, y: 0.22 },
                radius: 8,
                isDragging: false
            };

            // Convert data coordinates to pixel coordinates
            function dataToPixel(dataX, dataY) {
                const pixelX = margin + dataX * plotWidth;
                const pixelY = canvasHeight - margin - dataY * plotHeight;
                return { x: pixelX, y: pixelY };
            }

            // Convert pixel coordinates to data coordinates
            function pixelToData(pixelX, pixelY) {
                const dataX = (pixelX - margin) / plotWidth;
                const dataY = (canvasHeight - pixelY - margin) / plotHeight;
                return { x: dataX, y: dataY };
            }

            // Get the closest point on the distillation boundary to a given point
            function getClosestBoundaryPoint(point) {
                let closestPoint = { x: 0, y: 0 };
                let minDistance = Infinity;

                // Check all segments of the boundary polyline
                for (let i = 0; i < boundaryPoints.length - 1; i++) {
                    const p1 = boundaryPoints[i];
                    const p2 = boundaryPoints[i + 1];

                    // Find the closest point on this segment
                    const segmentPoint = closestPointOnSegment(point, p1, p2);
                    const distance = distSquared(point, segmentPoint);

                    if (distance < minDistance) {
                        minDistance = distance;
                        closestPoint = segmentPoint;
                    }
                }

                return closestPoint;
            }

            // Calculate the closest point on a line segment (p1-p2) to a point (p)
            function closestPointOnSegment(p, p1, p2) {
                const A = p.x - p1.x;
                const B = p.y - p1.y;
                const C = p2.x - p1.x;
                const D = p2.y - p1.y;

                const dot = A * C + B * D;
                const len_sq = C * C + D * D;
                let param = -1;

                if (len_sq !== 0) {
                    param = dot / len_sq;
                }

                let xx, yy;

                if (param < 0) {
                    xx = p1.x;
                    yy = p1.y;
                } else if (param > 1) {
                    xx = p2.x;
                    yy = p2.y;
                } else {
                    xx = p1.x + param * C;
                    yy = p1.y + param * D;
                }

                return { x: xx, y: yy };
            }

            // Squared distance between two points (for comparison)
            function distSquared(p1, p2) {
                const dx = p1.x - p2.x;
                const dy = p1.y - p2.y;
                return dx * dx + dy * dy;
            }

            // Vector field function
            function getVector(x, y) {
                const A3 = {x: 0.605, y: 0.345}; // Unstable node (repeller)
                const M = {x: 0, y: 0};         // Stable node (attractor)

                // Vector from repeller to current point
                let v_repel_x = x - A3.x;
                let v_repel_y = y - A3.y;
                // Vector from current point to attractor
                let v_attract_x = M.x - x;
                let v_attract_y = M.y - y;

                // Normalize influence by distance squared, add epsilon to avoid division by zero
                let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
                let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

                // Weighted sum of influences
                let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
                let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

                // Add a small rotational field to shape the curves
                dx += -0.1 * y;
                dy += 0.1 * x;

                return { dx, dy };
            }

            // Calculate the distillation line (residue curve)
            function calculateDistillationLine(startX, startY) {
                const stepSize = 0.005;
                const maxSteps = 250;
                const points = [];

                // Forward integration
                let x = startX;
                let y = startY;
                points.push({ x, y });

                for (let i = 0; i < maxSteps; i++) {
                    const { dx, dy } = getVector(x, y);
                    x += dx * stepSize;
                    y += dy * stepSize;

                    // Check if point is still within valid region
                    if (x < 0 || y < 0 || (x + y) > 1) break;

                    points.push({ x, y });
                }

                // Backward integration
                x = startX;
                y = startY;

                for (let i = 0; i < maxSteps; i++) {
                    const { dx, dy } = getVector(x, y);
                    x -= dx * stepSize;
                    y -= dy * stepSize;

                    // Check if point is still within valid region
                    if (x < 0 || y < 0 || (x + y) > 1) break;

                    points.unshift({ x, y });
                }

                return points;
            }

            p.setup = function() {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);

                // X-axis (chloroform)
                p.line(margin, canvasHeight - margin, canvasWidth - margin, canvasHeight - margin);
                // Y-axis (acetone)
                p.line(margin, canvasHeight - margin, margin, margin);
                // Hypotenuse (methanol)
                const p1 = dataToPixel(1, 0);
                const p2 = dataToPixel(0, 1);
                p.line(p1.x, p1.y, p2.x, p2.y);

                // Draw ticks and labels
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);
                p.fill(0);

                for (let i = 0; i <= 1; i += 0.2) {
                    // X-axis ticks
                    const xTick = dataToPixel(i, 0);
                    p.line(xTick.x, xTick.y, xTick.x, xTick.y + 5);
                    p.text(i.toFixed(1), xTick.x, xTick.y + 15);

                    // Y-axis ticks
                    const yTick = dataToPixel(0, i);
                    p.line(yTick.x, yTick.y, yTick.x - 5, yTick.y);
                    p.text(i.toFixed(1), yTick.x - 15, yTick.y);
                }

                // Component labels
                p.textSize(14);
                p.fill(255, 0, 0);

                // Methanol (near origin)
                p.text("methanol", margin + 10, canvasHeight - margin - 10);
                // Chloroform (x-axis)
                p.text("chloroform", canvasWidth - margin - 30, canvasHeight - margin - 10);
                // Acetone (y-axis)
                p.text("acetone", margin + 10, margin + 10);

                // Draw azeotropes
                p.stroke(0);
                p.strokeWeight(1);
                p.fill(153, 0, 0); // Dark red

                for (const key in azeotropes) {
                    const azeotrope = azeotropes[key];
                    const pixelPos = dataToPixel(azeotrope.x, azeotrope.y);

                    // Draw point
                    p.ellipse(pixelPos.x, pixelPos.y, 12, 12);

                    // Draw label
                    p.fill(0);
                    p.text(azeotrope.label, pixelPos.x, pixelPos.y - 15);
                    p.fill(153, 0, 0);
                }

                // Draw distillation boundary
                p.stroke(0);
                p.strokeWeight(1.5);
                p.noFill();

                p.beginShape();
                for (const point of boundaryPoints) {
                    const pixelPos = dataToPixel(point.x, point.y);
                    p.vertex(pixelPos.x, pixelPos.y);
                }
                p.endShape();

                // Calculate and draw distillation line
                const linePoints = calculateDistillationLine(locator.position.x, locator.position.y);

                p.stroke(0, 0, 255); // Blue
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                for (const point of linePoints) {
                    const pixelPos = dataToPixel(point.x, point.y);
                    p.vertex(pixelPos.x, pixelPos.y);
                }
                p.endShape();

                // Draw locator
                const locatorPixelPos = dataToPixel(locator.position.x, locator.position.y);

                p.stroke(0);
                p.strokeWeight(2);
                p.fill(255);
                p.ellipse(locatorPixelPos.x, locatorPixelPos.y, locator.radius * 2, locator.radius * 2);
            };

            p.mousePressed = function() {
                if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;

                const mouseDataPos = pixelToData(p.mouseX, p.mouseY);
                const locatorPixelPos = dataToPixel(locator.position.x, locator.position.y);

                // Check if mouse is within locator's radius
                const distance = p.dist(p.mouseX, p.mouseY, locatorPixelPos.x, locatorPixelPos.y);

                if (distance <= locator.radius) {
                    locator.isDragging = true;
                }
            };

            p.mouseDragged = function() {
                if (locator.isDragging) {
                    const mouseDataPos = pixelToData(p.mouseX, p.mouseY);

                    // Constrain the locator to the distillation boundary
                    const closestPoint = getClosestBoundaryPoint(mouseDataPos);
                    locator.position = closestPoint;

                    p.redraw();
                }
            };

            p.mouseReleased = function() {
                locator.isDragging = false;
            };
