            const wireframeMaterial = new THREE.MeshBasicMaterial({
                color: 0xcccccc,
                wireframe: true,
                transparent: true,
                opacity: 0.3
            });

            paraboloidWireframe = new THREE.Mesh(geometry, wireframeMaterial);
            scene.add(paraboloidWireframe);
        }

