    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.min.js"></script>
    <script>
        // Main application state
        const state = {
            j1: 1.5,
            m1: 0.5,
            j2: 1.0,
            m2: 0.0,
            j: 2.5,
            isAnimating: false,
            animationPhaseZ: 0,
            animationPhaseJ: 0,
            lastTime: 0,
            animationSpeed: 0.5
        };

        // Three.js scene setup
        let scene, camera, renderer, controls;
        let arrowJ, arrowJ1, arrowJ2, coneJ, coneJ1, coneJ2;
        let animationId = null;

        // Initialize the application
        function init() {
            setupThreeJS();
            setupEventListeners();
            updateVisualization();
        }

        // Set up Three.js scene
        function setupThreeJS() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(3, 3, 3);
            camera.up.set(0, 0, 1);

            renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas'), antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.25;

            // Add lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Add coordinate axes
            const axesHelper = new THREE.AxesHelper(2);
            scene.add(axesHelper);

            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        // Set up event listeners
        function setupEventListeners() {
            // Quantum number selectors
            document.getElementById('j1-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.j1 = parseFloat(e.target.value);
                    updateM1Buttons();
                    updateJButtons();
                    resetAnimation();
                    updateVisualization();
                }
            });

            document.getElementById('m1-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.m1 = parseFloat(e.target.value);
                    resetAnimation();
                    updateVisualization();
                }
            });

            document.getElementById('j2-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.j2 = parseFloat(e.target.value);
                    updateM2Buttons();
                    updateJButtons();
                    resetAnimation();
                    updateVisualization();
                }
            });

            document.getElementById('m2-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.m2 = parseFloat(e.target.value);
                    resetAnimation();
                    updateVisualization();
                }
            });

            document.getElementById('j-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.j = parseFloat(e.target.value);
                    resetAnimation();
                    updateVisualization();
                }
            });

            // Animation controls
            document.getElementById('btn-play-pause').addEventListener('click', toggleAnimation);
            document.getElementById('btn-reset').addEventListener('click', resetAnimation);

            // Update selected buttons
            updateSelectedButtons();
        }

        // Update the visualization based on current state
        function updateVisualization() {
            // Clear previous objects
            if (arrowJ) scene.remove(arrowJ);
            if (arrowJ1) scene.remove(arrowJ1);
            if (arrowJ2) scene.remove(arrowJ2);
            if (coneJ) scene.remove(coneJ);
            if (coneJ1) scene.remove(coneJ1);
            if (coneJ2) scene.remove(coneJ2);

            // Calculate vector magnitudes
            const L_J1 = Math.sqrt(state.j1 * (state.j1 + 1));
            const L_J2 = Math.sqrt(state.j2 * (state.j2 + 1));
            const L_J = Math.sqrt(state.j * (state.j + 1));

            // Check if we're in coupled or uncoupled model
            const isCoupled = state.j >= state.j1 && state.j >= state.j2;

            if (isCoupled) {
                // Coupled model: J = J1 + J2
                const m = state.m1 + state.m2;

                // Create J vector (precesses around Z-axis)
                const thetaJ = Math.acos(m / L_J);
                const dirJ = new THREE.Vector3(
                    Math.sin(thetaJ) * Math.cos(state.animationPhaseZ),
                    Math.sin(thetaJ) * Math.sin(state.animationPhaseZ),
                    Math.cos(thetaJ)
                );

                arrowJ = createArrow(dirJ, L_J, 0x000000);
                scene.add(arrowJ);

                // Create J cone (around Z-axis)
                coneJ = createCone(L_J * Math.sin(thetaJ), L_J * Math.cos(thetaJ), 0x000000, 0.2);
                scene.add(coneJ);

                // Calculate angles between vectors using law of cosines
                const theta1 = Math.acos((L_J*L_J + L_J1*L_J1 - L_J2*L_J2) / (2 * L_J * L_J1));
                const theta2 = Math.acos((L_J*L_J + L_J2*L_J2 - L_J1*L_J1) / (2 * L_J * L_J2));

                // Create J1 and J2 vectors (precess around J)
                const dirJ1 = new THREE.Vector3(
                    Math.sin(theta1) * Math.cos(state.animationPhaseJ),
                    Math.sin(theta1) * Math.sin(state.animationPhaseJ),
                    Math.cos(theta1)
                );

                // Rotate J1 and J2 to align with J's direction
                const rotation = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0, 0, 1), dirJ.clone().normalize());
                dirJ1.applyQuaternion(rotation);

                arrowJ1 = createArrow(dirJ1, L_J1, 0xff0000);
                scene.add(arrowJ1);

                // Create J1 cone (around J)
                coneJ1 = createCone(L_J1 * Math.sin(theta1), L_J1 * Math.cos(theta1), 0xff0000, 0.2);
                coneJ1.position.copy(dirJ.clone().setLength(L_J * Math.cos(theta1)));
                coneJ1.quaternion.setFromUnitVectors(new THREE.Vector3(0, 0, 1), dirJ.clone().normalize());
                scene.add(coneJ1);

                // J2 vector is J - J1
                const dirJ2 = dirJ.clone().multiplyScalar(L_J).sub(dirJ1.clone().multiplyScalar(L_J1)).normalize();
                arrowJ2 = createArrow(dirJ2, L_J2, 0x0000ff);
                scene.add(arrowJ2);

                // Create J2 cone (around J)
                coneJ2 = createCone(L_J2 * Math.sin(theta2), L_J2 * Math.cos(theta2), 0x0000ff, 0.2);
                coneJ2.position.copy(dirJ.clone().setLength(L_J * Math.cos(theta2)));
                coneJ2.quaternion.setFromUnitVectors(new THREE.Vector3(0, 0, 1), dirJ.clone().normalize());
                scene.add(coneJ2);
            } else {
                // Uncoupled model: J1 and J2 precess independently around Z
                const theta1 = Math.acos(state.m1 / L_J1);
                const theta2 = Math.acos(state.m2 / L_J2);

                // Create J1 vector and cone
                const dirJ1 = new THREE.Vector3(
                    Math.sin(theta1) * Math.cos(state.animationPhaseZ),
                    Math.sin(theta1) * Math.sin(state.animationPhaseZ),
                    Math.cos(theta1)
                );
                arrowJ1 = createArrow(dirJ1, L_J1, 0xff0000);
                scene.add(arrowJ1);

                coneJ1 = createCone(L_J1 * Math.sin(theta1), L_J1 * Math.cos(theta1), 0xff0000, 0.2);
                scene.add(coneJ1);

                // Create J2 vector and cone
                const dirJ2 = new THREE.Vector3(
                    Math.sin(theta2) * Math.cos(state.animationPhaseZ + Math.PI), // Offset phase for visibility
                    Math.sin(theta2) * Math.sin(state.animationPhaseZ + Math.PI),
                    Math.cos(theta2)
                );
                arrowJ2 = createArrow(dirJ2, L_J2, 0x0000ff);
                scene.add(arrowJ2);

                coneJ2 = createCone(L_J2 * Math.sin(theta2), L_J2 * Math.cos(theta2), 0x0000ff, 0.2);
                scene.add(coneJ2);
            }

            // Update selected buttons
            updateSelectedButtons();
        }

        // Helper function to create an arrow
        function createArrow(direction, length, color) {
            const origin = new THREE.Vector3(0, 0, 0);
            const normalizedDirection = direction.clone().normalize();
            const arrowHelper = new THREE.ArrowHelper(
                normalizedDirection,
                origin,
                length,
                color,
                0.2 * length,
                0.1 * length
            );
            return arrowHelper;
        }

        // Helper function to create a cone
        function createCone(radius, height, color, opacity) {
            const coneGeometry = new THREE.ConeGeometry(radius, height, 32, 1, true);
            const coneMaterial = new THREE.MeshPhongMaterial({
                color: color,
                transparent: true,
                opacity: opacity,
                side: THREE.DoubleSide
            });
            const cone = new THREE.Mesh(coneGeometry, coneMaterial);
            cone.position.z = height / 2;
            return cone;
        }

        // Update m1 buttons based on j1
        function updateM1Buttons() {
            const m1Selector = document.getElementById('m1-selector');
            m1Selector.innerHTML = '';

            const j = state.j1;
            for (let m = -j; m <= j; m += 1) {
                const button = document.createElement('button');
                button.value = m;
                button.textContent = m % 1 === 0 ? m.toString() : `${m.toFixed(1)}`;
                m1Selector.appendChild(button);
            }

            // Select middle value (or closest to 0)
            const defaultM = Math.max(-j, Math.min(j, 0.5));
            state.m1 = defaultM;
        }

        // Update m2 buttons based on j2
        function updateM2Buttons() {
            const m2Selector = document.getElementById('m2-selector');
            m2Selector.innerHTML = '';

            const j = state.j2;
            for (let m = -j; m <= j; m += 1) {
                const button = document.createElement('button');
                button.value = m;
                button.textContent = m % 1 === 0 ? m.toString() : `${m.toFixed(1)}`;
                m2Selector.appendChild(button);
            }

            // Select middle value (or closest to 0)
            const defaultM = Math.max(-j, Math.min(j, 0));
            state.m2 = defaultM;
        }

        // Update j buttons based on j1 and j2
        function updateJButtons() {
            const jSelector = document.getElementById('j-selector');
            jSelector.innerHTML = '';

            const j1 = state.j1;
            const j2 = state.j2;
            const minJ = Math.abs(j1 - j2);
            const maxJ = j1 + j2;

            for (let j = minJ; j <= maxJ; j += 1) {
                const button = document.createElement('button');
                button.value = j;
                button.textContent = j % 1 === 0 ? j.toString() : `${j.toFixed(1)}`;
                jSelector.appendChild(button);
            }

            // Select largest possible j
            state.j = maxJ;
        }

        // Update selected buttons in UI
        function updateSelectedButtons() {
            // Clear all selected classes
            document.querySelectorAll('.selector-buttons button').forEach(button => {
                button.classList.remove('selected');
            });

            // Set selected classes based on current state
            setSelectedButton('j1-selector', state.j1.toString());
            setSelectedButton('m1-selector', state.m1.toString());
            setSelectedButton('j2-selector', state.j2.toString());
            setSelectedButton('m2-selector', state.m2.toString());
            setSelectedButton('j-selector', state.j.toString());

            // Update play/pause button
            const playPauseBtn = document.getElementById('btn-play-pause');
            playPauseBtn.textContent = state.isAnimating ? '❚❚' : '▶';
        }

        function setSelectedButton(selectorId, value) {
            const buttons = document.querySelectorAll(`#${selectorId} button`);
            buttons.forEach(button => {
                if (button.value === value) {
                    button.classList.add('selected');
                }
            });
        }

        // Animation control functions
        function toggleAnimation() {
            state.isAnimating = !state.isAnimating;
            updateSelectedButtons();

            if (state.isAnimating) {
                if (!animationId) {
                    animate();
                }
            } else {
                if (animationId) {
                    cancelAnimationFrame(animationId);
                    animationId = null;
                }
            }
        }

        function resetAnimation() {
            state.isAnimating = false;
            state.animationPhaseZ = 0;
            state.animationPhaseJ = 0;
            updateSelectedButtons();
            updateVisualization();

            if (animationId) {
                cancelAnimationFrame(animationId);
                animationId = null;
            }
        }

        // Animation loop
        function animate(time = 0) {
            animationId = requestAnimationFrame(animate);

            if (state.isAnimating) {
                const deltaTime = (time - state.lastTime) / 1000;
                state.lastTime = time;

                state.animationPhaseZ += deltaTime * state.animationSpeed;
                state.animationPhaseJ += deltaTime * state.animationSpeed * 2; // Faster precession around J

                updateVisualization();
            }

            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize the application when the page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
