    <script>
        // Constants
        const GRID_SIZE = 10;
        const CELL_SIZE = 20;
        const CANVAS_SIZE = GRID_SIZE * CELL_SIZE + 1;

        const EMPTY = 0;
        const OBSTACLE = 1;
        const FILLED = 2;

        // Global state
        let grid = [];
        let recursiveHistory = [];
        let iterativeHistory = [];
        let recursiveSketch;
        let iterativeSketch;
        let currentPattern = 'scattered';

        // Initialize the grid with a specific pattern
        function initGrid(pattern) {
            grid = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(EMPTY));

            // Set random seed for consistent scattered pattern
            Math.seedrandom('floodfill');

            switch(pattern) {
                case 'none':
                    // No obstacles
                    break;

                case 'box':
                    // Border obstacles
                    for (let i = 0; i < GRID_SIZE; i++) {
                        grid[0][i] = OBSTACLE;
                        grid[GRID_SIZE-1][i] = OBSTACLE;
                        grid[i][0] = OBSTACLE;
                        grid[i][GRID_SIZE-1] = OBSTACLE;
                    }
                    break;

                case 'scattered':
                    // Random obstacles (25% probability)
                    for (let i = 0; i < GRID_SIZE; i++) {
                        for (let j = 0; j < GRID_SIZE; j++) {
                            if (Math.random() < 0.25 && !(i === 4 && j === 4)) {
                                grid[i][j] = OBSTACLE;
                            }
                        }
                    }
                    break;
            }

            // Reset histories
            recursiveHistory = [];
            iterativeHistory = [];

            // Run algorithms
            runRecursiveAlgorithm();
            runIterativeAlgorithm();

            // Update slider max
            const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            document.getElementById('steps-slider').max = maxSteps;

            // Update displays
            updateSliderValue();
            drawAllGrids();
        }

        // Recursive flood fill algorithm (DFS)
        function runRecursiveAlgorithm() {
            // Create a copy of the grid for the algorithm
            const algoGrid = grid.map(row => [...row]);
            recursiveHistory = [];

            function floodFill(x, y) {
                // Check boundaries
                if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;

                // Check if cell is obstacle or already filled
                if (algoGrid[x][y] !== EMPTY) return;

                // Fill the cell
                algoGrid[x][y] = FILLED;
                recursiveHistory.push({x, y});

                // Recursively fill neighbors (N, W, S, E order)
                floodFill(x, y-1); // North
                floodFill(x-1, y); // West
                floodFill(x, y+1); // South
                floodFill(x+1, y); // East
            }

            // Start from center
            floodFill(4, 4);
        }

        // Iterative flood fill algorithm (BFS)
        function runIterativeAlgorithm() {
            // Create a copy of the grid for the algorithm
            const algoGrid = grid.map(row => [...row]);
            iterativeHistory = [];

            const queue = [{x: 4, y: 4}];

            while (queue.length > 0) {
                const {x, y} = queue.shift();

                // Check boundaries
                if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) continue;

                // Check if cell is obstacle or already filled
                if (algoGrid[x][y] !== EMPTY) continue;

                // Fill the cell
                algoGrid[x][y] = FILLED;
                iterativeHistory.push({x, y});

                // Enqueue neighbors
                queue.push({x: x, y: y-1}); // North
                queue.push({x: x-1, y: y}); // West
                queue.push({x: x, y: y+1}); // South
                queue.push({x: x+1, y: y}); // East
            }
        }

        // Update the steps value display
        function updateSliderValue() {
            const slider = document.getElementById('steps-slider');
            const valueDisplay = document.getElementById('steps-value');
            valueDisplay.textContent = `+ ${slider.value}`;
        }

        // Draw all grids based on current step
        function drawAllGrids() {
            const step = parseInt(document.getElementById('steps-slider').value);

            if (recursiveSketch) {
                recursiveSketch.drawGrid(step);
            }

            if (iterativeSketch) {
                iterativeSketch.drawGrid(step);
            }
        }

        // Setup p5.js sketches
        function setupSketches() {
            // Recursive sketch
            recursiveSketch = new p5(function(sketch) {
                sketch.setup = function() {
                    const canvas = sketch.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                    canvas.parent('canvas-recursive');
                    sketch.noLoop();
                };

                sketch.drawGrid = function(step) {
                    sketch.background(255);

                    // Draw grid lines
                    sketch.stroke(0);
                    sketch.strokeWeight(1);

                    for (let i = 0; i <= GRID_SIZE; i++) {
                        // Vertical lines
                        sketch.line(i * CELL_SIZE, 0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE);
                        // Horizontal lines
                        sketch.line(0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE, i * CELL_SIZE);
                    }

                    // Draw obstacles
                    sketch.fill(0);
                    for (let i = 0; i < GRID_SIZE; i++) {
                        for (let j = 0; j < GRID_SIZE; j++) {
                            if (grid[i][j] === OBSTACLE) {
                                sketch.rect(i * CELL_SIZE + 1, j * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                            }
                        }
                    }

                    // Draw filled cells up to current step
                    sketch.fill(0, 0, 255);
                    for (let s = 0; s < step && s < recursiveHistory.length; s++) {
                        const {x, y} = recursiveHistory[s];
                        sketch.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                    }
                };
            });

            // Iterative sketch
            iterativeSketch = new p5(function(sketch) {
                sketch.setup = function() {
                    const canvas = sketch.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                    canvas.parent('canvas-iterative');
                    sketch.noLoop();
                };

                sketch.drawGrid = function(step) {
                    sketch.background(255);

                    // Draw grid lines
                    sketch.stroke(0);
                    sketch.strokeWeight(1);

                    for (let i = 0; i <= GRID_SIZE; i++) {
                        // Vertical lines
                        sketch.line(i * CELL_SIZE, 0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE);
                        // Horizontal lines
                        sketch.line(0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE, i * CELL_SIZE);
                    }

                    // Draw obstacles
                    sketch.fill(0);
                    for (let i = 0; i < GRID_SIZE; i++) {
                        for (let j = 0; j < GRID_SIZE; j++) {
                            if (grid[i][j] === OBSTACLE) {
                                sketch.rect(i * CELL_SIZE + 1, j * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                            }
                        }
                    }

                    // Draw filled cells up to current step
                    sketch.fill(0, 0, 255);
                    for (let s = 0; s < step && s < iterativeHistory.length; s++) {
                        const {x, y} = iterativeHistory[s];
                        sketch.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                    }
                };
            });
        }

        // Event listeners
        function setupEventListeners() {
            // Slider
            document.getElementById('steps-slider').addEventListener('input', function() {
                updateSliderValue();
                drawAllGrids();
            });

            // Pattern buttons
            document.getElementById('btn-none').addEventListener('click', function() {
                document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                currentPattern = 'none';
                initGrid(currentPattern);
            });

            document.getElementById('btn-box').addEventListener('click', function() {
                document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                currentPattern = 'box';
                initGrid(currentPattern);
            });

            document.getElementById('btn-scattered').addEventListener('click', function() {
                document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                currentPattern = 'scattered';
                initGrid(currentPattern);
            });
        }

        // Initialize everything when the page loads
        window.addEventListener('DOMContentLoaded', function() {
            setupSketches();
            setupEventListeners();
            initGrid(currentPattern);
        });

        // Simple seedable random number generator for consistent scattered pattern
        Math.seedrandom = function(seed) {
            let x = 0;
            for (let i = 0; i < seed.length; i++) {
                x = x * 32 + seed.charCodeAt(i) | 0;
            }

            return function() {
                x = Math.imul(48271, x) | 0;
                return (x & 0x7fffffff) / 2147483647;
            };
        }(Math.seedrandom || '');
    </script>
