</head>
<body>
    <div id="control-panel">
        <div>
            <label for="slider-k">k</label>
            <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
            <span id="k-value">0.9</span>
        </div>
        <div>
            <label for="slider-time">time (s)</label>
            <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
            <span id="time-value">15.0</span>
        </div>
        <div>
            <label for="slider-vx">initial velocity in x (m/s)</label>
            <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
            <span id="vx-value">2.5</span>
        </div>
        <div>
            <label for="slider-dt">Δt (s)</label>
            <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
            <span id="dt-value">0.15</span>
        </div>
    </div>
    <div id="canvas-container"></div>

    <script>
        let canvas;
        let padding = 40;
        let xScale, yScale;

        function setup() {
            canvas = createCanvas(700, 400);
            canvas.parent('canvas-container');
            noLoop();

            // Add event listeners to all sliders
            document.getElementById('slider-k').addEventListener('input', function() {
                document.getElementById('k-value').textContent = this.value;
                redraw();
            });
            document.getElementById('slider-time').addEventListener('input', function() {
                document.getElementById('time-value').textContent = this.value;
                redraw();
            });
            document.getElementById('slider-vx').addEventListener('input', function() {
                document.getElementById('vx-value').textContent = this.value;
                redraw();
            });
            document.getElementById('slider-dt').addEventListener('input', function() {
                document.getElementById('dt-value').textContent = this.value;
                redraw();
            });
        }

        function draw() {
            // Get current slider values
            const k = parseFloat(document.getElementById('slider-k').value);
            const totalTime = parseFloat(document.getElementById('slider-time').value);
            const vx = parseFloat(document.getElementById('slider-vx').value);
            const dt = parseFloat(document.getElementById('slider-dt').value);

            // Physics constants
            const g = 9.8; // m/s²
            const y0 = 10;  // m
            const vy0 = 0;  // m/s
            const x0 = 0;   // m

            // Simulation variables
            let currentTime = 0;
            let currentX = x0;
            let currentY = y0;
            let currentVy = vy0;

            // Arrays to store points
            let pathPoints = [];
            let strobePoints = [];
            let lastStrobeTime = 0;

            // Calculate trajectory
            while (currentTime < totalTime) {
                // Calculate time until next bounce (time of flight)
                const vy_impact = sqrt(currentVy * currentVy + 2 * g * currentY);
                const t_flight = (currentVy + vy_impact) / g;

                // If this bounce would exceed total time, adjust t_flight
                const actual_t_flight = min(t_flight, totalTime - currentTime);

                // Generate points for this parabolic arc
                for (let t = 0; t <= actual_t_flight; t += 0.01) {
                    const x = currentX + vx * t;
                    const y = currentY + currentVy * t - 0.5 * g * t * t;
                    pathPoints.push({x, y});
                }

                // Generate strobe points for this arc
                let strobeTime = lastStrobeTime;
                while (strobeTime < currentTime + actual_t_flight) {
                    if (strobeTime >= currentTime) {
                        const t = strobeTime - currentTime;
                        const x = currentX + vx * t;
                        const y = currentY + currentVy * t - 0.5 * g * t * t;
                        if (y >= 0) { // Only add if ball is above ground
                            strobePoints.push({x, y});
                        }
                    }
                    strobeTime += dt;
                }
                lastStrobeTime = strobeTime;

                // Update state for next bounce
                currentTime += actual_t_flight;
                currentX += vx * actual_t_flight;
                currentVy = sqrt(k) * (currentVy - g * actual_t_flight);
                currentY = 0;
            }

            // Determine scaling factors to fit the trajectory in the canvas
            const maxX = max(pathPoints.map(p => p.x).concat(strobePoints.map(p => p.x)));
            const maxY = max(pathPoints.map(p => p.y).concat(strobePoints.map(p => p.y)));

            const drawWidth = width - 2 * padding;
            const drawHeight = height - 2 * padding;

            xScale = drawWidth / maxX;
            yScale = drawHeight / maxY;

            // Clear canvas
            background(0);

            // Draw axes
            drawAxes(maxX, maxY);

            // Draw trajectory
            drawTrajectory(pathPoints);

            // Draw strobe points
            drawStrobePoints(strobePoints);
        }

        function drawAxes(maxX, maxY) {
            push();
            stroke(100, 100, 150);
            strokeWeight(1);

            // X axis
            line(padding, height - padding, width - padding, height - padding);

            // Y axis
            line(padding, height - padding, padding, padding);

            // X ticks
            const xStep = maxX > 20 ? 5 : maxX > 10 ? 2 : 1;
            for (let x = 0; x <= maxX; x += xStep) {
                const screenX = padding + x * xScale;
                line(screenX, height - padding - 5, screenX, height - padding + 5);
                textAlign(CENTER, TOP);
                noStroke();
                fill(200);
                text(x.toFixed(1), screenX, height - padding + 10);
            }

            // Y ticks
            const yStep = maxY > 20 ? 5 : maxY > 10 ? 2 : 1;
            for (let y = 0; y <= maxY; y += yStep) {
                const screenY = height - padding - y * yScale;
                line(padding - 5, screenY, padding + 5, screenY);
                textAlign(RIGHT, CENTER);
                noStroke();
                fill(200);
                text(y.toFixed(1), padding - 10, screenY);
            }

            // Labels
            textAlign(CENTER, CENTER);
            text("x (m)", width / 2, height - padding / 2);
            push();
            translate(padding / 2, height / 2);
            rotate(-HALF_PI);
            text("y (m)", 0, 0);
            pop();

            pop();
        }

        function drawTrajectory(points) {
            push();
            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();

            beginShape();
            for (const p of points) {
                vertex(padding + p.x * xScale, height - padding - p.y * yScale);
            }
            endShape();

            pop();
        }

        function drawStrobePoints(points) {
            push();
            noStroke();
            fill(255, 255, 0);

            for (const p of points) {
                ellipse(
                    padding + p.x * xScale,
                    height - padding - p.y * yScale,
                    8, 8
                );
            }

            pop();
        }
    </script>
</body>
</html>
