Here's the complete HTML document that implements the specified geometric visualization using p5.js:

```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Angle Bisectors on the Circumcircle</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
            background-color: white;
        }
        h1 {
            color: #333;
            margin-bottom: 10px;
        }
        p {
            color: #555;
            max-width: 800px;
            text-align: center;
            margin-bottom: 20px;
        }
        #canvas-container {
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 20px 0;
        }
    </style>
</head>
<body>
    <header>
        <h1>Angle Bisectors on the Circumcircle</h1>
        <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    </header>

    <div id="canvas-container">
        <!-- p5.js canvas will be inserted here -->
    </div>

    <script>
        let sketch = function(p) {
            // State variables
            let centerX, centerY;
            let radius = 200;
            let points = {
                A: { angle: p.radians(210), label: "A", isDragging: false },
                B: { angle: p.radians(330), label: "B", isDragging: false },
                C: { angle: p.radians(90), label: "C", isDragging: false }
            };
            let derivedPoints = {
                Aprime: { angle: 0, label: "A'" },
                Bprime: { angle: 0, label: "B'" },
                Cprime: { angle: 0, label: "C'" }
            };
            let activePoint = null;

            p.setup = function() {
                let canvas = p.createCanvas(500, 500);
                canvas.parent('canvas-container');
                centerX = p.width / 2;
                centerY = p.height / 2;
                p.background(240);

                // Calculate initial derived points
                updateDerivedPoints();
            };

            p.draw = function() {
                p.background(240);

                // Draw circumcircle
                p.stroke(106, 90, 205);
                p.strokeWeight(4);
                p.noFill();
                p.circle(centerX, centerY, radius * 2);

                // Calculate Cartesian coordinates for all points
                updateCartesianCoordinates();

                // Draw triangle ABC
                p.stroke(106, 90, 205);
                p.strokeWeight(4);
                p.line(points.A.x, points.A.y, points.B.x, points.B.y);
                p.line(points.B.x, points.B.y, points.C.x, points.C.y);
                p.line(points.C.x, points.C.y, points.A.x, points.A.y);

                // Draw extended bisectors and triangle A'B'C'
                p.stroke(255, 165, 0);
                p.strokeWeight(4);
                p.line(points.A.x, points.A.y, derivedPoints.Aprime.x, derivedPoints.Aprime.y);
                p.line(points.B.x, points.B.y, derivedPoints.Bprime.x, derivedPoints.Bprime.y);
                p.line(points.C.x, points.C.y, derivedPoints.Cprime.x, derivedPoints.Cprime.y);

                p.line(derivedPoints.Aprime.x, derivedPoints.Aprime.y, derivedPoints.Bprime.x, derivedPoints.Bprime.y);
                p.line(derivedPoints.Bprime.x, derivedPoints.Bprime.y, derivedPoints.Cprime.x, derivedPoints.Cprime.y);
                p.line(derivedPoints.Cprime.x, derivedPoints.Cprime.y, derivedPoints.Aprime.x, derivedPoints.Aprime.y);

                // Draw all points
                drawPoint(points.A);
                drawPoint(points.B);
                drawPoint(points.C);
                drawPoint(derivedPoints.Aprime);
                drawPoint(derivedPoints.Bprime);
                drawPoint(derivedPoints.Cprime);

                // Draw labels
                drawLabel(points.A);
                drawLabel(points.B);
                drawLabel(points.C);
                drawLabel(derivedPoints.Aprime);
                drawLabel(derivedPoints.Bprime);
                drawLabel(derivedPoints.Cprime);
            };

            function updateDerivedPoints() {
                // Calculate A' as midpoint of arc BC not containing A
                derivedPoints.Aprime.angle = p.atan2(
                    p.sin(points.B.angle) + p.sin(points.C.angle),
                    p.cos(points.B.angle) + p.cos(points.C.angle)
                );

                // Calculate B' as midpoint of arc AC not containing B
                derivedPoints.Bprime.angle = p.atan2(
                    p.sin(points.A.angle) + p.sin(points.C.angle),
                    p.cos(points.A.angle) + p.cos(points.C.angle)
                );

                // Calculate C' as midpoint of arc AB not containing C
                derivedPoints.Cprime.angle = p.atan2(
                    p.sin(points.A.angle) + p.sin(points.B.angle),
                    p.cos(points.A.angle) + p.cos(points.B.angle)
                );
            }

            function updateCartesianCoordinates() {
                // Update Cartesian coordinates for all points
                for (let key in points) {
                    points[key].x = centerX + radius * p.cos(points[key].angle);
                    points[key].y = centerY + radius * p.sin(points[key].angle);
                }

                for (let key in derivedPoints) {
                    derivedPoints[key].x = centerX + radius * p.cos(derivedPoints[key].angle);
                    derivedPoints[key].y = centerY + radius * p.sin(derivedPoints[key].angle);
                }
            }

            function drawPoint(point) {
                // Draw point circle
                p.fill(255, 165, 0);
                p.stroke(0);
                p.strokeWeight(1);
                p.circle(point.x, point.y, 12);

                // Draw crosshair for interactive points
                if (point.label.length === 1) {
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.line(point.x - 4, point.y, point.x + 4, point.y);
                    p.line(point.x, point.y - 4, point.x, point.y + 4);
                }
            }

            function drawLabel(point) {
                p.textSize(16);
                p.fill(0);
                p.noStroke();

                // Calculate offset direction (radially outward)
                let offsetX = 15 * p.cos(point.angle);
                let offsetY = 15 * p.sin(point.angle);

                p.text(point.label, point.x + offsetX, point.y + offsetY);
            }

            p.mousePressed = function() {
                // Check if mouse is near any interactive point
                for (let key in points) {
                    let point = points[key];
                    let d = p.dist(p.mouseX, p.mouseY, point.x, point.y);
                    if (d < 15) {
                        activePoint = key;
                        points[key].isDragging = true;
                        return;
                    }
                }
            };

            p.mouseDragged = function() {
                if (activePoint) {
                    // Calculate angle from center to mouse position
                    let newAngle = p.atan2(p.mouseY - centerY, p.mouseX - centerX);
                    points[activePoint].angle = newAngle;
                    updateDerivedPoints();
                }
            };

            p.mouseReleased = function() {
                if (activePoint) {
                    points[activePoint].isDragging = false;
                    activePoint = null;
                }
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
