```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Carbon Cycle Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
            display: flex;
            justify-content: center;
            background-color: #f5f5f5;
        }

        #container {
            display: flex;
            flex-direction: column;
            align-items: center;
            max-width: 800px;
        }

        #controls {
            background-color: #f0f0f0;
            padding: 15px;
            border: 1px solid #ddd;
            border-radius: 5px;
            margin-bottom: 20px;
            width: 100%;
            box-sizing: border-box;
        }

        #controls label {
            display: block;
            margin-bottom: 10px;
            font-weight: bold;
        }

        #controls input[type="range"] {
            width: 100%;
        }

        #canvas-container {
            border: 1px solid #ddd;
            border-radius: 5px;
            overflow: hidden;
            background-color: white;
        }

        .tooltip {
            position: absolute;
            background-color: rgba(0, 0, 0, 0.7);
            color: white;
            padding: 8px;
            border-radius: 4px;
            font-size: 14px;
            pointer-events: none;
            max-width: 200px;
            z-index: 100;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="controls">
            <label for="slider-steps">carbon cycle steps</label>
            <input type="range" id="slider-steps" min="0" max="7" step="1" value="0">
        </div>
        <div id="canvas-container"></div>
    </div>
    <div id="tooltip" class="tooltip" style="display: none;"></div>

    <script>
        let slider;
        let canvas;
        let tooltip;
        let hoveredProcess = null;
        const processDescriptions = {
            "Photosynthesis": "Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.",
            "Cellular Respiration": "Animals and plants break down sugars for energy, releasing carbon dioxide.",
            "Organisms' Waste and Decay": "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.",
            "Fossils and Fossil Fuels": "Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.",
            "Fossil Fuel Extraction": "Humans extract fossil fuels from the ground for energy.",
            "Fossil Fuel Emissions": "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.",
            "CO2 Diffusion": "Carbon dioxide dissolves into and is released from bodies of water like oceans."
        };

        function setup() {
            canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            slider = document.getElementById('slider-steps');
            tooltip = document.getElementById('tooltip');

            // Set canvas to be responsive
            canvas.elt.style.width = '100%';
            canvas.elt.style.height = 'auto';
        }

        function draw() {
            background(255);
            const sliderValue = parseInt(slider.value);
            hoveredProcess = null;

            // Draw static scene
            drawStaticScene();

            // Draw processes based on slider value
            if (sliderValue >= 1) drawCellularRespiration();
            if (sliderValue >= 2) drawWasteAndDecay();
            if (sliderValue >= 3) drawFossilsAndFuels();
            if (sliderValue >= 4) drawPhotosynthesis();
            if (sliderValue >= 5) drawFossilFuelEmissions();
            if (sliderValue >= 6) drawFossilFuelExtraction();
            if (sliderValue >= 7) drawCO2Diffusion();

            // Show tooltip if hovering over a process
            if (hoveredProcess) {
                tooltip.style.display = 'block';
                tooltip.style.left = (mouseX + 15) + 'px';
                tooltip.style.top = (mouseY + 15) + 'px';
                tooltip.textContent = processDescriptions[hoveredProcess];
            } else {
                tooltip.style.display = 'none';
            }
        }

        function drawStaticScene() {
            // Sky
            fill(65, 105, 225);
            rect(0, 0, width, height * 0.6);

            // Sun
            fill(255, 215, 0);
            noStroke();
            ellipse(width - 50, 50, 60, 60);

            // Cloud
            fill(255);
            ellipse(width * 0.5, 60, 60, 40);
            ellipse(width * 0.5 + 30, 60, 60, 40);
            ellipse(width * 0.5 + 15, 40, 60, 40);

            // Grass
            fill(34, 139, 34);
            rect(0, height * 0.6, width, height * 0.2);

            // Soil
            fill(139, 69, 19);
            rect(0, height * 0.8, width, height * 0.2);

            // Water
            fill(0, 0, 205);
            beginShape();
            vertex(width * 0.7, height * 0.6);
            vertex(width, height * 0.6);
            vertex(width, height);
            vertex(width * 0.6, height);
            endShape(CLOSE);

            // Trees
            drawTree(150, height * 0.5, 30, 80);
            drawTree(300, height * 0.5, 30, 80);

            // Sheep
            drawSheep(200, height * 0.7);

            // Factory
            drawFactory(450, height * 0.5);

            // Fossil deposits
            fill(0);
            ellipse(220, height * 0.9, 20, 10);
            ellipse(450, height * 0.9, 20, 10);
            ellipse(480, height * 0.85, 15, 8);
        }

        function drawTree(x, y, trunkWidth, trunkHeight) {
            // Trunk
            fill(101, 67, 33);
            rect(x - trunkWidth/2, y, trunkWidth, trunkHeight);

            // Foliage
            fill(0, 100, 0);
            ellipse(x, y - 20, trunkWidth * 3, trunkHeight);
        }

        function drawSheep(x, y) {
            // Body
            fill(255);
            ellipse(x, y, 60, 40);

            // Head
            ellipse(x + 30, y - 10, 25, 20);

            // Eye
            fill(0);
            ellipse(x + 35, y - 12, 3, 3);

            // Wool texture
            fill(240);
            ellipse(x - 5, y - 5, 15, 15);
            ellipse(x + 5, y + 5, 15, 15);
            ellipse(x - 10, y + 10, 15, 15);
        }

        function drawFactory(x, y) {
            // Building
            fill(105, 105, 105);
            rect(x, y, 100, 150);

            // Windows
            fill(200);
            for (let i = 0; i < 3; i++) {
                for (let j = 0; j < 4; j++) {
                    rect(x + 15 + j * 20, y + 20 + i * 30, 15, 15);
                }
            }

            // Smokestack
            fill(169, 169, 169);
            rect(x + 70, y - 30, 20, 30);

            // Smoke
            fill(220);
            ellipse(x + 80, y - 40, 30, 20);
            ellipse(x + 90, y - 60, 40, 20);
        }

        function drawArrow(x1, y1, x2, y2) {
            stroke(0);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Arrow head
            let angle = atan2(y2 - y1, x2 - x1);
            push();
            translate(x2, y2);
            rotate(angle);
            fill(0);
            noStroke();
            triangle(0, 0, -15, -5, -15, 5);
            pop();
        }

        function checkHover(label, x, y, w, h) {
