                <label for="radio-acceleration-off">off</label>
                <input type="radio" id="radio-acceleration-on" name="acceleration" value="on" checked>
                <label for="radio-acceleration-on">on</label>
            </div>

            <!-- Coordinate System Selector -->
            <label for="select-coords">coordinate system</label>
            <select id="select-coords">
                <option value="rectangular">rectangular</option>
                <option value="normal-tangential">normal-tangential</option>
                <option value="polar">polar</option>
            </select>

            <!-- Instantaneous Circle Toggle -->
            <label>instantaneous circle</label>
            <div id="toggle-circle" class="toggle-switch">
                <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
                <label for="radio-circle-off">off</label>
                <input type="radio" id="radio-circle-on" name="circle" value="on">
                <label for="radio-circle-on">on</label>
            </div>
        </div>

        <!-- Right: Visualization Canvas -->
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // Global state
        const state = {
            t: 0.4,
            showPosition: false,
            showVelocity: false,
            showAcceleration: true,
            coordSystem: 'rectangular',
            showCircle: false,
            scale: 100, // pixels per unit
            origin: { x: 0, y: 0 }, // Will be set in setup
            canvasSize: { width: 0, height: 0 } // Will be set in setup
        };

        // DOM elements
        const sliderTime = document.getElementById('slider-time');
        const btnResetTime = document.getElementById('btn-reset-time');
        const radioPositionOn = document.getElementById('radio-position-on');
        const radioVelocityOn = document.getElementById('radio-velocity-on');
        const radioAccelerationOn = document.getElementById('radio-acceleration-on');
        const selectCoords = document.getElementById('select-coords');
        const radioCircleOn = document.getElementById('radio-circle-on');

        // Event listeners
        sliderTime.addEventListener('input', () => {
            state.t = parseFloat(sliderTime.value);
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        btnResetTime.addEventListener('click', () => {
            sliderTime.value = '0.4';
            state.t = 0.4;
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        radioPositionOn.addEventListener('change', () => {
            state.showPosition = radioPositionOn.checked;
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        radioVelocityOn.addEventListener('change', () => {
            state.showVelocity = radioVelocityOn.checked;
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        radioAccelerationOn.addEventListener('change', () => {
            state.showAcceleration = radioAccelerationOn.checked;
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        selectCoords.addEventListener('change', () => {
            state.coordSystem = selectCoords.value;
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        radioCircleOn.addEventListener('change', () => {
            state.showCircle = radioCircleOn.checked;
            if (window.myP5) {
                window.myP5.redraw();
            }
        });

        // p5.js sketch
        function sketch(p) {
            window.myP5 = p;

            p.setup = function() {
                const canvasDiv = document.getElementById('canvas-container');
                const width = canvasDiv.clientWidth;
                const height = canvasDiv.clientHeight;
                state.canvasSize = { width, height };
                state.origin = { x: width / 2, y: height / 2 };

                const canvas = p.createCanvas(width, height);
                canvas.parent('canvas-container');
                p.noLoop(); // We'll manually redraw when needed
            };

            p.draw = function() {
                p.background(255);
                drawCoordinateSystem();
                drawLemniscate();
                drawPoint();

                // Calculate kinematic properties
                const pos = calculatePosition(state.t);
                const vel = calculateVelocity(state.t);
                const acc = calculateAcceleration(state.t);

                // Draw vectors based on settings
                if (state.showPosition) {
                    drawVector(pos, 'position', 0, 0, '#0000FF');
                }

                if (state.showVelocity) {
                    drawVector(vel, 'velocity', pos.x, pos.y, '#8B0000');
                }

                if (state.showAcceleration) {
                    drawVector(acc, 'acceleration', pos.x, pos.y, '#800080');
                }

                // Draw basis vectors based on coordinate system
                drawBasisVectors(pos, vel, acc);

                // Draw instantaneous circle if enabled
                if (state.showCircle) {
                    drawInstantaneousCircle(pos, vel, acc);
                }
            };

            function drawCoordinateSystem() {
                p.push();
                p.stroke(200);
                p.strokeWeight(1);

                // Draw grid lines
                for (let x = -2; x <= 2; x += 0.5) {
                    if (x === 0) continue;
                    p.line(
                        state.origin.x + x * state.scale, 
                        state.origin.y - 2 * state.scale,
                        state.origin.x + x * state.scale, 
                        state.origin.y + 2 * state.scale
                    );
                }

                for (let y = -2; y <= 2; y += 0.5) {
                    if (y === 0) continue;
                    p.line(
                        state.origin.x - 2 * state.scale, 
                        state.origin.y + y * state.scale,
                        state.origin.x + 2 * state.scale, 
                        state.origin.y + y * state.scale
                    );
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(
                    state.origin.x - 2 * state.scale, 
                    state.origin.y,
                    state.origin.x + 2 * state.scale, 
                    state.origin.y
                );
                p.line(
                    state.origin.x, 
                    state.origin.y - 2 * state.scale,
                    state.origin.x, 
                    state.origin.y + 2 * state.scale
                );

                // Draw tick marks and labels
                p.textSize(12);
                p.textAlign(p.CENTER, p.TOP);
                for (let x = -2; x <= 2; x += 1) {
                    if (x === 0) continue;
                    p.line(
                        state.origin.x + x * state.scale, 
                        state.origin.y - 5,
                        state.origin.x + x * state.scale, 
                        state.origin.y + 5
                    );
                    p.text(x, state.origin.x + x * state.scale, state.origin.y + 10);
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = -2; y <= 2; y += 1) {
                    if (y === 0) continue;
                    p.line(
                        state.origin.x - 5, 
                        state.origin.y + y * state.scale,
                        state.origin.x + 5, 
                        state.origin.y + y * state.scale
                    );
                    p.text(y, state.origin.x - 10, state.origin.y + y * state.scale);
                }

                // Axis labels
                p.textSize(14);
                p.textAlign(p.CENTER, p.TOP);
                p.text('x(t)', state.origin.x + 2 * state.scale, state.origin.y + 10);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text('y(t)', state.origin.x - 10, state.origin.y - 2 * state.scale);

                p.pop();
            }

            function drawLemniscate() {
                p.push();
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                for (let t = 0; t <= p.TWO_PI; t += 0.01) {
                    const pos = calculatePosition(t);
                    p.vertex(
                        state.origin.x + pos.x * state.scale,
                        state.origin.y - pos.y * state.scale
                    );
                }
                p.endShape();

                p.pop();
            }

            function drawPoint() {
                const pos = calculatePosition(state.t);
                p.push();
                p.fill(0);
                p.noStroke();
                p.circle(
                    state.origin.x + pos.x * state.scale,
                    state.origin.y - pos.y * state.scale,
                    8
                );
                p.pop();
            }

            function drawVector(vec, type, originX, originY, color) {
                const vecLength = p.sqrt(vec.x * vec.x + vec.y * vec.y);
                if (vecLength < 0.001) return; // Don't draw zero-length vectors

                const scaleFactor = type === 'position' ? 1 : 0.5; // Scale down velocity/acceleration
                const scaledX = vec.x * scaleFactor;
                const scaledY = vec.y * scaleFactor;

                p.push();
                p.stroke(color);
                p.strokeWeight(2);
                p.fill(color);

                // Draw the vector line
                p.line(
                    state.origin.x + originX * state.scale,
                    state.origin.y - originY * state.scale,
                    state.origin.x + (originX + scaledX) * state.scale,
                    state.origin.y - (originY + scaledY) * state.scale
                );

                // Draw the arrowhead
                const angle = p.atan2(-scaledY, scaledX);
                const arrowSize = 10;
                p.translate(
                    state.origin.x + (originX + scaledX) * state.scale,
                    state.origin.y - (originY + scaledY) * state.scale
                );
                p.rotate(angle);
                p.triangle(0, 0, -arrowSize, arrowSize/2, -arrowSize, -arrowSize/2);

                // Label the vector
                p.textSize(12);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text(type.charAt(0).toUpperCase() + type.slice(1), -arrowSize - 5, 0);

                p.pop();
            }

            function drawBasisVectors(pos, vel, acc) {
                if (state.coordSystem === 'rectangular') {
                    // Rectangular basis vectors (î, ĵ)
                    drawBasisVector(pos.x, pos.y, 1, 0, 'î', '#000000');
                    drawBasisVector(pos.x, pos.y, 0, 1, 'ĵ', '#000000');
                } 
                else if (state.coordSystem === 'normal-tangential') {
                    // Normal-tangential basis vectors (ê_T, ê_N)
                    const velLength = p.sqrt(vel.x * vel.x + vel.y * vel.y);
                    if (velLength > 0.001) {
                        const eT = { x: vel.x / velLength, y: vel.y / velLength };
                        const eN = { x: -eT.y, y: eT.x }; // Rotate 90 degrees counter-clockwise

                        // Make sure normal vector points towards center of curvature
                        // We can check the dot product with acceleration
                        const dotProduct = eN.x * acc.x + eN.y * acc.y;
                        if (dotProduct > 0) {
                            eN.x *= -1;
                            eN.y *= -1;
                        }

                        drawBasisVector(pos.x, pos.y, eT.x, eT.y, 'ê_T', '#000000');
                        drawBasisVector(pos.x, pos.y, eN.x, eN.y, 'ê_N', '#000000');
                    }
                } 
                else if (state.coordSystem === 'polar') {
                    // Polar basis vectors (ê_r, ê_θ)
                    const posLength = p.sqrt(pos.x * pos.x + pos.y * pos.y);
                    if (posLength > 0.001) {
                        const eR = { x: pos.x / posLength, y: pos.y / posLength };
                        const eTheta = { x: -eR.y, y: eR.x }; // Rotate 90 degrees counter-clockwise

                        drawBasisVector(pos.x, pos.y, eR.x, eR.y, 'ê_r', '#000000');
                        drawBasisVector(pos.x, pos.y, eTheta.x, eTheta.y, 'ê_θ', '#000000');
                    }
                }
            }

            function drawBasisVector(originX, originY, dirX, dirY, label, color) {
                const vecLength = 0.3; // Fixed length for basis vectors

                p.push();
                p.stroke(color);
                p.strokeWeight(1);
                p.fill(color);
                p.setLineDash([5, 3]);

                // Draw the vector line
                p.line(
                    state.origin.x + originX * state.scale,
                    state.origin.y - originY * state.scale,
                    state.origin.x + (originX + dirX * vecLength) * state.scale,
                    state.origin.y - (originY + dirY * vecLength) * state.scale
                );

                // Draw the arrowhead
                const angle = p.atan2(-dirY * vecLength, dirX * vecLength);
                const arrowSize = 8;
                p.translate(
                    state.origin.x + (originX + dirX * vecLength) * state.scale,
                    state.origin.y - (originY + dirY * vecLength) * state.scale
                );
                p.rotate(angle);
                p.triangle(0, 0, -arrowSize, arrowSize/2, -arrowSize, -arrowSize/2);

                // Label the vector
                p.textSize(12);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text(label, -arrowSize - 5, 0);

                p.pop();
            }

            function drawInstantaneousCircle(pos, vel, acc) {
                const velLength = p.sqrt(vel.x * vel.x + vel.y * vel.y);
                const crossProduct = vel.x * acc.y - vel.y * acc.x;

                if (p.abs(crossProduct) < 0.001 || velLength < 0.001) return;

                const radius = (velLength * velLength * velLength) / p.abs(crossProduct);

                // Calculate normal vector
                const eT = { x: vel.x / velLength, y: vel.y / velLength };
                let eN = { x: -eT.y, y: eT.x };

                // Make sure normal vector points towards center of curvature
                const dotProduct = eN.x * acc.x + eN.y * acc.y;
                if (dotProduct > 0) {
                    eN.x *= -1;
                    eN.y *= -1;
                }

                const center = {
                    x: pos.x + eN.x * radius,
                    y: pos.y + eN.y * radius
                };

                p.push();
                p.stroke(150);
                p.strokeWeight(1);
                p.noFill();
                p.setLineDash([5, 3]);

                p.circle(
                    state.origin.x + center.x * state.scale,
                    state.origin.y - center.y * state.scale,
                    radius * 2 * state.scale
                );

                p.pop();
            }

            function calculatePosition(t) {
                return {
                    x: p.cos(t),
                    y: p.sin(2 * t)
                };
            }

            function calculateVelocity(t) {
                return {
                    x: -p.sin(t),
                    y: 2 * p.cos(2 * t)
                };
            }

            function calculateAcceleration(t) {
                return {
                    x: -p.cos(t),
                    y: -4 * p.sin(2 * t)
                };
            }

            // Helper function to set dashed lines
            p.setLineDash = function(list) {
                p.drawingContext.setLineDash(list);
            };
        }

        // Create p5 instance
        new p5(sketch);
    </script>
</body>
