                <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="mass-value" class="value-display">1.00</span>
            </div>

            <div id="time-controls">
                <label>Time</label>
                <button id="btn-step-back">◀</button>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-forward">▶|</button>
            </div>

            <button id="btn-reset">×</button>
        </div>

        <div id="visualization-container">
            <div id="plot-canvas-container"></div>
            <div id="animation-canvas-container"></div>
        </div>
    </div>

    <script>
        // State variables
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency = Math.sqrt(stiffness / mass);
        let positionHistory = [];

        // p5.js sketch variables
        let plotCanvas;
        let animationCanvas;
        let plotWidth, plotHeight;
        let animWidth, animHeight;

        // Constants
        const TIME_STEP = 0.05;
        const PLOT_X_RANGE = 25;
        const SPRING_SEGMENTS = 10;

        // DOM elements
        const amplitudeSlider = document.getElementById('slider-amplitude');
        const stiffnessSlider = document.getElementById('slider-stiffness');
        const massSlider = document.getElementById('slider-mass');
        const amplitudeValue = document.getElementById('amplitude-value');
        const stiffnessValue = document.getElementById('stiffness-value');
        const massValue = document.getElementById('mass-value');
        const playPauseBtn = document.getElementById('btn-play-pause');
        const stepBackBtn = document.getElementById('btn-step-back');
        const stepForwardBtn = document.getElementById('btn-step-forward');
        const resetBtn = document.getElementById('btn-reset');

        // Initialize the simulation
        function initSimulation() {
            updateSliderDisplays();
            angularFrequency = Math.sqrt(stiffness / mass);
            time = 0;
            positionHistory = [];
            isPlaying = false;
            playPauseBtn.textContent = '▶';

            if (plotCanvas) plotCanvas.redraw();
            if (animationCanvas) animationCanvas.redraw();
        }

        // Update slider value displays
        function updateSliderDisplays() {
            amplitudeValue.textContent = amplitude.toFixed(3);
            stiffnessValue.textContent = stiffness.toFixed(2);
            massValue.textContent = mass.toFixed(2);
        }

        // Calculate current position
        function calculatePosition() {
            return amplitude * Math.sin(angularFrequency * time);
        }

        // Step the simulation
        function stepSimulation(dt) {
            time += dt;
            if (time < 0) time = 0;

            const y = calculatePosition();
            positionHistory.push({ time, y });

            // Limit history to visible range plus some buffer
            while (positionHistory.length > 0 && positionHistory[0].time < time - PLOT_X_RANGE - 5) {
                positionHistory.shift();
            }

            if (plotCanvas) plotCanvas.redraw();
            if (animationCanvas) animationCanvas.redraw();
        }

        // Setup plot canvas
        function setupPlotCanvas() {
            plotCanvas = new p5((p) => {
                p.setup = function() {
                    const container = document.getElementById('plot-canvas-container');
                    plotWidth = container.offsetWidth;
                    plotHeight = container.offsetHeight;
                    p.createCanvas(plotWidth, plotHeight);
                };

                p.draw = function() {
                    p.background(255);

                    // Draw grid and axes
                    p.stroke(200);
                    p.strokeWeight(1);

                    // Horizontal grid lines
                    for (let y = -1; y <= 1; y += 0.5) {
                        const pixelY = p.map(y, 1, -1, 0, plotHeight);
                        p.line(0, pixelY, plotWidth, pixelY);
                        p.fill(0);
                        p.noStroke();
                        p.text(y.toFixed(1), 5, pixelY - 5);
                        p.stroke(200);
                    }

                    // Vertical grid lines
                    const startTime = Math.max(0, time - PLOT_X_RANGE);
                    const endTime = time;
                    for (let t = Math.ceil(startTime); t <= endTime; t += 5) {
                        const pixelX = p.map(t, startTime, endTime, 0, plotWidth);
                        p.line(pixelX, 0, pixelX, plotHeight);
                        p.fill(0);
                        p.noStroke();
                        p.text(t, pixelX + 5, plotHeight - 5);
                        p.stroke(200);
                    }

                    // Draw axes
                    p.stroke(0);
                    p.strokeWeight(2);
                    const xAxisY = p.map(0, 1, -1, 0, plotHeight);
                    p.line(0, xAxisY, plotWidth, xAxisY);
                    p.line(0, 0, 0, plotHeight);

                    // Draw waveform
                    if (positionHistory.length > 1) {
                        p.stroke('#6363B2');
                        p.strokeWeight(2);
                        p.noFill();

                        p.beginShape();
                        for (const point of positionHistory) {
                            const x = p.map(point.time, startTime, endTime, 0, plotWidth);
                            const y = p.map(point.y, 1, -1, 0, plotHeight);
                            p.vertex(x, y);
                        }
                        p.endShape();
                    }
                };
            }, 'plot-canvas-container');
        }

        // Setup animation canvas
        function setupAnimationCanvas() {
            animationCanvas = new p5((p) => {
                p.setup = function() {
                    const container = document.getElementById('animation-canvas-container');
                    animWidth = container.offsetWidth;
                    animHeight = container.offsetHeight;
                    p.createCanvas(animWidth, animHeight);
                };

                p.draw = function() {
                    p.background(255);

                    // Draw vertical axis
                    p.stroke(0);
                    p.strokeWeight(1);
                    const centerX = animWidth / 2;

                    // Draw scale markers
                    for (let y = -1; y <= 1; y += 0.5) {
                        const pixelY = p.map(y, -1.2, 1.2, 0, animHeight);
                        p.line(centerX - 10, pixelY, centerX + 10, pixelY);
                        p.fill(0);
                        p.noStroke();
                        p.text(y.toFixed(1), centerX + 15, pixelY + 5);
                        p.stroke(0);
                    }

                    // Calculate current position
                    const y = calculatePosition();
                    const pixelY = p.map(y, -1.2, 1.2, 0, animHeight);

                    // Draw anchor point (top of spring)
                    const anchorY = p.map(1.2, -1.2, 1.2, 0, animHeight);
                    p.stroke(0);
                    p.strokeWeight(2);
                    p.line(centerX - 20, anchorY, centerX + 20, anchorY);

                    // Draw spring
                    const springLength = pixelY - anchorY;
                    const segmentLength = springLength / SPRING_SEGMENTS;
                    const amplitude = 15;

                    p.beginShape();
                    p.vertex(centerX, anchorY);

                    for (let i = 1; i < SPRING_SEGMENTS; i++) {
                        const yPos = anchorY + i * segmentLength;
                        const xOffset = (i % 2 === 0) ? -amplitude : amplitude;
                        p.vertex(centerX + xOffset, yPos);
                    }

                    p.vertex(centerX, pixelY);
                    p.endShape();

                    // Draw mass
                    p.fill('#6363B2');
                    p.noStroke();
                    const massWidth = 40;
                    const massHeight = 30;
                    p.rect(centerX - massWidth/2, pixelY, massWidth, massHeight);
                };
            }, 'animation-canvas-container');
        }

        // Event listeners
        amplitudeSlider.addEventListener('input', function() {
            amplitude = parseFloat(this.value);
            updateSliderDisplays();
            initSimulation();
        });

        stiffnessSlider.addEventListener('input', function() {
            stiffness = parseFloat(this.value);
            updateSliderDisplays();
            initSimulation();
        });

        massSlider.addEventListener('input', function() {
            mass = parseFloat(this.value);
            updateSliderDisplays();
            initSimulation();
        });

        playPauseBtn.addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '||' : '▶';
        });

        stepBackBtn.addEventListener('click', function() {
            if (!isPlaying) {
                stepSimulation(-TIME_STEP);
            }
        });

        stepForwardBtn.addEventListener('click', function() {
            if (!isPlaying) {
                stepSimulation(TIME_STEP);
            }
        });

        resetBtn.addEventListener('click', function() {
            amplitudeSlider.value = 0.3;
            stiffnessSlider.value = 1.0;
            massSlider.value = 1.0;
            amplitude = 0.3;
            stiffness = 1.0;
            mass = 1.0;
            initSimulation();
        });

        // Animation loop
        function animate() {
            if (isPlaying) {
                stepSimulation(TIME_STEP);
            }
            requestAnimationFrame(animate);
        }

        // Initialize everything when the page loads
        window.addEventListener('load', function() {
            setupPlotCanvas();
            setupAnimationCanvas();
            initSimulation();
            animate();
        });
    </script>
</body>
</html>
```

