            font-weight: bold;
            transition: background-color 0.2s;
        }

        .cell-on {
            background-color: #FFD700;
        }

        .cell-off {
            background-color: #D3D3D3;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <span class="distance-label">distance function:</span>
            <button id="btn-manhattan" class="distance-btn active">Manhattan</button>
            <button id="btn-euclidean" class="distance-btn">squared Euclidean</button>
            <button id="btn-chebyshev" class="distance-btn">Chebyshev</button>
        </div>
        <div id="grid-container"></div>
    </div>

    <script>
        // State variables
        let currentDistanceFunction = 'manhattan';
        const gridState = Array(9).fill().map(() => Array(7).fill(false));

        // Initialize with two "on" cells as specified
        gridState[2][1] = true;
        gridState[6][4] = true;

        // DOM elements
        const gridContainer = document.getElementById('grid-container');
        const btnManhattan = document.getElementById('btn-manhattan');
        const btnEuclidean = document.getElementById('btn-euclidean');
        const btnChebyshev = document.getElementById('btn-chebyshev');

        // Initialize the grid
        function initializeGrid() {
            gridContainer.innerHTML = '';

            for (let row = 0; row < 9; row++) {
                for (let col = 0; col < 7; col++) {
                    const cell = document.createElement('div');
                    cell.className = gridState[row][col] ? 'cell cell-on' : 'cell cell-off';
                    cell.dataset.row = row;
                    cell.dataset.col = col;

                    const span = document.createElement('span');
                    cell.appendChild(span);

                    cell.addEventListener('click', () => handleCellClick(row, col));
                    gridContainer.appendChild(cell);
                }
            }

            updateGridDisplay();
        }

        // Handle cell click
        function handleCellClick(row, col) {
            gridState[row][col] = !gridState[row][col];
            updateGridDisplay();
        }

        // Update the grid display with current distances
        function updateGridDisplay() {
            const distances = calculateDistanceTransform();
            const cells = gridContainer.querySelectorAll('.cell');

            let cellIndex = 0;
            for (let row = 0; row < 9; row++) {
                for (let col = 0; col < 7; col++) {
                    const cell = cells[cellIndex];
                    const span = cell.querySelector('span');

                    if (gridState[row][col]) {
                        cell.className = 'cell cell-on';
                        span.textContent = '0';
                    } else {
                        cell.className = 'cell cell-off';
                        span.textContent = distances[row][col] === Infinity ? '' : distances[row][col].toString();
                    }

                    cellIndex++;
                }
            }
        }

        // Calculate distance transform
        function calculateDistanceTransform() {
            const distances = Array(9).fill().map(() => Array(7).fill(Infinity));
            const sources = [];

            // Find all source points ("on" cells)
            for (let row = 0; row < 9; row++) {
                for (let col = 0; col < 7; col++) {
                    if (gridState[row][col]) {
                        sources.push({row, col});
                    }
                }
            }

            // If no sources, all distances remain Infinity
            if (sources.length === 0) {
                return distances;
            }

            // Calculate distance from each cell to nearest source
            for (let row = 0; row < 9; row++) {
                for (let col = 0; col < 7; col++) {
                    let minDistance = Infinity;

                    for (const source of sources) {
                        const distance = calculateDistance(
                            row, col, 
                            source.row, source.col, 
                            currentDistanceFunction
                        );

                        if (distance < minDistance) {
                            minDistance = distance;
                        }
                    }

                    distances[row][col] = minDistance;
                }
            }

            return distances;
        }

        // Calculate distance between two points based on current metric
        function calculateDistance(r1, c1, r2, c2, metric) {
            const dr = Math.abs(r1 - r2);
            const dc = Math.abs(c1 - c2);

            switch (metric) {
                case 'manhattan':
                    return dr + dc;
                case 'euclidean':
                    return dr * dr + dc * dc;
                case 'chebyshev':
                    return Math.max(dr, dc);
                default:
                    return 0;
            }
        }

        // Handle distance function button clicks
        function handleDistanceButtonClick(metric) {
            currentDistanceFunction = metric;

            // Update button styles
            btnManhattan.classList.toggle('active', metric === 'manhattan');
            btnEuclidean.classList.toggle('active', metric === 'euclidean');
            btnChebyshev.classList.toggle('active', metric === 'chebyshev');

            // Recalculate and update grid
            updateGridDisplay();
        }

        // Set up event listeners for distance buttons
        btnManhattan.addEventListener('click', () => handleDistanceButtonClick('manhattan'));
        btnEuclidean.addEventListener('click', () => handleDistanceButtonClick('euclidean'));
        btnChebyshev.addEventListener('click', () => handleDistanceButtonClick('chebyshev'));

        // Initialize the application
        initializeGrid();
    </script>
</body>
</html>
