    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // Wait for MathJax to be ready
        window.MathJax = {
            startup: {
                pageReady: () => {
                    MathJax.startup.defaultPageReady();
                    initialize();
                }
            }
        };

        function initialize() {
            // Set up event listeners
            document.getElementById('btn-model-ep').addEventListener('click', function() {
                document.getElementById('btn-model-ep').classList.add('active');
                document.getElementById('btn-model-ar').classList.remove('active');
                document.getElementById('label-param-name').textContent = '\\(a_{EP}\\)';
                MathJax.typeset();
                update();
            });

            document.getElementById('btn-model-ar').addEventListener('click', function() {
                document.getElementById('btn-model-ar').classList.add('active');
                document.getElementById('btn-model-ep').classList.remove('active');
                document.getElementById('label-param-name').textContent = '\\(a_{Ar}\\)';
                MathJax.typeset();
                update();
            });

