            document.getElementById('btn-tmax-high').addEventListener('click', function() {
                document.getElementById('btn-tmax-high').classList.add('active');
                document.getElementById('btn-tmax-low').classList.remove('active');
                document.getElementById('slider-tmax').max = 6000;
                update();
            });

            // Slider event listeners
            const sliders = ['n', 'tmin', 'tmax', 'tref', 'param'];
            sliders.forEach(slider => {
                document.getElementById(`slider-${slider}`).addEventListener('input', function() {
                    document.getElementById(`label-${slider}`).textContent = this.value;
                    update();
                });
            });

            // Initial update
            update();
        }

        function update() {
            // Read all control values
            const n = parseInt(document.getElementById('slider-n').value);
            let tmin = parseInt(document.getElementById('slider-tmin').value);
            let tmax = parseInt(document.getElementById('slider-tmax').value);
            const tref = parseInt(document.getElementById('slider-tref').value);
            const param = parseInt(document.getElementById('slider-param').value);

            // Validate slider constraints
            if (tmin >= tmax) {
                tmin = tmax - 1;
                document.getElementById('slider-tmin').value = tmin;
                document.getElementById('label-tmin').textContent = tmin;
            }

            if (tref < tmin) {
                document.getElementById('slider-tref').value = tmin;
                document.getElementById('label-tref').textContent = tmin;
            } else if (tref > tmax) {
                document.getElementById('slider-tref').value = tmax;
                document.getElementById('label-tref').textContent = tmax;
            }

            // Generate data points
            const temperatures = [];
            for (let i = 0; i < n; i++) {
                temperatures.push(tmin + (tmax - tmin) * i / (n - 1));
            }

            // Convert to Kelvin
            const tminK = tmin + 273.15;
            const tmaxK = tmax + 273.15;
            const trefK = tref + 273.15;
            const temperaturesK = temperatures.map(t => t + 273.15);

            // Calculate y values based on selected model
            const isEPModel = document.getElementById('btn-model-ep').classList.contains('active');
            let yValues = [];

            if (isEPModel) {
                // Eyring-Polanyi model
                yValues = temperaturesK.map(t => 
                    (t / trefK) * Math.exp(param * (1/trefK - 1/t))
                );
            } else {
                // Arrhenius model
                yValues = temperaturesK.map(t => 
                    Math.exp(param * (1/trefK - 1/t))
                );
            }

            // Fit the data to the other model
            let fitParam, r2;
            if (isEPModel) {
                // Fit to Arrhenius model
                const result = fitToArrhenius(temperaturesK, yValues, trefK);
                fitParam = result.a_fit;
                r2 = result.r2;

                // Update fit results display
                document.getElementById('fit-results').innerHTML = 
                    `\\(a_{Ar} = ${Math.round(fitParam).toLocaleString()}.\\) \\(r^2 = ${r2.toFixed(3)}\\)`;
            } else {
                // Fit to Eyring-Polanyi model
                const result = fitToEP(temperaturesK, yValues, trefK);
                fitParam = result.a_fit;
                r2 = result.r2;

                // Update fit results display
                document.getElementById('fit-results').innerHTML = 
                    `\\(a_{EP} = ${Math.round(fitParam).toLocaleString()}.\\) \\(r^2 = ${r2.toFixed(3)}\\)`;
            }

            MathJax.typeset();

            // Draw the plot
            drawPlot(temperatures, yValues, fitParam, isEPModel, trefK);
        }

        function fitToArrhenius(temperaturesK, yValues, trefK) {
            // Grid search for best a_fit parameter
            let bestFit = { a_fit: 0, rss: Infinity };
            const step = 10;

            for (let a = 0; a <= 10000; a += step) {
                let rss = 0;
                for (let i = 0; i < temperaturesK.length; i++) {
                    const t = temperaturesK[i];
                    const y = yValues[i];
                    const yFit = Math.exp(a * (1/trefK - 1/t));
                    rss += Math.pow(y - yFit, 2);
                }

                if (rss < bestFit.rss) {
                    bestFit = { a_fit: a, rss: rss };
                }
            }

            // Refine search around the best fit
            const refineRange = 100;
            for (let a = bestFit.a_fit - refineRange; a <= bestFit.a_fit + refineRange; a++) {
                if (a < 0) continue;

                let rss = 0;
                for (let i = 0; i < temperaturesK.length; i++) {
                    const t = temperaturesK[i];
                    const y = yValues[i];
                    const yFit = Math.exp(a * (1/trefK - 1/t));
                    rss += Math.pow(y - yFit, 2);
                }

                if (rss < bestFit.rss) {
                    bestFit = { a_fit: a, rss: rss };
                }
            }

            // Calculate R-squared
            const yMean = yValues.reduce((sum, y) => sum + y, 0) / yValues.length;
            const ssTot = yValues.reduce((sum, y) => sum + Math.pow(y - yMean, 2), 0);
            const r2 = 1 - (bestFit.rss / ssTot);

            return { a_fit: bestFit.a_fit, r2: r2 };
        }

        function fitToEP(temperaturesK, yValues, trefK) {
            // Grid search for best a_fit parameter
            let bestFit = { a_fit: 0, rss: Infinity };
            const step = 10;

            for (let a = 0; a <= 10000; a += step) {
                let rss = 0;
                for (let i = 0; i < temperaturesK.length; i++) {
                    const t = temperaturesK[i];
                    const y = yValues[i];
                    const yFit = (t / trefK) * Math.exp(a * (1/trefK - 1/t));
                    rss += Math.pow(y - yFit, 2);
                }

                if (rss < bestFit.rss) {
                    bestFit = { a_fit: a, rss: rss };
                }
            }

            // Refine search around the best fit
            const refineRange = 100;
            for (let a = bestFit.a_fit - refineRange; a <= bestFit.a_fit + refineRange; a++) {
                if (a < 0) continue;

                let rss = 0;
                for (let i = 0; i < temperaturesK.length; i++) {
                    const t = temperaturesK[i];
                    const y = yValues[i];
                    const yFit = (t / trefK) * Math.exp(a * (1/trefK - 1/t));
                    rss += Math.pow(y - yFit, 2);
                }

                if (rss < bestFit.rss) {
                    bestFit = { a_fit: a, rss: rss };
                }
            }

            // Calculate R-squared
            const yMean = yValues.reduce((sum, y) => sum + y, 0) / yValues.length;
            const ssTot = yValues.reduce((sum, y) => sum + Math.pow(y - yMean, 2), 0);
            const r2 = 1 - (bestFit.rss / ssTot);

            return { a_fit: bestFit.a_fit, r2: r2 };
        }

        function drawPlot(temperatures, yValues, fitParam, isEPModel, trefK) {
            // Generate fit curve points
            const fitTemperatures = [];
            const fitYValues = [];
            const tmin = Math.min(...temperatures);
            const tmax = Math.max(...temperatures);
            const steps = 200;

            for (let i = 0; i <= steps; i++) {
                const t = tmin + (tmax - tmin) * i / steps;
                const tK = t + 273.15;
                fitTemperatures.push(t);

                if (isEPModel) {
                    // Fit to Arrhenius model
                    fitYValues.push(Math.exp(fitParam * (1/trefK - 1/tK)));
                } else {
                    // Fit to Eyring-Polanyi model
                    fitYValues.push((tK / trefK) * Math.exp(fitParam * (1/trefK - 1/tK)));
                }
            }

            // Create traces
            const dataTrace = {
                x: temperatures,
                y: yValues,
                mode: 'markers',
                name: 'Data Points',
                marker: {
                    color: 'blue',
                    size: 8,
                    line: {
                        color: 'green',
                        width: 2
                    }
                }
            };

            const fitTrace = {
                x: fitTemperatures,
                y: fitYValues,
                mode: 'lines',
                name: 'Fit Curve',
                line: {
                    color: 'green',
                    width: 2
                }
            };

            // Set layout
            const modelName = isEPModel ? 'Eyring-Polanyi' : 'Arrhenius';
            const fitName = isEPModel ? 'Arrhenius' : 'Eyring-Polanyi';

            const layout = {
                title: `${fitName} model fit to ${modelName} data`,
                xaxis: {
                    title: 'T °C'
                },
                yaxis: {
                    title: 'k(T) / k(T<sub>ref</sub>)'
                },
                showlegend: true
            };

            // Draw plot
            Plotly.newPlot('plot-container', [dataTrace, fitTrace], layout);
        }
    </script>
</body>
</html>
