    <div id="controls-container">
        <span id="n-display">6</span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6" class="active">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n = 6;
        let canvasWidth = 800;
        let canvasHeight = 500;
        let colors = ['#fefd00', '#ee1f24', '#0048a1'];
        let myp5;

        function setup() {
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            drawScene();
        }

        function drawScene() {
            background(255);
            stroke(0);
            strokeWeight(1);

            const R = 150;
            const centerX = canvasWidth / 4;
            const centerY = canvasHeight / 2;
            const angleStep = PI / n;

            // Draw polygon on left side
            for (let j = 1; j <= 2*n; j++) {
                const color = colors[(j-1) % 3];
                fill(color);

                beginShape();
                vertex(centerX, centerY);
                vertex(
                    centerX + R * cos((j-1) * angleStep - PI/2),
                    centerY + R * sin((j-1) * angleStep - PI/2)
                );
                vertex(
                    centerX + R * cos(j * angleStep - PI/2),
                    centerY + R * sin(j * angleStep - PI/2)
                );
                endShape(CLOSE);
            }

            // Draw rectangles on right side
            const s = 2 * R * sin(angleStep / 2);
            const a = R * cos(angleStep / 2);
            const startX = canvasWidth / 2;
            const startY = centerY - (floor(n/2) * 2*a + (n%2 ? a : 0)) / 2;

            // Draw full rectangles
            for (let k = 0; k < floor(n/2); k++) {
                const rectX = startX;
                const rectY = startY + k * 2*a;

                // Top triangle (yellow)
                fill(colors[0]);
                beginShape();
                vertex(rectX, rectY);
                vertex(rectX + s/2, rectY + a);
                vertex(rectX - s/2, rectY + a);
                endShape(CLOSE);

                // Bottom triangle (red)
                fill(colors[1]);
                beginShape();
                vertex(rectX, rectY + 2*a);
                vertex(rectX + s/2, rectY + a);
                vertex(rectX - s/2, rectY + a);
                endShape(CLOSE);

                // Left triangle (blue)
                fill(colors[2]);
                beginShape();
                vertex(rectX - s/2, rectY + a);
                vertex(rectX, rectY);
                vertex(rectX, rectY + 2*a);
                endShape(CLOSE);

                // Right triangle (yellow)
                fill(colors[0]);
                beginShape();
                vertex(rectX + s/2, rectY + a);
                vertex(rectX, rectY);
                vertex(rectX, rectY + 2*a);
                endShape(CLOSE);

                // Outline
                noFill();
                rect(rectX - s/2, rectY, s, 2*a);
                line(rectX - s/2, rectY + a, rectX + s/2, rectY + a);
                line(rectX, rectY, rectX, rectY + 2*a);
            }

            // Draw half rectangle if n is odd
            if (n % 2 === 1) {
                const halfRectX = startX;
                const halfRectY = startY + floor(n/2) * 2*a;

                // Top triangle (blue)
                fill(colors[2]);
                beginShape();
                vertex(halfRectX, halfRectY);
                vertex(halfRectX + s/2, halfRectY + a);
                vertex(halfRectX - s/2, halfRectY + a);
                endShape(CLOSE);

                // Bottom triangle (red)
                fill(colors[1]);
                beginShape();
                vertex(halfRectX, halfRectY + 2*a);
                vertex(halfRectX + s/2, halfRectY + a);
                vertex(halfRectX - s/2, halfRectY + a);
                endShape(CLOSE);

                // Outline
                noFill();
                rect(halfRectX - s/2, halfRectY, s, 2*a);
                line(halfRectX - s/2, halfRectY + a, halfRectX + s/2, halfRectY + a);
            }
        }

        function updateVisualization(newN) {
            n = newN;
            document.getElementById('n-display').textContent = n;

            // Update button states
            document.querySelectorAll('button[id^="btn-n-"]').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-n-${n}`).classList.add('active');

            // Redraw scene
            if (myp5) {
                myp5.redraw();
            }
        }

        // Initialize p5.js sketch
        function sketch(p) {
            myp5 = p;

            p.setup = setup;
            p.draw = function() {}; // Empty draw function since we'll use redraw()
            p.drawScene = drawScene;
        }

        new p5(sketch);

        // Set up button event listeners
        document.getElementById('btn-n-3').addEventListener('click', () => updateVisualization(3));
        document.getElementById('btn-n-4').addEventListener('click', () => updateVisualization(4));
        document.getElementById('btn-n-5').addEventListener('click', () => updateVisualization(5));
        document.getElementById('btn-n-6').addEventListener('click', () => updateVisualization(6));
        document.getElementById('btn-n-7').addEventListener('click', () => updateVisualization(7));
        document.getElementById('btn-n-8').addEventListener('click', () => updateVisualization(8));
        document.getElementById('btn-n-9').addEventListener('click', () => updateVisualization(9));
        document.getElementById('btn-n-10').addEventListener('click', () => updateVisualization(10));
    </script>
</body>
</html>
