    <script>
        // State variables
        let qValue = 2;
        let pValue = 1;
        let startConfig = { q1: -1.5, q2: 0.5 };
        let goalConfig = { q1: 1.5, q2: 1.0 };
        let obstacles = [
            { x: 200, y: 100, radius: 15 },
            { x: 200, y: 300, radius: 15 }
        ];

        // Flags for dragging
        let isDraggingObstacle1 = false;
        let isDraggingObstacle2 = false;
        let isDraggingStart = false;
        let isDraggingGoal = false;

        // Path and grid data
        let path = [];
        let grid = [];
        let gridSize = 0;
        let cellSize = 0;
        let distanceField = [];

        // p5.js sketches
        let workspaceSketch = function(p) {
            let armLength1 = 75;
            let armLength2 = 50;
            let armWidth = 10;
            let baseX = 200;
            let baseY = 200;

            p.setup = function() {
                let canvas = p.createCanvas(400, 400);
                canvas.parent('canvas-workspace');
                p.angleMode(p.RADIANS);
            };

            p.draw = function() {
                p.background(240);

                // Draw obstacles
                p.fill(204, 102, 0);
                p.noStroke();
                for (let i = 0; i < obstacles.length; i++) {
                    p.ellipse(obstacles[i].x, obstacles[i].y, obstacles[i].radius * 2);

                    // Draw pattern on obstacles
                    p.stroke(153, 51, 0);
                    p.strokeWeight(1);
                    p.noFill();
                    p.ellipse(obstacles[i].x, obstacles[i].y, obstacles[i].radius * 1.6);
                    p.ellipse(obstacles[i].x, obstacles[i].y, obstacles[i].radius * 1.2);
                }

                // Calculate current configuration based on path and pValue
                let currentConfig = { q1: startConfig.q1, q2: startConfig.q2 };
                if (path.length > 0) {
                    let pathIndex = Math.floor(pValue * (path.length - 1));
                    pathIndex = p.constrain(pathIndex, 0, path.length - 1);
                    currentConfig = path[pathIndex];
                }

                // Draw robot arm
                p.push();
                p.translate(baseX, baseY);

                // Draw angle indicator for q1
                p.noFill();
                p.stroke(150);
                p.strokeWeight(1);
                p.arc(0, 0, 30, 30, 0, currentConfig.q1, p.OPEN);

                // Draw first link
                p.rotate(currentConfig.q1);
                p.fill(150);
                p.stroke(0);
                p.strokeWeight(1);
                p.rect(0, -armWidth/2, armLength1, armWidth, armWidth/2);

                // Draw second link
                p.translate(armLength1, 0);
                p.rotate(currentConfig.q2);

                // Draw angle indicator for q2
                p.noFill();
                p.stroke(150);
                p.strokeWeight(1);
                p.arc(0, 0, 20, 20, 0, currentConfig.q2, p.OPEN);

                p.fill(150);
                p.stroke(0);
                p.strokeWeight(1);
                p.rect(0, -armWidth/2, armLength2, armWidth, armWidth/2);

                // Label on second link
                p.fill(0);
                p.noStroke();
                p.textSize(12);
                p.text("Robot Arm", armLength2/2 - 30, -armWidth - 5);

                p.pop();
            };

            p.mousePressed = function() {
                // Check if mouse is over obstacle 1
                if (p.dist(p.mouseX, p.mouseY, obstacles[0].x, obstacles[0].y) < obstacles[0].radius) {
                    isDraggingObstacle1 = true;
                    return;
                }

                // Check if mouse is over obstacle 2
                if (p.dist(p.mouseX, p.mouseY, obstacles[1].x, obstacles[1].y) < obstacles[1].radius) {
                    isDraggingObstacle2 = true;
                    return;
                }
            };

            p.mouseDragged = function() {
                if (isDraggingObstacle1) {
                    obstacles[0].x = p.mouseX;
                    obstacles[0].y = p.mouseY;
                    recalculateAll();
                } else if (isDraggingObstacle2) {
                    obstacles[1].x = p.mouseX;
                    obstacles[1].y = p.mouseY;
                    recalculateAll();
                }
            };

            p.mouseReleased = function() {
                isDraggingObstacle1 = false;
                isDraggingObstacle2 = false;
            };
        };

        let phaseSpaceSketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(400, 400);
                canvas.parent('canvas-phase-space');
                p.angleMode(p.RADIANS);
            };

            p.draw = function() {
                p.background(255);

                // Draw grid
                if (gridSize > 0 && cellSize > 0) {
                    // Draw distance field and obstacles
                    for (let i = 0; i < gridSize; i++) {
                        for (let j = 0; j < gridSize; j++) {
                            let x = i * cellSize;
                            let y = j * cellSize;

                            if (grid[i][j] === 1) { // Obstacle
                                p.fill(204, 0, 0);
                                p.noStroke();
                                p.rect(x, y, cellSize, cellSize);
                            } else { // Free space
                                let distVal = distanceField[i][j];
                                let maxDist = gridSize * 1.414; // Approximate max distance
                                let grayVal = p.map(distVal, 0, maxDist, 255, 50);
                                grayVal = p.constrain(grayVal, 50, 255);
                                p.fill(grayVal);
                                p.noStroke();
                                p.rect(x, y, cellSize, cellSize);
                            }
                        }
                    }

                    // Draw grid lines
                    p.stroke(200);
                    p.strokeWeight(0.5);
                    for (let i = 0; i <= gridSize; i++) {
                        p.line(i * cellSize, 0, i * cellSize, p.height);
                        p.line(0, i * cellSize, p.width, i * cellSize);
                    }

                    // Draw path
                    if (path.length > 1) {
                        p.stroke(0, 255, 0);
                        p.strokeWeight(3);
                        p.noFill();

                        p.beginShape();
                        for (let i = 0; i < path.length; i++) {
                            let gridX = angleToGridCoord(path[i].q1);
                            let gridY = angleToGridCoord(path[i].q2);
                            p.vertex(gridX * cellSize + cellSize/2, gridY * cellSize + cellSize/2);
                        }
                        p.endShape();

                        // Draw current position marker
                        let pathIndex = Math.floor(pValue * (path.length - 1));
                        pathIndex = p.constrain(pathIndex, 0, path.length - 1);
                        let current = path[pathIndex];
                        let gridX = angleToGridCoord(current.q1);
                        let gridY = angleToGridCoord(current.q2);

                        p.fill(255, 255, 0);
                        p.noStroke();
                        p.ellipse(gridX * cellSize + cellSize/2, gridY * cellSize + cellSize/2, 10, 10);
                    }

                    // Draw start and goal markers
                    drawConfigMarker(p, startConfig, true);
                    drawConfigMarker(p, goalConfig, false);
                }
            };

            p.mousePressed = function() {
                if (gridSize === 0) return;

                let gridX = Math.floor(p.mouseX / cellSize);
                let gridY = Math.floor(p.mouseY / cellSize);

                if (gridX < 0 || gridX >= gridSize || gridY < 0 || gridY >= gridSize) return;

                // Check if mouse is over start marker
                let startGridX = angleToGridCoord(startConfig.q1);
                let startGridY = angleToGridCoord(startConfig.q2);
                if (gridX === startGridX && gridY === startGridY) {
                    isDraggingStart = true;
                    return;
                }

                // Check if mouse is over goal marker
                let goalGridX = angleToGridCoord(goalConfig.q1);
                let goalGridY = angleToGridCoord(goalConfig.q2);
                if (gridX === goalGridX && gridY === goalGridY) {
                    isDraggingGoal = true;
                    return;
                }
            };

            p.mouseDragged = function() {
                if (gridSize === 0) return;

                let gridX = Math.floor(p.mouseX / cellSize);
                let gridY = Math.floor(p.mouseY / cellSize);

                if (gridX < 0 || gridX >= gridSize || gridY < 0 || gridY >= gridSize) return;

                if (isDraggingStart) {
                    let newQ1 = gridCoordToAngle(gridX);
                    let newQ2 = gridCoordToAngle(gridY);

                    // Only update if not in obstacle
                    if (grid[gridX][gridY] !== 1) {
                        startConfig = { q1: newQ1, q2: newQ2 };
                        recalculatePath();
                    }
                } else if (isDraggingGoal) {
                    let newQ1 = gridCoordToAngle(gridX);
                    let newQ2 = gridCoordToAngle(gridY);

                    // Only update if not in obstacle
                    if (grid[gridX][gridY] !== 1) {
                        goalConfig = { q1: newQ1, q2: newQ2 };
                        recalculateAll();
                    }
                }
            };

            p.mouseReleased = function() {
                isDraggingStart = false;
                isDraggingGoal = false;
            };
        };

        function drawConfigMarker(p, config, isStart) {
            let gridX = angleToGridCoord(config.q1);
            let gridY = angleToGridCoord(config.q2);

            // Check if configuration is valid (not in obstacle)
            let isValid = true;
            if (gridX >= 0 && gridX < gridSize && gridY >= 0 && gridY < gridSize) {
                isValid = grid[gridX][gridY] !== 1;
            }

            let markerColor = isValid ? p.color(0, 200, 0) : p.color(200, 0, 0);

            p.fill(markerColor);
            p.noStroke();
            p.ellipse(gridX * cellSize + cellSize/2, gridY * cellSize + cellSize/2, 15, 15);

            // Draw crosshair
            p.stroke(255);
            p.strokeWeight(2);
            p.line(
                gridX * cellSize + cellSize/2 - 5, 
                gridY * cellSize + cellSize/2, 
                gridX * cellSize + cellSize/2 + 5, 
                gridY * cellSize + cellSize/2
            );
            p.line(
                gridX * cellSize + cellSize/2, 
                gridY * cellSize + cellSize/2 - 5, 
                gridX * cellSize + cellSize/2, 
                gridY * cellSize + cellSize/2 + 5
            );

            // Label
            p.fill(0);
            p.noStroke();
            p.textSize(12);
            p.textAlign(p.CENTER);
            p.text(isStart ? "Start" : "Goal", gridX * cellSize + cellSize/2, gridY * cellSize + cellSize/2 + 25);
        }

        // Helper functions
        function angleToGridCoord(angle) {
            // Map angle from [-PI, PI] to [0, gridSize-1]
            let normalized = (angle + Math.PI) / (2 * Math.PI);
            return Math.floor(normalized * gridSize);
        }

        function gridCoordToAngle(coord) {
            // Map grid coordinate [0, gridSize-1] to angle [-PI, PI]
            return (coord / gridSize) * 2 * Math.PI - Math.PI;
        }

        function recalculateAll() {
            // Calculate grid size based on Q value
            gridSize = 10 * Math.pow(2, qValue);
            cellSize = 400 / gridSize;

            // Initialize grid and distance field
            grid = Array(gridSize).fill().map(() => Array(gridSize).fill(0));
            distanceField = Array(gridSize).fill().map(() => Array(gridSize).fill(0));

            // Calculate C-space obstacles
            calculateCSpaceObstacles();

            // Calculate distance field from goal
            calculateDistanceField();

            // Calculate path from start to goal
            recalculatePath();
        }

        function calculateCSpaceObstacles() {
            // For each grid cell, check if the corresponding configuration collides with obstacles
            for (let i =  ˝0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    let q1 = gridCoordToAngle(i);
                    let q2 = gridCoordToAngle(j);

                    if (checkCollision(q1, q2)) {
                        grid[i][j] = 1; // Mark as obstacle
                    } else {
                        grid[i][j] = 0; // Mark as free
                    }
                }
            }
        }

        function checkCollision(q1, q2) {
            // Robot arm parameters
            let armLength1 = 75;
            let armLength2 = 50;
            let armWidth = 10;
            let baseX = 200;
            let baseY = 200;

            // Calculate positions of arm segments
            let joint1X = baseX + armLength1 * Math.cos(q1);
            let joint1Y = baseY + armLength1 * Math.sin(q1);

            let joint2X = joint1X + armLength2 * Math.cos(q1 + q2);
            let joint2Y = joint1Y + armLength2 * Math.sin(q1 + q2);

            // Check collision for each obstacle
            for (let obs of obstacles) {
                // Check first link (base to joint1)
                if (lineCircleCollision(
                    baseX, baseY, joint1X, joint1Y, armWidth/2,
                    obs.x, obs.y, obs.radius
                )) {
                    return true;
                }

                // Check second link (joint1 to joint2)
                if (lineCircleCollision(
                    joint1X, joint1Y, joint2X, joint2Y, armWidth/2,
                    obs.x, obs.y, obs.radius
                )) {
                    return true;
                }
            }

            return false;
        }

        function lineCircleCollision(x1, y1, x2, y2, lineRadius, cx, cy, cr) {
            // Find the closest point on the line segment to the circle center
            let lineLength = Math.sqrt((x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1));
            let directionX = (x2 - x1) / lineLength;
            let directionY = (y2 - y1) / lineLength;

            let u = ((cx - x1) * directionX + (cy - y1) * directionY) / lineLength;
            u = Math.max(0, Math.min(1, u));

            let closestX = x1 + u * (x2 - x1);
            let closestY = y1 + u * (y2 - y1);

            // Calculate distance between closest point and circle center
            let distance = Math.sqrt((cx - closestX) * (cx - closestX) + (cy - closestY) * (cy - closestY));

            // Check if distance is less than sum of radii
            return distance < (lineRadius + cr);
        }

        function calculateDistanceField() {
            // Initialize distance field with large values
            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    distanceField[i][j] = gridSize * gridSize; // Large initial value
                }
            }

            // Get goal grid coordinates
            let goalX = angleToGridCoord(goalConfig.q1);
            let goalY = angleToGridCoord(goalConfig.q2);

            // Check if goal is in obstacle
            if (grid[goalX][goalY] === 1) {
                return;
            }

            // BFS queue
            let queue = [];
            queue.push({ x: goalX, y: goalY });
            distanceField[goalX][goalY] = 0;

            // 8-directional neighbors
            let dx = [-1, -1, -1, 0, 0, 1, 1, 1];
            let dy = [-1, 0, 1, -1, 1, -1, 0, 1];

            while (queue.length > 0) {
                let current = queue.shift();

                for (let k = 0; k < 8; k++) {
                    let nx = current.x + dx[k];
                    let ny = current.y + dy[k];

                    // Check bounds
                    if (nx < 0 || nx >= gridSize || ny < 0 || ny >= gridSize) continue;

                    // Check if it's an obstacle
                    if (grid[nx][ny] === 1) continue;

                    // Calculate new distance (Euclidean distance between angles)
                    let angleDist = Math.sqrt(
                        Math.pow(gridCoordToAngle(nx) - gridCoordToAngle(current.x), 2) +
                        Math.pow(gridCoordToAngle(ny) - gridCoordToAngle(current.y), 2)
                    );

                    let newDist = distanceField[current.x][current.y] + angleDist;

                    // Update if we found a shorter path
                    if (newDist < distanceField[nx][ny]) {
                        distanceField[nx][ny] = newDist;
                        queue.push({ x: nx, y: ny });
                    }
                }
            }
        }

        function recalculatePath() {
            path = [];

            // Get start and goal grid coordinates
            let startX = angleToGridCoord(startConfig.q1);
            let startY = angleToGridCoord(startConfig.q2);
            let goalX = angleToGridCoord(goalConfig.q1);
            let goalY = angleToGridCoord(goalConfig.q2);

            // Check if start or goal are in obstacles
            if (grid[startX][startY] === 1 || grid[goalX][goalY] === 1) {
                updatePathLength(0);
                return;
            }

            // A* pathfinding from start to goal following the distance field
            let currentX = startX;
            let currentY = startY;

            path.push({ q1: startConfig.q1, q2: startConfig.q2 });

            // 8-directional neighbors
            let dx = [-1, -1, -1, 0, 0, 1, 1, 1];
            let dy = [-1, 0, 1, -1, 1, -1, 0, 1];

            let visited = Array(gridSize).fill().map(() => Array(gridSize).fill(false));
            visited[currentX][currentY] = true;

            let found = false;
            let steps = 0;
            let maxSteps = gridSize * gridSize;

            while (steps < maxSteps) {
                steps++;

                // Find neighbor with smallest distance to goal
                let bestX = -1;
                let bestY = -1;
                let bestDist = Infinity;

                for (let k = 0; k < 8; k++) {
                    let nx = currentX + dx[k];
                    let ny = currentY + dy[k];

                    // Check bounds
                    if (nx < 0 || nx >= gridSize || ny < 0 || ny >= gridSize) continue;

                    // Check if it's an obstacle or already visited
                    if (grid[nx][ny] === 1 || visited[nx][ny]) continue;

                    // Check if it's better than current best
                    if (distanceField[nx][ny] < bestDist) {
                        bestX = nx;
                        bestY = ny;
                        bestDist = distanceField[nx][ny];
                    }
                }

                // No valid neighbors found
                if (bestX === -1) break;

                // Move to best neighbor
                currentX = bestX;
                currentY = bestY;
                visited[currentX][currentY] = true;

                // Add to path
                path.push({ 
                    q1: gridCoordToAngle(currentX), 
                    q2: gridCoordToAngle(currentY) 
                });

                // Check if we reached the goal
                if (currentX === goalX && currentY === goalY) {
                    found = true;
                    break;
                }
            }

            // Calculate path length
            let pathLength = 0;
            if (path.length > 1) {
                for (let i = 1; i < path.length; i++) {
                    let dx = path[i].q1 - path[i-1].q1;
                    let dy = path[i].q2 - path[i-1].q2;
                    pathLength += Math.sqrt(dx*dx + dy*dy);
                }
            }

            updatePathLength(pathLength);
        }

        function updatePathLength(length) {
            document.getElementById('phase-space-title').textContent = 
                `robot phase space, path length = ${length.toFixed(2)} rads`;
        }

        // Initialize p5.js sketches
        new p5(workspaceSketch);
        new p5(phaseSpaceSketch);

        // Event listeners for controls
        document.getElementById('slider-q').addEventListener('input', function() {
            qValue = parseInt(this.value);
            document.getElementById('value-q').textContent = qValue;
            recalculateAll();
        });

        document.getElementById('slider-p').addEventListener('input', function() {
            pValue = parseFloat(this.value);
            document.getElementById('value-p').textContent = pValue.toFixed(2);
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            // Reset sliders
            qValue = 2;
            pValue = 1;
            document.getElementById('slider-q').value = qValue;
            document.getElementById('slider-p').value = pValue;
            document.getElementById('value-q').textContent = qValue;
            document.getElementById('value-p').textContent = pValue.toFixed(2);

            // Reset configurations
            startConfig = { q1: -1.5, q2: 0.5 };
            goalConfig = { q1: 1.5, q2: 1.0 };

            // Reset obstacles
            obstacles = [
                { x: 200, y: 100, radius: 15 },
                { x: 200, y: 300, radius: 15 }
            ];

            // Recalculate everything
            recalculateAll();
        });

        // Initial calculation
        recalculateAll();
    </script>
</body>
</html>
