                }
                canvas.parent('canvas-container');

                // Generate initial points
                generatePoints();
                resetAlgorithm();
            };

            p.draw = function() {
                p.background(224); // Light gray background

                if (state.dimension === '2D') {
                    draw2D();
                } else {
                    draw3D();
                }

                // Handle animation if playing
                if (state.animationState === 'playing') {
                    if (p.frameCount % 10 === 0) { // Update every 10 frames
                        stepAnimation();
                    }
                }
            };

            function draw2D() {
                // Draw MST edges
                p.stroke('red');
                p.strokeWeight(2);
                for (let i = 0; i < state.animationStep && i < state.mstEdges.length; i++) {
                    const edge = state.mstEdges[i];
                    p.line(edge.a.x, edge.a.y, edge.b.x, edge.b.y);
                }

                // Draw points
                p.fill('blue');
                p.noStroke();
                for (let point of state.points) {
                    p.ellipse(point.x, point.y, 8, 8);
                }
            }

            function draw3D() {
                p.orbitControl();
                p.rotateX(p.PI/4);
                p.rotateY(p.PI/4);

                // Draw wireframe cube
                p.stroke(200);
                p.noFill();
                p.box(400);

                // Draw MST edges
                p.stroke('red');
                p.strokeWeight(2);
                for (let i = 0; i < state.animationStep && i < state.mstEdges.length; i++) {
                    const edge = state.mstEdges[i];
                    p.line(edge.a.x, edge.a.y, edge.a.z, edge.b.x, edge.b.y, edge.b.z);
                }

                // Draw points
                p.fill('blue');
                p.noStroke();
                for (let point of state.points) {
                    p.push();
                    p.translate(point.x, point.y, point.z);
                    p.sphere(5);
                    p.pop();
                }
            }

            function generatePoints() {
                p.randomSeed(state.seed);
                state.points = [];

                const margin = 50;
                const width = state.dimension === '2D' ? state.canvasWidth : 400;
                const height = state.dimension === '2D' ? state.canvasHeight : 400;
                const depth = state.dimension === '2D' ? 0 : 400;

                for (let i = 0; i < state.numPoints; i++) {
                    const x = p.random(-width/2 + margin, width/2 - margin);
                    const y = p.random(-height/2 + margin, height/2 - margin);
                    const z = state.dimension === '2D' ? 0 : p.random(-depth/2 + margin, depth/2 - margin);

                    state.points.push(p.createVector(x, y, z));
                }
            }

            function resetAlgorithm() {
                state.animationStep = 0;
                state.mstEdges = [];
                state.animationState = 'stopped';

                // Pre-calculate all possible edges for Kruskal
                state.allEdges = [];
                for (let i = 0; i < state.points.length; i++) {
                    for (let j = i + 1; j < state.points.length; j++) {
                        const a = state.points[i];
                        const b = state.points[j];
                        const dist = p.dist(a.x, a.y, a.z, b.x, b.y, b.z);
                        state.allEdges.push({ a, b, dist });
                    }
                }

                // Sort edges by distance for Kruskal
                state.allEdges.sort((a, b) => a.dist - b.dist);

                // Pre-calculate MST steps based on algorithm
                if (state.algorithm === 'prim') {
                    calculatePrimSteps();
                } else {
                    calculateKruskalSteps();
                }

                updateButtonStates();
            }

            function calculatePrimSteps() {
                state.mstEdges = [];
                const visited = new Set();
                const edges = [];

                // Start with first point
                if (state.points.length === 0) return;
                visited.add(0);

                // Add all edges from first point
                for (let i = 1; i < state.points.length; i++) {
                    edges.push({
                        a: state.points[0],
                        b: state.points[i],
                        dist: p.dist(state.points[0].x, state.points[0].y, state.points[0].z,
                                     state.points[i].x, state.points[i].y, state.points[i].z)
                    });
                }

                // Sort edges
                edges.sort((a, b) => a.dist - b.dist);

                // Prim's algorithm
                while (visited.size < state.points.length && edges.length > 0) {
                    // Find the shortest edge to an unvisited node
                    let minEdge = null;
                    let minIndex = -1;

                    for (let i = 0; i < edges.length; i++) {
                        const edge = edges[i];
                        const aVisited = visited.has(state.points.indexOf(edge.a));
                        const bVisited = visited.has(state.points.indexOf(edge.b));

                        if (aVisited !== bVisited) {
                            minEdge = edge;
                            minIndex = i;
                            break;
                        }
                    }

                    if (!minEdge) break;

                    // Add the edge to MST
                    state.mstEdges.push(minEdge);
                    edges.splice(minIndex, 1);

                    // Add the new point to visited
                    const newPoint = visited.has(state.points.indexOf(minEdge.a)) ? minEdge.b : minEdge.a;
                    const newIndex = state.points.indexOf(newPoint);
                    visited.add(newIndex);

                    // Add edges from the new point
                    for (let i = 0; i < state.points.length; i++) {
                        if (!visited.has(i) && i !== newIndex) {
                            edges.push({
                                a: newPoint,
                                b: state.points[i],
                                dist: p.dist(newPoint.x, newPoint.y, newPoint.z,
                                             state.points[i].x, state.points[i].y, state.points[i].z)
                            });
                        }
                    }

                    // Re-sort edges
                    edges.sort((a, b) => a.dist - b.dist);
                }
            }

            function calculateKruskalSteps() {
                state.mstEdges = [];
                const parent = {};

                // Initialize parent pointers
                for (let i = 0; i < state.points.length; i++) {
                    parent[i] = i;
                }

                function find(u) {
                    if (parent[u] !== u) {
                        parent[u] = find(parent[u]);
                    }
                    return parent[u];
                }

                function union(u, v) {
                    const rootU = find(u);
                    const rootV = find(v);
                    if (rootU !== rootV) {
                        parent[rootV] = rootU;
                        return true;
                    }
                    return false;
                }

                // Kruskal's algorithm
                for (let edge of state.allEdges) {
                    const u = state.points.indexOf(edge.a);
                    const v = state.points.indexOf(edge.b);

                    if (union(u, v)) {
                        state.mstEdges.push(edge);
                    }

                    // Stop when we have enough edges
                    if (state.mstEdges.length >= state.points.length - 1) {
                        break;
                    }
                }
            }

            function stepAnimation() {
                if (state.animationStep < state.mstEdges.length) {
                    state.animationStep++;
                } else {
                    state.animationState = 'stopped';
                    updateButtonStates();
                }
            }

            function updateButtonStates() {
                elements.btnPlay.disabled = state.animationState === 'playing';
                elements.btnPause.disabled = state.animationState !== 'playing';

                // Disable play button if animation is complete
                if (state.animationStep >= state.mstEdges.length && state.mstEdges.length > 0) {
                    elements.btnPlay.disabled = true;
                }
            }

            function handleReset() {
                generatePoints();
                resetAlgorithm();
            }
        };

        // Create p5 instance
        new p5(sketch);

        // Event listeners
        elements.btn2d.addEventListener('click', function() {
