                elements.btn2d.classList.add('active');
                elements.btn3d.classList.remove('active');
                handleReset();
            }
        });

        elements.btn3d.addEventListener('click', function() {
            if (state.dimension !== '3D') {
                state.dimension = '3D';
                elements.btn3d.classList.add('active');
                elements.btn2d.classList.remove('active');
                handleReset();
            }
        });

        elements.sliderSeed.addEventListener('input', function() {
            state.seed = parseInt(this.value);
            elements.seedValue.textContent = state.seed;
            handleReset();
        });

        elements.sliderNumber.addEventListener('input', function() {
            state.numPoints = parseInt(this.value);
            elements.numberValue.textContent = state.numPoints;
            handleReset();
        });

        elements.btnPrim.addEventListener('click', function() {
            if (state.algorithm !== 'prim') {
                state.algorithm = 'prim';
                elements.btnPrim.classList.add('active');
                elements.btnKruskal.classList.remove('active');
                resetAlgorithm();
            }
        });

        elements.btnKruskal.addEventListener('click', function() {
            if (state.algorithm !== 'kruskal') {
                state.algorithm = 'kruskal';
                elements.btnKruskal.classList.add('active');
                elements.btnPrim.classList.remove('active');
                resetAlgorithm();
            }
        });

        elements.btnPlay.addEventListener('click', function() {
            state.animationState = 'playing';
            updateButtonStates();
        });

        elements.btnPause.addEventListener('click', function() {
            state.animationState = 'paused';
            updateButtonStates();
        });

        elements.btnReset.addEventListener('click', function() {
            resetAlgorithm();
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const container = document.getElementById('canvas-container');
            state.canvasWidth = container.clientWidth;
            state.canvasHeight = container.clientHeight;

            if (sketch) {
                sketch.resizeCanvas(state.canvasWidth, state.canvasHeight);
                if (state.dimension === '3D') {
                    sketch.createCanvas(state.canvasWidth, state.canvasHeight, sketch.WEBGL);
                } else {
                    sketch.createCanvas(state.canvasWidth, state.canvasHeight);
                }
                handleReset();
            }
        });
    </script>
</body>
</html>
