            <p id="label-right">no change in the molecular dipole moment</p>
        </div>
    </div>

    <script>
        // Global variables
        let scene, moleculeScene, vectorScene;
        let moleculeCamera, vectorCamera;
        let renderer;
        let moleculeControls, vectorControls;
        let animationId = null;
        let animationTime = 0;
        let animationSpeed = 0.5;
        let currentMode = 'symmetric-stretch';

        // Molecule components
        let carbonAtom, hydrogenAtoms = [], bonds = [];
        // Vector components
        let bondVectors = [], netDipoleVector = null;

        // Initialize the application
        document.addEventListener('DOMContentLoaded', function() {
            initThreeJS();
            initUI();
            createIRSpectrum();
        });

        function initThreeJS() {
            // Create renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(1200, 500);
            document.getElementById('three-js-canvas-container').appendChild(renderer.domElement);

            // Create scenes
            moleculeScene = new THREE.Scene();
            vectorScene = new THREE.Scene();

            // Create cameras
            moleculeCamera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
            moleculeCamera.position.set(5, 5, 5);

            vectorCamera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
            vectorCamera.position.set(0, 0, 5);

            // Add lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            moleculeScene.add(ambientLight);
            vectorScene.add(ambientLight.clone());

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1);
            moleculeScene.add(directionalLight);
            vectorScene.add(directionalLight.clone());

            // Add axes helper to vector scene
            const axesHelper = new THREE.AxesHelper(2);
            vectorScene.add(axesHelper);

            // Create methane molecule
            createMethaneMolecule();

            // Create controls
            moleculeControls = new THREE.OrbitControls(moleculeCamera, renderer.domElement);
            moleculeControls.enableDamping = true;
            moleculeControls.dampingFactor = 0.25;

            vectorControls = new THREE.OrbitControls(vectorCamera, renderer.domElement);
            vectorControls.enableDamping = true;
            vectorControls.dampingFactor = 0.25;

            // Start animation loop
            animate();
        }

        function createMethaneMolecule() {
            // Carbon atom (black sphere at origin)
            const carbonGeometry = new THREE.SphereGeometry(0.4, 32, 32);
            const carbonMaterial = new THREE.MeshLambertMaterial({ color: 0x000000 });
            carbonAtom = new THREE.Mesh(carbonGeometry, carbonMaterial);
            moleculeScene.add(carbonAtom);

            // Hydrogen atoms (light blue spheres at tetrahedral positions)
            const hydrogenGeometry = new THREE.SphereGeometry(0.3, 32, 32);
            const hydrogenMaterial = new THREE.MeshLambertMaterial({ color: 0xadd8e6 });

            // Tetrahedral positions (normalized)
            const positions = [
                new THREE.Vector3(1, 1, 1).normalize(),
                new THREE.Vector3(1, -1, -1).normalize(),
                new THREE.Vector3(-1, 1, -1).normalize(),
                new THREE.Vector3(-1, -1, 1).normalize()
            ];

            // Create hydrogen atoms and bonds
            for (let i = 0; i < 4; i++) {
                const hydrogen = new THREE.Mesh(hydrogenGeometry, hydrogenMaterial);
                hydrogen.position.copy(positions[i]);
                moleculeScene.add(hydrogen);
                hydrogenAtoms.push(hydrogen);

                // Create bond (cylinder between carbon and hydrogen)
                const bond = new THREE.Mesh(
                    new THREE.CylinderGeometry(0.1, 0.1, 1, 8),
                    new THREE.MeshBasicMaterial({ color: 0x888888 })
                );
                updateBondPosition(bond, carbonAtom.position, hydrogen.position);
                moleculeScene.add(bond);
                bonds.push(bond);

                // Create bond vector (in vector scene)
                const dir = new THREE.Vector3().subVectors(hydrogen.position, carbonAtom.position).normalize();
                const origin = new THREE.Vector3();
                const length = 1;
                const hex = 0x0000ff;
                const arrowHelper = new THREE.ArrowHelper(dir, origin, length, hex, 0.2, 0.1);
                vectorScene.add(arrowHelper);
                bondVectors.push(arrowHelper);
            }

            // Create net dipole vector (initially hidden)
            netDipoleVector = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(0, 0, 0),
                0,
                0xff0000,
                0.3,
                0.15
            );
            vectorScene.add(netDipoleVector);
        }

        function updateBondPosition(bond, start, end) {
            const middle = new THREE.Vector3().addVectors(start, end).multiplyScalar(0.5);
            bond.position.copy(middle);

            const direction = new THREE.Vector3().subVectors(end, start);
            const distance = direction.length();
            bond.scale.set(1, distance, 1);

            bond.quaternion.setFromUnitVectors(
                new THREE.Vector3(0, 1, 0),
                direction.clone().normalize()
            );
        }

        function animate() {
            requestAnimationFrame(animate);

            // Update controls
            moleculeControls.update();
            vectorControls.update();

            // Set viewports for side-by-side rendering
            const width = renderer.domElement.width;
            const height = renderer.domElement.height;

            // Left viewport (molecule)
            renderer.setViewport(0, 0, width / 2, height);
            renderer.setScissor(0, 0, width / 2, height);
            renderer.setScissorTest(true);
            renderer.render(moleculeScene, moleculeCamera);

            // Right viewport (vectors)
            renderer.setViewport(width / 2, 0, width / 2, height);
            renderer.setScissor(width / 2, 0, width / 2, height);
            renderer.setScissorTest(true);
            renderer.render(vectorScene, vectorCamera);

            // Reset renderer settings
            renderer.setViewport(0, 0, width, height);
            renderer.setScissorTest(false);

            // Update animation if playing
            if (animationId !== null) {
                updateAnimation();
            }
        }

        function updateAnimation() {
            animationTime += animationSpeed * 0.01;

            // Get displacement vectors for current mode
            const displacements = getDisplacementVectors(currentMode, animationTime);

            // Update hydrogen atom positions and bonds
            for (let i = 0; i < 4; i++) {
                const initialPos = hydrogenAtoms[i].position.clone().normalize();
                const displacement = displacements[i];

                // Update hydrogen position
                hydrogenAtoms[i].position.copy(initialPos).add(displacement);

                // Update bond position
                updateBondPosition(bonds[i], carbonAtom.position, hydrogenAtoms[i].position);

                // Update bond vector
                const bondDir = new THREE.Vector3().subVectors(hydrogenAtoms[i].position, carbonAtom.position).normalize();
                bondVectors[i].setDirection(bondDir);
                bondVectors[i].setLength(1 + displacement.length() * 2);
            }

            // Calculate and update net dipole vector
            const netDipole = new THREE.Vector3();
            for (let i = 0; i < 4; i++) {
                const bondDir = new THREE.Vector3().subVectors(hydrogenAtoms[i].position, carbonAtom.position).normalize();
                netDipole.add(bondDir.multiplyScalar(bondVectors[i].getLength()));
            }

            const dipoleMagnitude = netDipole.length();
            if (dipoleMagnitude > 0.1) {
                netDipoleVector.setDirection(netDipole.normalize());
                netDipoleVector.setLength(dipoleMagnitude * 0.5);
            } else {
                netDipoleVector.setLength(0);
            }
        }

        function getDisplacementVectors(mode, time) {
            const displacements = [];
            const amplitude = 0.2;

            switch (mode) {
                case 'symmetric-stretch':
                    // All H atoms move radially in unison
                    for (let i = 0; i < 4; i++) {
                        const dir = hydrogenAtoms[i].position.clone().normalize();
                        displacements.push(dir.multiplyScalar(amplitude * Math.sin(time)));
                    }
                    break;

                case 'asymmetric-stretch':
                    // One bond stretches while another compresses
                    displacements.push(
                        hydrogenAtoms[0].position.clone().normalize().multiplyScalar(amplitude * Math.sin(time)),
                        hydrogenAtoms[1].position.clone().normalize().multiplyScalar(-amplitude * Math.sin(time)),
                        hydrogenAtoms[2].position.clone().normalize().multiplyScalar(amplitude * 0.5 * Math.sin(time)),
                        hydrogenAtoms[3].position.clone().normalize().multiplyScalar(-amplitude * 0.5 * Math.sin(time))
                    );
                    break;

                case 'bending-e':
                    // H atoms move tangentially (bending motion)
                    for (let i = 0; i < 4; i++) {
                        const dir = hydrogenAtoms[i].position.clone().normalize();
                        const tangent = new THREE.Vector3(dir.y, -dir.x, 0).normalize();
                        displacements.push(tangent.multiplyScalar(amplitude * Math.sin(time)));
                    }
                    break;

                case 'bending-f2':
                    // More complex bending motion
                    displacements.push(
                        new THREE.Vector3(0, amplitude * Math.sin(time), 0),
                        new THREE.Vector3(0, -amplitude * Math.sin(time), 0),
                        new THREE.Vector3(amplitude * Math.sin(time), 0, 0),
                        new THREE.Vector3(-amplitude * Math.sin(time), 0, 0)
                    );
                    break;

                default:
                    // No displacement
                    for (let i = 0; i < 4; i++) {
                        displacements.push(new THREE.Vector3(0, 0, 0));
                    }
            }

            return displacements;
        }

        function resetAnimation() {
            // Reset time
            animationTime = 0;

            // Reset hydrogen positions to initial tetrahedral positions
            const positions = [
                new THREE.Vector3(1, 1, 1).normalize(),
                new THREE.Vector3(1, -1, -1).normalize(),
                new THREE.Vector3(-1, 1, -1).normalize(),
                new THREE.Vector3(-1, -1, 1).normalize()
            ];

            for (let i = 0; i < 4; i++) {
                hydrogenAtoms[i].position.copy(positions[i]);
                updateBondPosition(bonds[i], carbonAtom.position, hydrogenAtoms[i].position);

                // Reset bond vectors
                const dir = new THREE.Vector3().subVectors(hydrogenAtoms[i].position, carbonAtom.position).normalize();
                bondVectors[i].setDirection(dir);
                bondVectors[i].setLength(1);
            }

            // Hide net dipole vector
            netDipoleVector.setLength(0);
        }

        function createIRSpectrum() {
            // Generate x-axis (wavenumber)
            const x = [];
            for (let i = 400; i <= 4000; i += 10) {
                x.push(i);
            }

            // Generate baseline with noise
            const y = x.map(v => {
                // Baseline at ~0.95 with some noise
                let value = 0.95 + (Math.random() - 0.5) * 0.05;

                // Add absorption peaks
                // Peak at 1306 cm⁻¹ (F₂ bend)
                value -= 0.8 * Math.exp(-Math.pow((v - 1306) / 30, 2));

                // Peak at 3019 cm⁻¹ (F₂ stretch)
                value -= 0.8 * Math.exp(-Math.pow((v - 3019) / 30, 2));

                // Add rotational fine structure (smaller peaks near main peaks)
                if (v > 1200 && v < 1400) {
                    value -= 0.1 * Math.exp(-Math.pow((v - 1306) / 10, 2));
                    value -= 0.05 * Math.exp(-Math.pow((v - 1280) / 5, 2));
                    value -= 0.05 * Math.exp(-Math.pow((v - 1330) / 5, 2));
                }

                if (v > 2900 && v < 3100) {
                    value -= 0.1 * Math.exp(-Math.pow((v - 3019) / 10, 2));
                    value -= 0.05 * Math.exp(-Math.pow((v - 2990) / 5, 2));
                    value -= 0.05 * Math.exp(-Math.pow((v - 3040) / 5, 2));
                }

                return Math.max(0, Math.min(1, value));
            });

            // Create plot
            const trace = {
                x: x,
                y: y,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2 }
            };

            const layout = {
                title: 'Methane IR Spectrum',
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [400, 4000]
                },
                yaxis: {
                    title: 'Transmittance',
                    range: [0, 1.1]
                },
                annotations: [
                    {
                        x: 1306,
                        y: 0.15,
                        xref: 'x',
                        yref: 'y',
                        text: 'ν F₂ 1306 cm⁻¹',
                        showarrow: true,
                        arrowhead: 2,
                        ax: 0,
                        ay: -40,
                        font: { color: 'red' }
                    },
                    {
                        x: 3019,
                        y: 0.15,
                        xref: 'x',
                        yref: 'y',
                        text: 'ν F₂ 3019 cm⁻¹',
                        showarrow: true,
                        arrowhead: 2,
                        ax: 0,
                        ay: -40,
                        font: { color: 'red' }
                    }
                ]
            };

            Plotly.newPlot('plotly-chart-container', [trace], layout);
        }

        function initUI() {
            // Mode selection dropdown
            const modeSelect = document.getElementById('select-vibration-mode');
            modeSelect.addEventListener('change', function() {
                currentMode = this.value;

                if (currentMode === 'ir-spectrum') {
                    // Show spectrum, hide 3D view
                    document.getElementById('three-js-canvas-container').style.display = 'none';
                    document.getElementById('description-area').style.display = 'none';
                    document.getElementById('plotly-chart-container').style.display = 'block';

                    // Disable animation controls
                    document.getElementById('btn-play').disabled = true;
                    document.getElementById('btn-pause').disabled = true;
                    document.getElementById('btn-reset').disabled = true;

                    // Stop any running animation
                    if (animationId !== null) {
                        cancelAnimationFrame(animationId);
                        animationId = null;
                    }
                } else {
                    // Show 3D view, hide spectrum
                    document.getElementById('three-js-canvas-container').style.display = 'flex';
                    document.getElementById('description-area').style.display = 'flex';
                    document.getElementById('plotly-chart-container').style.display = 'none';

                    // Enable animation controls
                    document.getElementById('btn-play').disabled = false;
                    document.getElementById('btn-pause').disabled = false;
                    document.getElementById('btn-reset').disabled = false;

                    // Reset animation
                    resetAnimation();

                    // Update labels based on selected mode
                    updateLabels();
                }
            });

            // Play button
            document.getElementById('btn-play').addEventListener('click', function() {
                if (animationId === null) {
                    animationId = requestAnimationFrame(function animate() {
                        updateAnimation();
                        animationId = requestAnimationFrame(animate);
                    });
                }
            });

            // Pause button
            document.getElementById('btn-pause').addEventListener('click', function() {
                if (animationId !== null) {
                    cancelAnimationFrame(animationId);
                    animationId = null;
                }
            });

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', function() {
                if (animationId !== null) {
                    cancelAnimationFrame(animationId);
                    animationId = null;
                }
                resetAnimation();
            });
        }

        function updateLabels() {
            const leftLabel = document.getElementById('label-left');
            const rightLabel = document.getElementById('label-right');

            switch (currentMode) {
                case 'symmetric-stretch':
                    leftLabel.textContent = 'stretching A₁ Raman active 2916 cm⁻¹';
                    leftLabel.style.backgroundColor = 'rgba(0, 0, 255, 0.1)';
                    leftLabel.style.color = 'blue';
                    rightLabel.textContent = 'no change in the molecular dipole moment';
                    rightLabel.style.backgroundColor = 'rgba(0, 0, 255, 0.1)';
                    rightLabel.style.color = 'blue';
                    break;

                case 'asymmetric-stretch':
                    leftLabel.textContent = 'stretching F₂ IR active 3019 cm⁻¹';
                    leftLabel.style.backgroundColor = 'rgba(255, 0, 0, 0.1)';
                    leftLabel.style.color = 'red';
                    rightLabel.textContent = 'change in the molecular dipole moment';
                    rightLabel.style.backgroundColor = 'rgba(255, 0, 0, 0.1)';
                    rightLabel.style.color = 'red';
                    break;

                case 'bending-e':
                    leftLabel.textContent = 'bending E Raman active 1534 cm⁻¹';
                    leftLabel.style.backgroundColor = 'rgba(0, 0, 255, 0.1)';
                    leftLabel.style.color = 'blue';
                    rightLabel.textContent = 'no change in the molecular dipole moment';
                    rightLabel.style.backgroundColor = 'rgba(0, 0, 255, 0.1)';
                    rightLabel.style.color = 'blue';
                    break;

                case 'bending-f2':
                    leftLabel.textContent = 'bending F₂ IR active 1306 cm⁻¹';
                    leftLabel.style.backgroundColor = 'rgba(255, 0, 0, 0.1)';
                    leftLabel.style.color = 'red';
                    rightLabel.textContent = 'change in the molecular dipole moment';
                    rightLabel.style.backgroundColor = 'rgba(255, 0, 0, 0.1)';
                    rightLabel.style.color = 'red';
                    break;
            }
        }

        // Handle window resize
        window.addEventListener('resize', function() {
            if (renderer) {
                const width = window.innerWidth * 0.8;
                const height = 500;
                renderer.setSize(width, height);

                moleculeCamera.aspect = (width / 2) / height;
                moleculeCamera.updateProjectionMatrix();

                vectorCamera.aspect = (width / 2) / height;
                vectorCamera.updateProjectionMatrix();
            }
        });
    </script>
</body>
</html>
