<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time (seconds)</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset">+</button>
        </div>

        <div id="material-visualization">
            <canvas id="canvas-material-bar"></canvas>
        </div>

        <div id="plot-area">
            <div id="div-plot"></div>
        </div>
    </div>

    <script>
        // Constants
        const TOTAL_WIDTH_MM = 16;
        const GLASS_WIDTH_MM = 6;
        const CANVAS_WIDTH = 600;
        const PIXELS_PER_MM = CANVAS_WIDTH / TOTAL_WIDTH_MM;

        // Stefan distance function
        function stefanDistance(t) {
            return 0.155 * Math.sqrt(t);
        }

        // Initialize Plotly chart
        function initPlot() {
            // Generate data for the curve
            const xValues = [];
            const yValues = [];
            for (let t = 0; t <= 100; t += 0.5) {
                xValues.push(t);
                yValues.push(stefanDistance(t));
            }

            // Create the initial trace
            const trace = {
                x: xValues,
                y: yValues,
                type: 'scatter',
                mode: 'lines',
                line: { color: '#FFA500', width: 2 },
                name: 'Stefan distance'
            };

            // Initial layout with indicator line at t=9.8
            const initialTime = parseFloat(document.getElementById('slider-time').value);
            const initialDistance = stefanDistance(initialTime);

            const layout = {
                title: 'Stefan distance versus time',
                xaxis: { title: 'time (s)', range: [0, 100] },
                yaxis: { title: 'distance (mm)', range: [0, 1.6] },
                shapes: [{
                    type: 'line',
                    x0: initialTime,
                    y0: 0,
                    x1: initialTime,
                    y1: initialDistance,
                    line: { color: '#000000', width: 2 }
                }]
            };

            // Create the plot
            Plotly.newPlot('div-plot', [trace], layout);
        }

        // Draw material visualization
        function drawMaterialBar(t) {
            const canvas = document.getElementById('canvas-material-bar');
            const ctx = canvas.getContext('2d');
            const s = stefanDistance(t);

            // Clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw glass block (6mm wide)
            const glassWidthPx = GLASS_WIDTH_MM * PIXELS_PER_MM;
            const gradient = ctx.createLinearGradient(0, 0, glassWidthPx, 0);
            gradient.addColorStop(0, '#FF0000');
            gradient.addColorStop(1, '#FFFFFF');

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 10, glassWidthPx, 40);

            // Draw water layer (s(t) mm wide)
            const waterLeftPx = glassWidthPx;
            const waterWidthPx = s * PIXELS_PER_MM;

            ctx.fillStyle = '#00BFFF';
            ctx.fillRect(waterLeftPx, 10, waterWidthPx, 40);

            // Draw ice block (remaining width)
            const iceLeftPx = waterLeftPx + waterWidthPx;
            const iceWidthPx = CANVAS_WIDTH - iceLeftPx;

            ctx.fillStyle = '#003366';
            ctx.fillRect(iceLeftPx, 10, iceWidthPx, 40);

            // Draw axis
            ctx.strokeStyle = '#000000';
            ctx.lineWidth = 1;
            ctx.beginPath();
            ctx.moveTo(0, 70);
            ctx.lineTo(CANVAS_WIDTH, 70);
            ctx.stroke();

            // Draw axis labels
            ctx.font = '12px Arial';
            ctx.fillStyle = '#000000';
            ctx.textAlign = 'center';

            [0, 5, 10, 15].forEach(pos => {
                const x = pos * PIXELS_PER_MM;
                ctx.fillText(pos.toString(), x, 85);
                ctx.beginPath();
                ctx.moveTo(x, 70);
                ctx.lineTo(x, 74);
                ctx.stroke();
            });
        }

        // Update all visualizations based on current time
        function updateVisualizations(t) {
            // Update time display
            document.getElementById('span-time-value').textContent = t.toFixed(1);

            // Update material visualization
            drawMaterialBar(t);

            // Update plot indicator line
            const s = stefanDistance(t);
            Plotly.relayout('div-plot', {
                shapes: [{
                    type: 'line',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: s,
                    line: { color: '#000000', width: 2 }
                }]
            });
        }

        // Initialize the page
        function init() {
            // Set up slider event listener
            const slider = document.getElementById('slider-time');
            slider.addEventListener('input', function() {
                const t = parseFloat(this.value);
                updateVisualizations(t);
            });

            // Set up reset button
            document.getElementById('btn-reset').addEventListener('click', function() {
                slider.value = 0;
                slider.dispatchEvent(new Event('input'));
            });

            // Initialize visualizations
            const initialTime = parseFloat(slider.value);
            initPlot();
            updateVisualizations(initialTime);
        }

        // Start the application when the page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
