</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" step="1" value="340">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>

        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper" class="clock-wrapper">
                        <canvas id="local-clock-canvas"></canvas>
                    </div>
                    <div id="local-time-digital" class="digital-display">00:00:00</div>
                </div>

                <div id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper" class="clock-wrapper">
                        <canvas id="solar-clock-canvas"></canvas>
                    </div>
                    <div id="solar-time-digital" class="digital-display">00:00:00</div>
                </div>
            </div>

            <div id="offset-display">
                offset of solar time from local time: -46 minutes
            </div>
        </div>
    </div>

    <script>
        // User location variables with defaults (Greenwich, London)
        let userLatitude = 51.5;
        let userLongitude = 0;

        // Get user location if permitted
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    userLatitude = position.coords.latitude;
                    userLongitude = position.coords.longitude;
                },
                error => {
                    console.log("Geolocation error:", error);
                }
            );
        }

        // Date handling
        const dateSlider = document.getElementById('date-slider');
        const dateDisplay = document.getElementById('date-display');
        const todayButton = document.getElementById('today-button');

        // Update date display based on slider value
        function updateDateDisplay(dayOfYear) {
            const date = new Date(2023, 0); // Using 2023 as a non-leap year
            date.setDate(dayOfYear + 1); // Add 1 because day 0 is Jan 1

            const options = { weekday: 'long', day: 'numeric', month: 'long' };
            dateDisplay.textContent = date.toLocaleDateString('en-US', options);
        }

        // Set slider to today's date
        function setToToday() {
            const today = new Date();
            const start = new Date(today.getFullYear(), 0, 0);
            const diff = today - start;
            const oneDay = 1000 * 60 * 60 * 24;
            const dayOfYear = Math.floor(diff / oneDay) - 1; // 0-364

            dateSlider.value = dayOfYear;
            updateDateDisplay(dayOfYear);
        }

        // Event listeners
        dateSlider.addEventListener('input', () => {
            updateDateDisplay(parseInt(dateSlider.value));
        });

        todayButton.addEventListener('click', setToToday);

        // Initialize date display
        updateDateDisplay(parseInt(dateSlider.value));

        // Clock drawing with p5.js
        let localClock;
        let solarClock;

        function setup() {
            localClock = new p5(localClockSketch, 'local-clock-wrapper');
            solarClock = new p5(solarClockSketch, 'solar-clock-wrapper');
        }

        // Local clock sketch
        const localClockSketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(200, 200);
                canvas.parent('local-clock-wrapper');
                p.angleMode(p.DEGREES);
            };

            p.draw = function() {
                const now = new Date();
                drawClock(p, now, '#E6E6FA');

                // Update digital display
                document.getElementById('local-time-digital').textContent = 
                    now.toLocaleTimeString('en-US', {hour12: false});
            };
        };

        // Solar clock sketch
        const solarClockSketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(200, 200);
                canvas.parent('solar-clock-wrapper');
                p.angleMode(p.DEGREES);
            };

            p.draw = function() {
                const solarTime = calculateSolarTime();
                drawClock(p, solarTime, '#FAFAD2');

                // Update digital display
                document.getElementById('solar-time-digital').textContent = 
                    solarTime.toLocaleTimeString('en-US', {hour12: false});

                // Update offset display
                const now = new Date();
                const offsetMinutes = Math.round((solarTime.getTime() - now.getTime()) / 60000);
                document.getElementById('offset-display').textContent = 
                    `offset of solar time from local time: ${offsetMinutes} minutes`;
            };
        };

        // Shared clock drawing function
        function drawClock(p, time, faceColor) {
            p.background(255);

            // Clock face
            p.translate(p.width/2, p.height/2);
            p.stroke(0);
            p.strokeWeight(2);
            p.fill(faceColor);
            p.ellipse(0, 0, 180, 180);

            // Hour marks
            p.strokeWeight(3);
            for (let i = 0; i < 12; i++) {
                p.push();
                p.rotate(i * 30);
                p.line(0, -70, 0, -80);
                p.pop();
            }

            // Minute marks
            p.strokeWeight(1);
            for (let i = 0; i < 60; i++) {
                if (i % 5 !== 0) { // Skip hour marks
                    p.push();
                    p.rotate(i * 6);
                    p.line(0, -75, 0, -80);
                    p.pop();
                }
            }

            // Numbers
            p.textSize(16);
            p.textAlign(p.CENTER, p.CENTER);
            p.fill(0);
            for (let i = 1; i <= 12; i++) {
                const angle = i * 30 - 60;
                const x = 60 * p.cos(angle);
                const y = 60 * p.sin(angle);
                p.text(i, x, y);
            }

            // Get time components
            const hours = time.getHours() % 12;
            const minutes = time.getMinutes();
            const seconds = time.getSeconds();

            // Hour hand
            p.push();
            p.rotate(hours * 30 + minutes * 0.5);
            p.stroke(0);
            p.strokeWeight(6);
            p.line(0, 0, 0, -40);
            p.pop();

            // Minute hand
            p.push();
            p.rotate(minutes * 6 + seconds * 0.1);
            p.stroke(0);
            p.strokeWeight(4);
            p.line(0, 0, 0, -60);
            p.pop();

            // Second hand
            p.push();
            p.rotate(seconds * 6);
            p.stroke(255, 0, 0);
            p.strokeWeight(2);
            p.line(0, 10, 0, -70);
            p.pop();

            // Center pin
            p.fill(255, 0, 0);
            p.ellipse(0, 0, 8, 8);
        }

        // Calculate solar time
        function calculateSolarTime() {
            const dayOfYear = parseInt(dateSlider.value);
            const localTime = new Date();

            // Equation of Time calculation
            const B = (360 / 365.24) * (dayOfYear - 81);
            const EoT = 9.87 * Math.sin(2 * B * Math.PI / 180) - 
                        7.53 * Math.cos(B * Math.PI / 180) - 
                        1.5 * Math.sin(B * Math.PI / 180);

            // Time offset calculation
            const timeOffsetMinutes = EoT + (4 * userLongitude);

            // Calculate solar time
            const UTCTime = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
            const solarTimeMilliseconds = UTCTime + timeOffsetMinutes * 60000;
            return new Date(solarTimeMilliseconds);
        }

        // Initialize p5 sketches when page loads
        window.onload = setup;
    </script>
</body>
</html>
