</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let sketch = function(p) {
            let steps = 2;
            let zoom = false;
            let colorScheme = "DarkRainbow";
            let colorPalettes = {
                DarkRainbow: ['#1E4363', '#2E6341'],
                SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],
                RustTones: ['#B7410E', '#000033', '#FFFFFF'],
                FuchsiaTones: ['#C71585', '#000000', '#FFFFFF'],
                Grays: ['#404040', '#808080', '#C0C0C0']
            };
            let currentPalette = colorPalettes[colorScheme];
            const sqrt7 = Math.sqrt(7);
            const subdivideAngle = Math.atan(Math.sqrt(3)/5) * 180 / Math.PI; // ~19.1066 degrees

            p.setup = function() {
                let canvas = p.createCanvas(800, 600);
                canvas.parent('canvas-container');
                p.angleMode(p.DEGREES);
                p.noLoop();

                // Set up event listeners
                p.select('#input-steps').changed(function() {
                    steps = parseInt(this.value());
                    p.redraw();
                });

                p.select('#checkbox-zoom').changed(function() {
                    zoom = this.checked();
                    p.redraw();
                });

                p.select('#select-color').changed(function() {
                    colorScheme = this.value();
                    currentPalette = colorPalettes[colorScheme];
                    p.redraw();
                });
            };

            p.draw = function() {
                p.background(255);
                p.translate(p.width/2, p.height/2);

                // Set scale based on zoom
                let baseScale = 1;
                if (zoom) {
                    baseScale = Math.pow(sqrt7, steps);
                } else {
                    // Adjust scale to fit the entire fractal
                    baseScale = 200 / Math.pow(sqrt7, steps);
                }
                p.scale(baseScale);

                // Draw the central hexagon or tiling
                if (steps === 0) {
                    drawCentralHexagon();
                } else {
                    // For steps > 0, draw the recursive tiling
                    let size = 1.0;
                    for (let angle = 0; angle < 360; angle += 60) {
                        drawTiling(steps, 0, 0, size, angle);
                    }

                    // Draw the central hexagon for certain color schemes
                    if (colorScheme === "RustTones" || colorScheme === "FuchsiaTones") {
                        p.fill(currentPalette[2]);
                        p.noStroke();
                        drawHexagon(0, 0, size * 0.5);
                    }
                }
            };

            function drawCentralHexagon() {
                let size = 1.0;
                for (let angle = 0; angle < 360; angle += 60) {
                    p.fill(currentPalette[Math.floor(angle/60) % currentPalette.length]);
                    p.noStroke();
                    drawRhombus(0, 0, size, angle);
                }
            }

            function drawTiling(level, x, y, size, angle) {
                if (level === 0) {
                    p.fill(currentPalette[Math.floor(angle/60) % currentPalette.length]);
                    p.noStroke();
                    drawRhombus(x, y, size, angle);

                    // Add faint diagonal line
                    p.stroke(255, 200);
                    p.strokeWeight(0.02);
                    let halfSize = size * 0.5;
                    let cos60 = p.cos(60);
                    let sin60 = p.sin(60);
                    let x1 = x + halfSize * p.cos(angle);
                    let y1 = y + halfSize * p.sin(angle);
                    let x2 = x + halfSize * p.cos(angle + 120);
                    let y2 = y + halfSize * p.sin(angle + 120);
                    p.line(x1, y1, x2, y2);
                    return;
                }

                let newSize = size / sqrt7;
                let newAngle = angle + subdivideAngle;

                // Center rhombus
                drawTiling(level - 1, x, y, newSize, newAngle);

                // Six surrounding rhombi
                for (let i = 0; i < 6; i++) {
                    let dist = size * (2/3) * sqrt7;
                    let surroundAngle = angle + i * 60;
                    let nx = x + dist * p.cos(surroundAngle);
                    let ny = y + dist * p.sin(surroundAngle);
                    drawTiling(level - 1, nx, ny, newSize, newAngle - i * 60);
                }
            }

            function drawRhombus(x, y, size, angle) {
                let halfSize = size * 0.5;
                let cos60 = p.cos(60);
                let sin60 = p.sin(60);

                p.push();
                p.translate(x, y);
                p.rotate(angle);

                p.beginShape();
                p.vertex(halfSize, 0);
                p.vertex(halfSize * cos60, halfSize * sin60);
                p.vertex(-halfSize, 0);
                p.vertex(-halfSize * cos60, -halfSize * sin60);
                p.endShape(p.CLOSE);

                p.pop();
            }

            function drawHexagon(x, y, size) {
                p.beginShape();
                for (let angle = 0; angle < 360; angle += 60) {
                    let px = x + size * p.cos(angle);
                    let py = y + size * p.sin(angle);
                    p.vertex(px, py);
                }
                p.endShape(p.CLOSE);
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
