        const orientationVectors = [
            {x: 1, y: 0, z: 0},   // 0: →
            {x: -1, y: 0, z: 0},  // 1: ←
            {x: 0, y: 1, z: 0},   // 2: ↑
            {x: 0, y: -1, z: 0},  // 3: ↓
            {x: 0, y: 0, z: 1},   // 4: ⊙ (out of screen)
            {x: 0, y: 0, z: -1}   // 5: ⊗ (into screen)
        ];
        let magnetStrength = 5;
