                });

                document.getElementById(`magnet-orientation-down-${i}`).addEventListener('click', () => {
                    magnetOrientations[i] = (magnetOrientations[i] - 1 + 6) % 6;
                    updateOrientationDisplays();
                    drawVisualization();
                });
            }

            // Magnet strength slider
            document.getElementById('slider-strength').addEventListener('input', (e) => {
                magnetStrength = parseInt(e.target.value);
                document.getElementById('slider-strength-value').textContent = magnetStrength;
                drawVisualization();
            });

            // Plot type buttons
            document.getElementById('btn-stream-plot').addEventListener('click', () => {
