            for (let i = 0; i < 5; i++) {
                document.getElementById(`magnet-orientation-display-${i}`).textContent = 
                    orientationSymbols[magnetOrientations[i]];
            }
        }

        function drawVisualization() {
            background(255);
            translate(width / 2, height / 2);

            // Draw the field visualization
            if (plotType === 'stream') {
                drawStreamPlot();
            } else {
                drawDensityPlot();
            }

            // Draw the magnets
            drawMagnets();
