        function drawStreamPlot() {
            const gridSize = 20;
            const steps = 100;
            const stepSize = 2;

            stroke(0, 100, 255);
            strokeWeight(1);
            noFill();

            // Draw field lines from grid points
            for (let x = -width/2 + gridSize/2; x < width/2; x += gridSize) {
                for (let y = -height/2 + gridSize/2; y < height/2; y += gridSize) {
                    drawStreamLine(x, y, steps, stepSize);
                }
            }
        }

        function drawStreamLine(startX, startY, steps, stepSize) {
            // Trace forward
            let x = startX;
            let y = startY;
            let path = [];

            for (let i = 0; i < steps; i++) {
                if (abs(x) > width/2 || abs(y) > height/2) break;

                path.push({x, y});

                // Calculate field direction at this point
                const field = calculateField(x, y);
                const mag = sqrt(field.x * field.x + field.y * field.y);
                if (mag < 0.001) break;

                // Normalize and step
                const dx = (field.x / mag) * stepSize;
                const dy = (field.y / mag) * stepSize;

                x += dx;
                y += dy;
            }

            // Draw the path
            beginShape();
            for (const point of path) {
                vertex(point.x, point.y);
            }
            endShape();

            // Draw arrowheads at intervals
            for (let i = 0; i < path.length; i += 10) {
                if (i + 1 >= path.length) break;

                const p1 = path[i];
                const p2 = path[i + 1];
                drawArrowhead(p1.x, p1.y, p2.x, p2.y);
            }
        }

        function drawArrowhead(x1, y1, x2, y2) {
            const headSize = 5;
            const angle = atan2(y2 - y1, x2 - x1);

            push();
            translate(x2, y2);
            rotate(angle);

            line(0, 0, -headSize, -headSize/2);
            line(0, 0, -headSize, headSize/2);

            pop();
        }

        function drawDensityPlot() {
            const gridSize = 10;

            // First pass to find min/max field magnitude
            let minMag = Infinity;
            let maxMag = -Infinity;

            for (let x = -width/2; x < width/2; x += gridSize) {
                for (let y = -height/2; y < height/2; y += gridSize) {
                    const field = calculateField(x, y);
                    const mag = sqrt(field.x * field.x + field.y * field.y);
                    if (mag < minMag) minMag = mag;
                    if (mag > maxMag) maxMag = mag;
                }
            }

            // Avoid division by zero
            if (maxMag - minMag < 0.001) {
                maxMag = minMag + 0.001;
            }

            // Color map from light yellow to dark blue
            const color1 = color(255, 255, 200); // light yellow
            const color2 = color(255, 150, 0);  // orange
            const color3 = color(200, 0, 0);     // red
            const color4 = color(0, 0, 100);     // dark blue

            // Second pass to draw the density plot
            noStroke();
            for (let x = -width/2; x < width/2; x += gridSize) {
                for (let y = -height/2; y < height/2; y += gridSize) {
                    const field = calculateField(x, y);
                    const mag = sqrt(field.x * field.x + field.y * field.y);

                    // Normalize magnitude to [0, 1]
                    let t = (mag - minMag) / (maxMag - minMag);
                    t = constrain(t, 0, 1);

                    // Interpolate colors
                    let c;
                    if (t < 0.5) {
                        c = lerpColor(color1, color2, t * 2);
                    } else {
                        c = lerpColor(color2, color3, (t - 0.5) * 2);
                    }

                    // Add some blue at higher values
                    if (t > 0.7) {
                        const blueAmount = map(t, 0.7, 1, 0, 1);
                        c = lerpColor(c, color4, blueAmount);
                    }

                    fill(c);
                    rect(x, y, gridSize, gridSize);
                }
            }
        }

        function drawMagnets() {
            const magnetWidth = magnetSize;
            const magnetHeight = magnetSize * 0.6;
            const magnetDepth = magnetSize * 0.4;

            for (let i = 0; i < 5; i++) {
                const x = (i - 2) * magnetSpacing;
                const y = 0;

                // Draw 3D cube (simplified)
                push();
                translate(x, y);

                // Front face
                fill(220);
                stroke(0);
                strokeWeight(1);
                rect(-magnetWidth/2, -magnetHeight/2, magnetWidth, magnetHeight);

                // Top face
                beginShape();
                vertex(-magnetWidth/2, -magnetHeight/2);
                vertex(-magnetWidth/2 + magnetDepth, -magnetHeight/2 - magnetDepth);
                vertex(magnetWidth/2 + magnetDepth, -magnetHeight/2 - magnetDepth);
                vertex(magnetWidth/2, -magnetHeight/2);
                endShape(CLOSE);

                // Side face
                beginShape();
                vertex(magnetWidth/2, -magnetHeight/2);
                vertex(magnetWidth/2 + magnetDepth, -magnetHeight/2 - magnetDepth);
                vertex(magnetWidth/2 + magnetDepth, magnetHeight/2 - magnetDepth);
                vertex(magnetWidth/2, magnetHeight/2);
                endShape(CLOSE);

                // Draw orientation symbol
                const orientation = magnetOrientations[i];
                fill(0, 0, 255);
                noStroke();
                textSize(magnetSize * 0.5);
                textAlign(CENTER, CENTER);
                text(orientationSymbols[orientation], 0, 0);

                pop();
            }
        }

        function calculateField(x, y) {
            // Convert to "world" coordinates (1 unit = 1 magnet size)
            x = x / magnetSize;
            y = y / magnetSize;

            let Bx = 0;
            let By = 0;

            // Calculate field from each dipole
            for (let i = 0; i < 5; i++) {
                const dipoleX = (i - 2) * (magnetSpacing / magnetSize);
                const dipoleY = 0;

                // Position relative to this dipole
                const rx = x - dipoleX;
                const ry = y - dipoleY;
                const rz = 0;
                const r = sqrt(rx*rx + ry*ry + rz*rz);

                if (r < 0.1) continue; // Avoid singularity

                // Get dipole moment vector
                const m = orientationVectors[magnetOrientations[i]];
                const mx = m.x * magnetStrength;
                const my = m.y * magnetStrength;
                const mz = m.z * magnetStrength;

                // Dot product of m and r
                const mDotR = mx*rx + my*ry + mz*rz;

                // Magnetic dipole field equation
                const factor = 3 * mDotR / (r*r*r*r*r);
                const bx = factor * rx - mx / (r*r*r);
                const by = factor * ry - my / (r*r*r);

                Bx += bx;
                By += by;
            }

            return {x: Bx, y: By};
        }
    </script>
